/* fix audio icons */
.top_audio_player .top_audio_player_btn .top_audio_player_btn_icon {
    margin: 5px 4px!important;
   	width: 12px!important;
    height: 16px!important;
}
.top_audio_player .top_audio_player_prev>div {
    background-position: -32px -78px!important;
}

.top_audio_player .top_audio_player_play>div {
	background-position: 0 -78px!important;
}

.top_audio_player.top_audio_player_playing .top_audio_player_play>div {
	background-position: -17px -78px!important;
}

.top_audio_player .top_audio_player_next>div {
	background-position: -48px -78px!important;
}

/* New gradientful group info icons */

.group_info_row:before {
    display: block;
    position: absolute;
    content: '';
    background: url(https://i.imgur.com/St18vKV.png) no-repeat 0 0;
    opacity: 0.85;
}

.group_info_row.site:hover:before {
    opacity: 1
}
/* Online indicator */
.online:after {
    bottom: -1px;
    right: -1px;
}
.online.mobile:after {
    bottom: -1px;
    right: -2px;
    border-radius: 3px 3px 0 0;
}

/* No icon on Interesting first */
.ui_toggler_wrap.hot {
    background: none;
    border-left: 0;
}

.ui_toggler_wrap.hot .ui_toggler.on:before {
	content: "Внимание! Включена ужасная умная лента. Нажмите сюда, чтобы отключить";
    position: fixed;
    background: #d64a4a;
    top: 60px;
       width: 290px;
    color: white;
    box-sizing: border-box;
    padding: 10px;
    border-radius: 3px;
    left: 20px;
    z-index: 10000;
}
}
