    width: 114px;
    padding-top: 20px;
    opacity: 0.5;
    filter: alpha(opacity=50);
    -o-transition: background-color 200ms linear, opacity 200ms linear;
    transition: background-color 200ms linear, opacity 200ms linear;
}

