css:
@-moz-document domain("vk.com") {
    body {
       background: #fafafa;
/*        font-family: tahoma, sans-serif; */
       font-size: 11px;
    }
    
    #side_bar_inner .left_icon {
        display: none;
    }
    
    #side_bar_inner .left_label {
        padding: 0 10px;
    }
    
    #side_bar_inner .more_div {
        margin-left: 0;
    }
    
    #ads_left {
        display: none!important;
    }
    
    img,
    .text.ts_input,
    .people_cell_img, 
    .post_img,
    .cell_img,
    .thumb,
    .post_field_user_image, 
    .feedback_img,
    .no_current_info,
    .top_profile_img,
    .audio_play,
    .fans_idolph_wrap,
    .copy_post_img,
    .reply_img,
    .nim-peer .nim-peer--photo-w,
    .audio_page_player .audio_page_player_play .icon {
        border-radius: 2px!important;
    }
    
    .module_header {
        background: #d8e2ee;
        border-bottom: 1px solid #c0cedf;
        border: none;
    }
    
    .wall_module .copy_quote {
        border-left: 1px solid #E7E8EC;
        padding-left: 20px;
        margin: 5px 0 0;
    }
    
    .im-mess-stack--content {
        border-bottom: 1px solid #eef1f5;
    }
    
    .audio_row {
        border-bottom: 1px solid #eef1f5;
    }
    
    .module_header .header_top, .header_right_link {
        line-height: 33px;
    }
    
    .page_name, h2.page_name {
        margin-bottom: 10px;
    }
    
    .wide_column .module_header .header_top {
        padding: 0;
        padding-left: 15px;
    }
    
    .im-mess-stack--tools {
        position: relative;
        top: -1px;
    }
    
    .page_block {
        box-shadow: 0 1px 0 0 #c0cedf, 0 0 0 1px #c0cedf;
    }
    
    .im-mess-stack .im-mess-stack--content .im-mess-stack--pname > a {
        font-weight: normal;
    }
    
    .wall_module .post_header {
        background: #e4eaf0;
        padding-bottom: 10px;
    }
    
    .wr_header {
        background: #f4f1c6;
    }
    
    .post_image, .post_img {
        background: white;
        border-radius: 50%!important;
    }
    
    .narrow_column {
        transition: opacity .35s ease;
    }
    
    .narrow_column.fixed {
        opacity: 0.07;
    }
    
    .narrow_column.fixed:hover {
        opacity: 1;
    }
    
    .im-page.im-page_classic .im-page--header {
        border-color: #fafafa;
    }
    
    .im-page.im-page_classic .im-page--dialogs-search {
        box-shadow: 0 0 0 1px #c0cedf;
    }
}
