css:
@-moz-document domain('icanhazchat.com') {

body {
    background: #212121;
    color: #e9e9e9;
}
.page_header {
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    margin: 0 auto;
    width: 1024px;
}
#panelHeader {
    margin-bottom: 40px;
}
.header {
    width: 1024px;
    height: 50px;
    background-color: #B0BEC5;
    color: #333;
    font-size: 16px;
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    border-radius: 0;
}
.gray_back {
    background-color: #444;
}
.rounded {
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
}
.chat_footer {
    background-color: #fff;
    width: 1024px;
    padding-top: 0;
    filter: alpha(opacity=100);
    opacity: 1;
    border: 0;
    top: 620px;
}
.chat_footer .gray_back {
    margin: 0;
    background: rgba(0, 0, 0, .1);
}
.footer_links {
    margin-top: 20px;
    font-size: 14px;
    text-shadow: none;
}
.room_translucent_back {
    border-color: #eee;
    border: 0;
    width: 1024px;
}
.body_container {
    border-color: #fff;
    background: #fff;
    width: 1024px;
    top: 50px;
    height: 620px;
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    border-radius: 0;
}
.rounded_square {
    border-color: rgba(0, 0, 0, .1);
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    background: rgba(0, 0, 0, .1);
}
.message_display {
    background-color: rgba(0, 0, 0, .05);
    border-color: transparent;
    padding-left: 5px;
    padding-top: 5px;
    padding-right: 0;
    padding-bottom: 5px;
    -moz-border-radius: 5px 5px 5px 5px;
    -webkit-border-radius: 5px 5px 5px 5px;
    color: #000000;
}
.chat_container {
    z-index: 1;
}
.room_command_bar {
    border: 0;
}
.body_container {
    border: 0;
}
.main {
    top: 0;
}
.camControl {
    width: 390px!important;
}
.camControl:before {
    content: ' ';
    display: inline-block;
    vertical-align: middle;
    height: 100%;
}
#camControl u {
    text-decoration: none;
    text-indent: 5px;
}
.btn-primary {
    background-color: #43A047;
    border: 0;
    border-bottom: 1px solid #2E7D32;
    margin-left: -60px;
}
.btn-primary:hover {
    background: #388E3C;
    border: 0;
}
a[href="javascript:startCam('normal','broadcaster014.swf');"] {
    vertical-align: middle;
}
a[href="javascript:startCam('normal','broadcaster014.swf');"]:hover {
    text-decoration: none;
}
#camControl img {
    content: url(http://i.imgur.com/C6yFLWq.png);
}
a[href="javascript:stopCam(true);"] {
    background: #F44336;
    padding: 3px 10px;
    border-radius: 3px;
}
a[href="javascript:stopCam(true);"]:hover {
    background: #E53935;
}
a[href="javascript:micUnmute();"],
a[href="javascript:showBroadcastingCam();"],
#divPushToTalk {
    background: rgba(0, 0, 0, .2);
    padding: 3px 10px;
    border-radius: 3px;
    color: #fff;
}
a[href="javascript:micUnmute();"]:hover,
a[href="javascript:showBroadcastingCam();"]:hover,
#divPushToTalk:hover {
    text-decoration: none;
    background: rgba(0, 0, 0, .3);
    color: #fff!important;
    cursor: pointer;
}
#topicContainer {
    top: -40px;
    height: 40px;
    background: #039BE5;
    width: 100%;
    left: 0;
    box-shadow: 0 5px 5px -5px rgba(0, 0, 0, 0.5);
}
#topic {
    font-size: 14px;
    padding-left: 10px;
    color: #fff;
}
#topic a,
#topic a:visited {
    color: #fff;
}
.camControl {
    background: rgba(0,0,0,.07);
    top: 0;
    bottom: 0;
    left: 0;
    width: 310px;
    font-size: 14px;
}
.camControl:after {
content: "";
position: absolute;
display: block;
    top: 0;
right: -50px;
width: 0;
height: 0;
border-style: solid;
border-width: 50px 50px 0 0;
border-color: rgba(0,0,0,.07) transparent transparent transparent;
}

.camPreview {
    top: 40px;
    left: 3%;
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    border-radius: 0;
}
.room_user_list {
    color: #212121;
    margin-top: 20px;
    margin-bottom: 10px;
    border: 1px solid rgba(0, 0, 0, .1);
    border-radius: 0;
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    background: rgba(0, 0, 0, .02);
    padding: 10px 20px;
}
.footer_table {
    background-color: #444;
    color: #fff;
}
.footer_table a {
    color: #fff;
}
.room_translucent_back {
    height: 100%;
}
#activeUserList p {
    background: #E3F2FD;
    margin-top: 10px;
    padding: 8px;
    border: 1px solid rgba(0, 0, 0, .1);
}
.message_display {
    height: 427px;
}
.odd_row1 {
    background: #fff;
    border: 0;
}
.room_command_bar {
    padding: 0;
    text-align: right;
    margin-top: 5px;
}
.room_command_bar span {
    padding: 0;
}
a[href="javascript:scrollOff();"] img,
a[href="javascript:clearChatHistory();"] img,
a[href="javascript:toggleChatFont();"] img,
a[href="javascript:setLineStyles(true);"] img,
a[href="javascript:toggleNotifications();"] img,
a[href="javascript:toggleChatSound();"] img,
a[href="javascript:pickColor();"] img,
a[href="javascript:toggleEmoticons();"] img,
a[href="javascript:toggleImages();"] img,
a[href="javascript:togglePMPrefs();"] img,
a[href="help"] img,
a[href="javascript:scrollOn();"] img {
    height: 26px;
    width: 26px;
    background: #F0F0F0;
    border: 1px solid rgba(0, 0, 0, .1);
}
a[href="javascript:scrollOff();"] img {
    content: url(http://i.imgur.com/XdLIrh2.png);
}
a[href="javascript:clearChatHistory();"] img {
    content: url(http://i.imgur.com/qtRwBem.png);
}
a[href="javascript:toggleChatFont();"] img {
    content: url(http://i.imgur.com/4dNIkiv.png);
}
a[href="javascript:setLineStyles(true);"] img {
    content: url(http://i.imgur.com/S8bQLbG.png);
}
a[href="javascript:toggleNotifications();"] img {
    content: url(http://i.imgur.com/dHa5qMZ.png);
}
a[href="javascript:toggleChatSound();"] img {
    content: url(http://i.imgur.com/7yoQGTi.png);
}
a[href="javascript:pickColor();"] img {
    content: url(http://i.imgur.com/9UH69Tp.png);
}
a[href="javascript:toggleEmoticons();"] img {
    content: url(http://i.imgur.com/kezyg21.png);
}
a[href="javascript:toggleImages();"] img {
    content: url(http://i.imgur.com/Vk869Tz.png);
}
a[href="javascript:togglePMPrefs();"] img {
    content: url(http://i.imgur.com/o2JfTyy.png);
}
a[href="help"] img {
    content: url(http://i.imgur.com/tcbuebY.png);
}
a[href="javascript:scrollOn();"] img {
    content: url(http://i.imgur.com/02EwsQR.png);
    background: #42A5F5;
}
a[href="javascript:scrollOff();"] img:hover,
a[href="javascript:clearChatHistory();"] img:hover,
a[href="javascript:toggleChatFont();"] img:hover,
a[href="javascript:setLineStyles(true);"] img:hover,
a[href="javascript:toggleNotifications();"] img:hover,
a[href="javascript:toggleChatSound();"] img:hover,
a[href="javascript:pickColor();"] img:hover,
a[href="javascript:toggleEmoticons();"] img:hover,
a[href="javascript:toggleImages();"] img:hover,
a[href="javascript:togglePMPrefs();"] img:hover,
a[href="help"] img:hover,
a[href="javascript:scrollOn();"] img:hover {
    background: #dcdcdc;
}
#chatFontSize,
#scrollControl,
.chat_button,
#Div4,
#showNotifications,
#toggleChatSound,
#pickColor,
#toggleEmoticons,
#toggleImageViewing,
#togglePMs,
#showHelp {
    position: relative;
}
#chatFontSize:hover:after,
#scrollControl:hover:after,
.chat_button:hover:after,
#Div4:hover:after,
#showNotifications:hover:after,
#toggleChatSound:hover:after,
#pickColor:hover:after,
#toggleEmoticons:hover:after,
#toggleImageViewing:hover:after,
#togglePMs:hover:after,
#showHelp:hover:after {
    position: absolute;
    white-space: normal;
    display: block;
    z-index: 1000;
    width: 205px;
    font-size: 12px;
    line-height: 27px;
    background: #E57373;
    content: "Toggle text size";
    text-align: center;
    font-weight: bold;
    color: #fff;
    margin-top: -27px;
    right: 94px;
}
#scrollControl:hover:after {
    content: "Pause Text Window Scrolling";
    right: 33px;
}
.chat_button:hover:after {
    content: "Clear Text Window (ctlr + c)";
    right: 64px;
}
#Div4:hover:after {
    content: "Alternating Line Style";
    right: 125px;
}
#showNotifications:hover:after {
    content: "Show Notifications";
    right: 155px;
}
#toggleChatSound:hover:after {
    content: "Play Notification Sounds";
    right: 186px;
}
#pickColor:hover:after {
    content: "Chose Your Text Colour";
    right: 216px;
}
#toggleEmoticons:hover:after {
    content: "Disable Graphical Emoticons";
    right: 246px;
}
#toggleImageViewing:hover:after {
    content: "Image Viewing Options";
    right: 277px;
}
#togglePMs:hover:after {
    content: "Toggle PM/Whisper Preferences";
    right: 307px;
}
#showHelp:hover:after {
    content: "Command Help (ctrl + h)";
    right: 338px;
}
#togglePMViewing {
    top: -50px;
    right: 105px;
    height: 50px;
    border: 0;
    padding: 0!important;
    background: transparent;
    text-align: center;
}
#togglePMViewing a[href="javascript:hide_pms();"] {
    line-height: 50px;
}
a[href="javascript:hide_pms();"] {
    background-color: #607D8B!important;
    padding: 4px 10px!important;
    border-radius: 3px;
    color: #fff!important;
    margin: 0!important;
}
#togglePMViewing.chat_button:hover:after {
    display: none;
}
.room_btn_hidecams {
    left: 0;
    margin-top: 3px;
}
.room_btn_mutecams {
    left: 30px;
    margin-top: 3px;
}
.room_btn_refreshcams {
    left: 60px;
    margin-top: 3px;
}
.room_btn_hidecams img,
.room_btn_mutecams img,
.room_btn_refreshcams img {
    height: 27px;
    width: 27px;
    padding: 5px;
    background: rgba(0, 0, 0, .1);
    border: 1px solid rgba(0, 0, 0, .1);
    content: url(http://i.imgur.com/4XMnXYn.png);
}
.room_btn_mutecams img {
    content: url(http://i.imgur.com/NNPhkNv.png);
}
.room_btn_mutecams a[href="javascript:unMuteCams();"] img {
    background: #ff7e82;
    content: url(http://i.imgur.com/MR6T02C.png);
}
.room_btn_refreshcams img {
    content: url(http://i.imgur.com/UI6oZFO.png);
}
.room_btn_hidecams img:hover,
.room_btn_mutecams img:hover,
.room_btn_refreshcams img:hover {
    background: #42A5F5;
}
.room_btn_hidecams img:hover {
    content: url(http://i.imgur.com/8jL1wfA.png);
}
.room_btn_mutecams img:hover {
    content: url(http://i.imgur.com/ui1OY7M.png);
}
.room_btn_refreshcams img:hover {
    content: url(http://i.imgur.com/3GmylSx.png);
}
.room_btn_hidecams a[href="javascript:toggleCams();"],
.room_btn_mutecams a[href="javascript:unMuteCams();"] {
    position: relative;
}
.room_btn_hidecams a[href="javascript:toggleCams();"]:hover:after,
.room_btn_mutecams a[href="javascript:unMuteCams();"]:hover:after,
.room_btn_mutecams a[href="javascript:muteCams();"]:hover:after,
.room_btn_refreshcams a[href="javascript:refreshCams();"]:hover:after {
    position: absolute;
    white-space: normal;
    display: block;
    z-index: 1000;
    width: 200px;
    font-size: 14px;
    line-height: 27px;
    background: #E57373;
    content: "Turn cams on/off ";
    text-align: center;
    font-weight: bold;
    color: #fff;
    margin-top: -27px;
    left: 90px;
}
.room_btn_mutecams a[href="javascript:unMuteCams();"]:hover:after {
    content: "Turn sound back on";
    left: 60px;
}
.room_btn_mutecams a[href="javascript:muteCams();"]:hover:after {
    content: "Turn sound off";
    left: 60px;
}
.room_btn_refreshcams a[href="javascript:refreshCams();"]:hover:after {
    content: "Refresh the cams";
    left: 30px;
}
.signout {
    top: 10px;
    right: 10px;
    left: auto;
    background: #607D8B;
    padding: 3px 20px;
    border-radius: 4px;
    border-bottom: 1px solid #455A64;
}
.signout:hover {
    background: #546E7A;
    border: 0;
}
.signout a:hover,
a[href="javascript:stopCam(true);"]:hover {
    text-decoration: none;
    color: #fff!important;
}
.chat_error_message {
    background-color: #FF5A5F;
    text-indent: 20px;
}
.page_header_logo {
    padding: 0;
}
.page_header_logo img {
    content: url(http://i.imgur.com/Dz1XOuA.png);
    width: 100px;
    height: 55px;
}
.col-sm-5 {
    padding-right: 0;
}
.page_header_userlinks {
    position: relative;
    padding-top: 0;
    padding-right: 15px;
    text-shadow: none;
    height: 55px;
    font-size: .7rem;
    background: rgba(255, 255, 255, .05);
}
.page_header_userlinks:before {
    content: "";
    position: absolute;
    display: block;
    left: -55px;
    top: 0;
    width: 0;
    height: 0;
    border-style: solid;
    border-width: 0 0 55px 55px;
    border-color: transparent transparent rgba(255, 255, 255, .05) transparent;
}
.page_header_userlinks a {
    color: #f5f5f5;
}
a[href="/groups"],
a[href="/settings"],
a[href="/dashboard"] {
    font-size: 14px;
}
.header_links {
    color: #e9e9e9;
}
.unread {
    background-color: rgba(255, 255, 255, .3);
    padding: 2px 10px;
    border-radius: 3px!important;
    font-size: 14pt;
    font-size: 14px;
}
.message_body,
.message_content_block,
#ctl00_ContentPlaceHolder1_divForMods,
#ctl00_ContentPlaceHolder1_divForOwners,
#ctl00_ContentPlaceHolder1_divMenuTabs,
#ctl00_ContentPlaceHolder1_divRules,
#ctl00_ContentPlaceHolder1_divWelcome,
#ctl00_ContentPlaceHolder1_divAge,
#ctl00_ContentPlaceHolder1_divSettings,
#ctl00_ContentPlaceHolder1_divUsers,
#ctl00_ContentPlaceHolder1_divOwners {
    color: #212121;
}

.camControl:before { display: none; }
#camControl > .btn-primary { margin-top: 8px; }

}
