css:
@-moz-document domain("tweetdeck.twitter.com") {




/* Scrollbar */

::-webkit-scrollbar-thumb {
       background-color: #3C5775;
       border: 2px solid #1A2835;
}

::-webkit-scrollbar-track {
       background-color: #1A2835;
}

.yt-scrollbar ::-webkit-scrollbar-thumb:hover {
       background-color: #3C5775;
       border: 2px solid #1A2835;
}

.yt-scrollbar ::-webkit-scrollbar-thumb {
       background-color: #233446;
       border: 2px solid #233446;
}




.exp-scrollable-guide #appbar-guide-menu {
       /* Scrollbar always active */
       
       overflow-y: visible;
}


/* Scrollbar & Placeholder */

::-webkit-scrollbar {
       width: 12px;
       height: 12px;
}

::-webkit-scrollbar-thumb {
       border-radius: 6px;
       min-height: 50px;
}

.yt-scrollbar ::-webkit-scrollbar {
       width: 12px;
       height: 12px;
}

.yt-scrollbar ::-webkit-scrollbar-thumb:hover {
       border-radius: 6px;
       min-height: 50px;
}

.yt-scrollbar ::-webkit-scrollbar-thumb {
       border-radius: 6px;
       min-height: 50px;
}

::-webkit-input-placeholder {
       text-transform: capitalize;
       color: rgba(255, 255, 255, 0.75) !important;
}


body, #container {
    font-family: 'Segoe UI', 'Arial', 'Helvetica', sans-serif;
    background: #fff !important;
    text-shadow: none;
}
.app-columns {
    background: #1A2835;
    padding:0;
}

*{  border-radius: 0; }

h1, h1 *, .attribution {
  font-size:20px !important;
  color:#DADADA;
}
.column-type-icon
{
  color:#A4A4A5;
}

.stream-item{
    text-decoration:none;
  	border-bottom: 1px solid #303E4B !important;
 	 color:white!important;
    font-family:Arial;
    background:#1A2835;
    font-size:/*[[fontsize]]*/; /* KEY */
}


.account-bio{
    font-family: "Arial"!important;
	color:#C3C3C3;
    text-decoration:none!important;
}

.account-link{
			color:#DADADA;
}
 
.column {   
    width: 319px !important;
    box-shadow: none !important;
  margin:0;
}
.column-drag-handle {background:transparent;}

.column-options{
    background: RED !important;
}
.column-settings-link,
.is-options-open .column-settings-link  {
    background: red;
}
.column-settings-link:hover{
    background: transparent;
}
.column-header:hover .column-settings-link, .column-header-link{
    background:transparent;
}
.is-options-open .column-settings-link .icon{
    background: none !important;
    color:#999 !important;
}
.stream-item .icon-reply,
.stream-item .icon-retweet,
.stream-item .icon-favorite,
.stream-item .icon-more
{
    color:#AAB8C2;
}
.stream-item .icon-small-context
{
    color:#AAB8C2;
}



.btn-with-icon-small {
    padding: 4px 10px !important;
}
.menu-button{
    padding-top:3px !important;    
}


.app-search-input {
    box-shadow: none !important;
}

.column-title,.column-title .attribution {
    color:#8899a6 !important;
}
.column-header-link i{
    color:#AAB8C2;
}
.column-header-link i:hover{
    color:white;
}
.column-number {
    color: #444 !important;
}


.tweet-avatar{
  	background: transparent;
	-moz-border-radius: 24px;
	border-radius: 24px;
	-webkit-border-radius: 24px;
}
.avatar{
  	background: transparent;
	-moz-border-radius: 37px;
	border-radius: 37px;
	-webkit-border-radius: 37px;
}
.prf-img {
    background:transparent;
}


 
 .is-selected-tweet 
 {
    background: #fff !important;
 }


section:nth-child(1) .column-header,
section:nth-child(2) .column-header,
section:nth-child(3) .column-header,
section:nth-child(4) .column-header, 
section:nth-child(5) .column-header
{
    background: #233446 !important
}

.column-nav-item:nth-child(1) .column-nav-link .icon,
.column-nav-item:nth-child(2) .column-nav-link .icon
{
    color: #A4A4A5;
}

section:nth-child(1) .column-scroller,
section:nth-child(2) .column-scroller,
section:nth-child(3) .column-scroller,
section:nth-child(4) .column-scroller, 
section:nth-child(5) .column-scroller,
section:nth-child(6) .column-scroller,
section:nth-child(7) .column-scroller 
{
    background: #233446;
}




.column-nav-item:nth-child(3) .column-nav-link .icon,
.column-nav-item:nth-child(4) .column-nav-link .icon,
.column-nav-item:nth-child(5) .column-nav-link .icon 
{
    color: #A4A4A5;
}


section:nth-child(6) .column-header,
section:nth-child(7) .column-header 
{
    background: #f39 !important;
}

.column-nav-item:nth-child(6) .column-nav-link .icon,
.column-nav-item:nth-child(7) .column-nav-link .icon 
{
    color: #f39;
}



}
