css:
@namespace html url(http://www.w3.org/1999/xhtml);

 
@-moz-document url-prefix(http://), url-prefix(https://), url-prefix(ftp://), url-prefix(file://)

{

  * {
    color: #000 !important;
  }

  a {
    text-decoration: underline !important;
  }

}

@-moz-document url-prefix("https://pod.geraspora.de/"),
               url-prefix("https://joindiaspora.com/"),
               url-prefix("https://pod.binchan.xyz/"),
               regexp("^https?://.*/(liked|commented|stream|activity|mensions|aspects|contacts|notifications|conversations|followed_tags)"),
               regexp("^https?://.*/(people|posts|tags|conversations)/.*"), regexp("^https?://.*/conversations\\?.*") 

{

.navbar-inverse {
    background-color: #fff;
}
  
  #main-nav.navbar-fixed-top #nav-badges .badge {
    background-color: #fff;
}

  .navbar-inverse .navbar-toggle .icon-bar {
    background-color: #000;
}  
  
#main-nav.navbar-fixed-top #header-title img {
    background-color: #000;
}

  .bottom-bar .post-stats [class^="entypo"] {
    color: #888 !important;
}

  .bottom-bar .post-stats .entypo-heart.active {
    color: #A80000 !important;
}

.bottom-bar .post-stats .entypo-reshare.active {
    color: #000000 !important;
}

}
