/* DBA clutter free By NightNiRa */

@-moz-document domain(dba.dk) {
    /* Make sure body has no background */
    body
    {
        background-image: none !important;
        background: none !important;
    }

    /* Fix breadcrumb position */
    .container
    {
        margin: auto !important;
    }

    /* Stretch footer */
    #page
    {
        width: 100% !important;
    }

    /* Remove bold text from listings */
    .search-result tr.listing td[title=Pris],
    .searchResults .boldListing .listingLink,
    .searchResults .boldListing td.simple
    {
        font-weight: 400 !important;
    }

    /* Elements to hide */
    #cto_cookies_ack_header_message.criteo_header,
    #cto_cookies_header_message.criteo_header,
    #FrontpageFeature,
    #gallup-iam-overlay,
    #gcsa-container-primary-1,
    #gcsa-container-primary-2,
    #google-dfp-article-1,
    #google-dfp-article-2,
    #google-dfp-article-3,
    #google-dfp-bottom,
    #google-dfp-skyscraper-left,
    #google-dfp-skyscraper-left-2,
    #google-dfp-skyscraper-right,
    #google-dfp-skyscraper-right-2,
    #google-dfp-top,
    #google-dfp-wallpaper,
    #RelatedListingsByExternalId,
    #searchAgentForm,
    #TopGalleryFeature,
    #uvTab,
    #Webshop,
    .banner.calculation-banners,
    .blog-content,
    .facebookCounter,
    .gallery-component.gallery-component-compressed.loaded.non-background-image,
    .headline-block,
    .listing.listing-cas.listing-cas-dco,
    .listing.listing-gAFSh,
    .marketing-container,
    .popular-searches-list,
    .search-result-separator,
    .shipping-delivery,
    .tabbable.tabs-left.popular-searches-tabs
    {
        display: none !important;
    }
}

/* DBA clutter free By NightNiRa */
