css:
@-moz-document url-prefix("http://www.wsgrpg.net/") {
	body { background: #090909; color: #FF8800 !important; }
  select { background: #FF8800; color: #111; border-color: #111; }
  a.link, div.header_bar_text a { color: #CEAA6F !important; }
  a.link:hover, div.header_bar_text a:hover { color: #666 !important; }

  /* Sign In */
  div.login_top_text { color: #444; }
  div.login_top { 
      width: 410px;
      border: 2px solid #876fce;
      border-top-left-radius: 8px; 
      border-top-right-radius: 8px; 
      background: #999999;
      background: -moz-linear-gradient(top,  #999999 0%, #727272 51%, #876fce 100%);
      background: -webkit-linear-gradient(top,  #999999 0%,#727272 51%,#876fce 100%);
      background: linear-gradient(to bottom,  #999999 0%,#727272 51%,#876fce 100%);
  }
  div.login_mid {
      width: 410px;
      background: #999999;
      border-left: 2px solid #876fce;
      border-right: 2px solid #876fce;
  }
  div.login_bot {
      background: #999;
      width: 410px;
      border: 2px solid #876fce;
      border-bottom-left-radius: 8px; 
      border-bottom-right-radius: 8px; 
  }
  div.login_cont { color: #FF8800; }
  div.login_cont hr { background: #FF8800; color: #FF8800; }
  div.login_cont input[type=text], div.login_cont input[type=password] { border: 1px solid #FF8800; background: #333; }
  div.login_cont input[type=submit] { background: #FF8800; color: #111; border: 1px solid #333; }
  div.login_cont a { color: #876fce; }
  div.login_cont a:hover { color: #9681D4; }
  div.main_content_mid { background: #222; border-left: 2px solid #FF0000; border-right: 2px solid #FF0000; padding-bottom: 5px; width: 830px; }
  div.main_content_top_text { color: #333; }
  div.main_content_mid_text { color: #7558CC; }

  /* Tooltips */
  .tooltips { background: #222; color: #ff8800; border-color: #ff8800; }

  /* Header */
  div.header { background-image: url('http://i.imgur.com/7opmi4L.png'); }
  div.header_bar_text { color: #333; }

  /* Notices/Announcements */
  div.imp_ex { background: #CEAA6F; border: 2px solid #FF0000; border-radius: 50%; }
  div.imp_msg { background: #333; border: 1px solid #FF0000; margin-left: 7px; color: #CEAA6F; }

  /* Tables */
  td.topleft, td.topmid, td.topright, td.midleft, td.midmid, td.midright, td.botleft, td.botmid, td.botright, td.botsingle
  { border-color: #ff0000; }

  /* Body */
  div.content_bottom { background: #161616; border-left: 2px solid #ff0000; border-right: 2px solid #ff0000; width: 846px; }
  div.content_bg { background: #161616; border-left: 2px solid #ff0000; border-right: 2px solid #ff0000; width: 846px; }
  div.time_bar { 
      border-top-left-radius: 8px; 
      border-top-right-radius: 8px; 
      background: #ff8800;
      background: -moz-linear-gradient(top, #ff8800 46%, #ff8800 46%, #ff0000 100%);
      background: -webkit-linear-gradient(top, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      background: linear-gradient(to bottom, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      border: 2px solid #FF0000;
      width: 846px;
  }
  div.header_bar_text a { color: #444 !important; }
  div.header_bar { 
      background: #ff8800;
      background: -moz-linear-gradient(top, #ff8800 46%, #ff8800 46%, #ff0000 100%);
      background: -webkit-linear-gradient(top, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      background: linear-gradient(to bottom, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      border: 2px solid #FF0000;
      border-top: none;
      width: 846px;
  }
  div.lol_content_top { 
      background: #ff8800;
      background: -moz-linear-gradient(top, #ff8800 46%, #ff8800 46%, #ff0000 100%);
      background: -webkit-linear-gradient(top, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      background: linear-gradient(to bottom, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      border: 2px solid #FF0000;
      width: 518px;
  }
  div.lol_content_top_text { color: #333; }
  td.topsingle { 
      background: #ff8800;
      background: -moz-linear-gradient(top, #ff8800 46%, #ff8800 46%, #ff0000 100%);
      background: -webkit-linear-gradient(top, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      background: linear-gradient(to bottom, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      border: 2px solid #FF0000;
      border-color: #FF0000; 
  }
  div.nav_top { 
      background: #ff8800;
      background: -moz-linear-gradient(top, #ff8800 46%, #ff8800 46%, #ff0000 100%);
      background: -webkit-linear-gradient(top, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      background: linear-gradient(to bottom, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      border: 2px solid #FF0000;
      border-top-left-radius: 8px;
      border-top-right-radius: 8px;
      width: 146px;
  }
  div.nav_bar_mid { 
      background: #ff8800;
      background: -moz-linear-gradient(top, #ff8800 46%, #ff8800 46%, #ff0000 100%);
      background: -webkit-linear-gradient(top, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      background: linear-gradient(to bottom, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      border: 2px solid #FF0000;
      border-top: none;
      width: 146px;
  }
  div.nav_bar_mid_text { color: #444; }
  div.nav_bar_mid_text a, td.topsingle, div.time_bar_text, div.nav_top_text, td.topleft, td.topright, td.topmid
  { color: #333 !important; }
  div.nav_mid_text { color: #444; }
  div.nav_mid_text a { color: #D4A881; }
  div.nav_mid_text a:hover { color: #FF8800; }
  div.nav_bot { 
      background: #ff8800;
      background: -moz-linear-gradient(top, #ff8800 46%, #ff8800 46%, #ff0000 100%);
      background: -webkit-linear-gradient(top, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      background: linear-gradient(to bottom, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      border: 2px solid #FF0000;
      border-bottom-left-radius: 8px;
      border-bottom-right-radius: 8px;
      border-top: none;
      width: 146px;
  }
  td.topleft, td.topmid, td.topright { 
      background: #ff8800;
      background: -moz-linear-gradient(top, #ff8800 46%, #ff8800 46%, #ff0000 100%);
      background: -webkit-linear-gradient(top, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      background: linear-gradient(to bottom, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      border: 2px solid #FF0000;
  }
  td.topmid { border-left-width: 1px; border-right-width: 1px; }
  div.lol_content_mid { background: #222; border-left: 1px solid #ff0000; border-right: 1px solid #ff0000; width: 520px; }
  div.lol_content_mid_text { color: #FF8800; }
  div.lol_content_mid_text a, div.nav_top_text a { color: #555 !important; }
  div.lol_content_mid_text a:hover, div.nav_top_text a { color: #666 !important; }
  div.lol_content_bot { background: #222; border: 1px solid #ff0000; border-top: none; border-bottom-left-radius: 8px; border-bottom-right-radius: 8px; width: 520px; }
  td.midsingle { border-color: #ff0000; }
  td.botleft, td.botright, td.midsingle, td.midleft, td.midmid, td.midright,td.botsingle, td.botmid
  { background: #222; }

  /* Sidebars */
  div.nav_mid_promo, div.nav_mid, div.nav_mid_bot
  { background: #222; border-bottom: 1px solid #ff0000; border-left: 1px solid #ff0000; border-right: 1px solid #ff0000; width: 148px; 
  }

  /* Roster */
  /* UPDATE THE ROSTER BAR EXP IMAGE TO BE #876fce */
  div.nav_mid_roster { background: #222; border-color: #ff0000; border-left: 1px solid #ff0000; border-right: 1px solid #ff0000; width: 148px; }
  /* ROSTER EXP BAR */
  div.nav_mid_roster_bar div { background-image: url('http://i.imgur.com/hsVTsBy.png') !important; }
  div.nav_mid_roster_bar { border-color: #ff0000; }
  div.nav_mid_roster_text, div.nav_mid_roster_text a { color: #CEAA6F; }
  div.nav_mid_roster_text a:hover { color: #FF8800; }
  div.roster_tip_top { 
      background: #222;
      border:  2px solid #ff0000; 
      border-bottom: none;
      border-top-left-radius: 8px;
  }
  div.roster_tip_mid { 
      background: #222;
      border:  2px solid #ff0000; 
      border-top: none;
      border-bottom: none;
  }
  div.roster_tip_mid_text { color: #D46D00; }
  div.roster_tip_bot { 
      background: #222;
      border:  2px solid #ff0000; 
      border-top: none;
      border-bottom-left-radius: 8px;
  }

  /* Footer */
  div.footer_bar {
      background: #ff8800;
      background: -moz-linear-gradient(top, #ff8800 46%, #ff8800 46%, #ff0000 100%);
      background: -webkit-linear-gradient(top, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      background: linear-gradient(to bottom, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      border: 2px solid #FF0000;
      border-bottom-left-radius: 8px;
      border-bottom-right-radius: 8px;
      width: 846px;
  }
  div.footer_bar_text { color: #111; }
  div.footer_bar_text a { color: #555; }
  div.footer_bar_text a:hover { color: #333; }
  div.content_bottom { background-image: none; }

  /* Toolbar */
  div.toolbar_arrow { 
      background: #333;
      border-top-right-radius: 8px;
      color: #444; 
  }
  div.toolbar_main { 
      background: #ff8800;
      background: -moz-linear-gradient(top, #ff8800 46%, #ff8800 46%, #ff0000 100%);
      background: -webkit-linear-gradient(top, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      background: linear-gradient(to bottom, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
  }
  div.toolbar_main_text { color: #222; }
  div.toolbar_main_text a { color: #222; }
  div.toolbar_main_text a:hover { color: #444; }
  div.toolbar_box_x { background-image: url('http://i.imgur.com/2iCOJ67.png'); }
  div.toolbar_box_mid { background: #ff0000; }
  div.toolbar_box_top {
      background: #ff8800;
      background: -moz-linear-gradient(top, #ff8800 46%, #ff8800 46%, #ff0000 100%);
      background: -webkit-linear-gradient(top, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      background: linear-gradient(to bottom, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      border: 2px solid #FF0000;
      border-top-left-radius: 8px;
  }
  div.toolbar_box_top_text { color: #444; }
  div.toolbar_frame_text { color: #FF8800; }
  div.toolbar_frame_text a { color: #666; }
  div.toolbar_frame_text a:hover { color: #996E1D; }

  /* Forums */
  .link1 { color: #111 !important; }
  .link1:hover { color: #555 !important; }
  div.main_content a, div.main_content_mid_sections_text a { color: #CEAA6F !important; }
  div.main_content a:hover, div.main_content_mid_sections_text a:hover { color: #996E1D !important; }
  div.main_content_top { 
      width: 830px;
      background: #ff8800;
      background: -moz-linear-gradient(top, #ff8800 46%, #ff8800 46%, #ff0000 100%);
      background: -webkit-linear-gradient(top, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      background: linear-gradient(to bottom, #ff8800 46%,#ff8800 46%,#ff0000 100%); 
      border: 2px solid #FF0000;
  }
  div.main_content_bot {
      background: #222;
      border: 2px solid #ff0000;
      border-bottom-left-radius: 8px;
      border-bottom-right-radius: 8px;
      border-top: none;
      width: 830px;
  }
  td.forum_section_left, td.forum_tsection_left, td.forum_tsection_mid, td.forum_tsection_right, td.forum_section_mid, td.forum_section_right, td.forum_section_single_noheight, td.profile_main, td.profile_main_v2, td.forum_asection_left, td.forum_asection_right,td.forum_section_left_noheight, td.forum_section_right_noheight
  {
      background: #222; 
      border-color: #ff0000;
      color: #FF8800; 
  }
  td.forum_tsection_single { background: #222; border-color: #ff0000; color: #ff8800; }
  td.forum_asection_single { background: #222; border-color: #ff0000; color: #ff8800 }
  /* posts */
  td.forum_asection_left, td.forum_asection_right,td.forum_section_left_noheight, td.forum_section_right_noheight { 
      color: #FF8800; 
  }
}

@-moz-document url-prefix("http://www.wsgrpg.net/forum/index.php?act=profile&p=") {
  div.main_content_mid_sections_text a { color: #555 !important; }
	div.main_content_mid_sections_text a:hover { color: #111 !important; }
}
