    border-bottom-color: #282828;
}

.tabs.tabs-justified>li {
    border-bottom: 1px solid #383838;
}

.tab-contents {
    border: 1px solid #282828;
    background-color: #282828;
}

.tab-contents>div {
    display: block;
    background-color: #282828;
}

hr, .divider-with-content, .divider-with-badge {
    border-top: 1px solid #383838;
}

