css:
@-moz-document domain("star-citizen-ru.ru") {
.cPost .ipsComment_content,
.cPost .ipsComment_content *{
	font-size: 18px !important;
	color: #adb9b4;
    font-weight: 200;
    background-color: transparent;
}

.cPost .ipsComment_content p,
.cPost .ipsComment_content span{
	background-color: transparent !important;
}

.cPost .ipsComment_content a,
.cPost .ipsComment_content a *{
	color: #5ad9e0 !important;
}

.cPost .citation{
 	font-size: 14px !important;
}

.cPost .ipsBlockquote *{
    color: #8e8989;
}

.cPost .edit{
	background: #3c3425;
    border: 1px solid #736144;
    color: #c18d56;
    text-shadow: #231d14 0px 1px 0px;
    font-size: 14px !important
}

.cPost div.bbc_spoiler_content{
	padding: 1em !important;
}

.cPost .bbc_spoiler input{
	width: auto;
    padding-left: 1em;
    padding-right: 1em
}

.starmon_helper{
    color: #5ad9e0 !important;
}

.ipsList_inline>li{
    opacity: 0.4;
}

.ipsfocusBox,
.ipsBox:not(.ipsBox_transparent):not(.ipsModerated), .ipsAreaBackground_reset, .ipsPageHeader, .ipsComment, .ipsPager, .ipsTabs_panel, .ipsWidget.ipsWidget_horizontal .ipsTabs_panel, .cCalendar, .acpBlock .ipsTable_zebra {
    background-color: rgba(23,23,23,1);
}
}
