@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mmorpg.su") {
  body {
    background: url(http://ya-vidoc3000.narod.ru/anime/112.jpg) !important;
    background-attachment: fixed !important;
    background-repeat: no-repeat !important;
    background-position: center top !important;
    -webkit-background-size: 100%;
    -o-background-size: 100%;
    background-size: 100%;
    padding-bottom: 0px;
  }
  .page-top {background: none !important;}
  div.page {
    top: 0 !important;
    background: linear-gradient(rgba(196,195,195,0.6),rgba(148,148,148,0.6));
  }
  .header-menu {background: none !important;}
  .header-menu ul li + li div, .header-menu ul li + li {background: none !important;}
  .header-menu ul li div a.header-menu-active, .header-menu ul li div p.header-menu-active {border: none !important;}
  .header-menu ul li div a, .header-menu ul li div p {
    color: white !important;
    text-shadow: 1px 1px 0px red !important;
    border: none !important;
  }
  .main-slider {background: linear-gradient(rgba(196,195,195,0.6),rgba(148,148,148,0.6)) !important;}
  .main-slider-screen-additional {color: black !important;}
  .main-slider-screen-additional a {color: black !important;}
  .main-slider-carousel p {color: black !important;}
  .list-blocks-wrapper {background: linear-gradient(rgba(196,195,195,0.6),rgba(148,148,148,0.6));}
  footer {background: none !important;}
  #bottomAd {display: none !important;}
  .article-block-mod + .w625 + .article-grid-block + .pNone {background: linear-gradient(rgba(196,195,195,0.6),rgba(148,148,148,0.6)) !important;}
  article {background: linear-gradient(rgba(196,195,195,0.6),rgba(148,148,148,0.6)) !important;}
  section > .section-heading {background: none !important;}
  .list-block > h4 {background: none !important; text-shadow: 1px 1px 0px red !important;}
  .list-block > ul > li:nth-child(2n+1) {background: none !important;}
  article.article-block-mod .article-block-mod-actions > time {background: none !important;}
  article.article-block-mod .article-block-mod-actions a.details-link {background: none !important; text-shadow: none !important;}
  article .article-block-mod-actions a.comment-link {background: none !important;}
  .list-block > ul > li > a, .list-block > ul > span > a {text-shadow: none !important;}
  section > .section-heading h2 {text-shadow: 1px 1px 0px red !important;}
  article .article-block-mod-actions div.article-block-mod-actions-vote {background: red !important;}
  article .article-block-mod-actions a.user-link {background: none !important;}
  .catalog-filter--title {background: none !important;}
  .catalog-filter {background: none !important; border: none !important;}
  .article-slider {background: linear-gradient(rgba(196,195,195,0.6),rgba(148,148,148,0.6)) !important;}
  section div.section-pagination {background: none !important;}
  .paginator .scroll_trough {background: none !important;}
  .paginator .current_page_mark {background: red !important;}
  /*Мусор*/
  a[href*="gamenet.ru"] {display: none !important;}
  /*******/
}
