css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
url-prefix("https://sdarot."),
url-prefix("http://sdarot."),
url-prefix("https://www.sdarot."),
url-prefix("http://www.sdarot.")
{


* {
    border-radius: 0 !important;
}
body {
    background: #263238 none repeat scroll 0 0 !important;
    overflow-x: hidden !important;
}
article#video {
    display: block !important;
    float: left !important;
    left: 0 !important;
    margin-top: 0 !important;
    width: 50% !important;
    z-index: 20100 !important;
}
#details div[style="overflow: hidden;"] {
    display: none !important;
}
.menu.container ul {
    width: 3em !important;
}
.menu .container ul {
    margin-right: 12% !important;
    width: 100% !important;
}
.menu ul li a:hover, .menu ul #current a {
    background: transparent none repeat scroll 0 0 !important;
    box-shadow: inherit !important;
}
div#epinfo div div p b::before, div#otherinfo p b::before {
    background: #37474f none repeat scroll 0 0 !important;
    border-color: #37474f !important;
    border-image: none !important;
    border-style: solid !important;
    border-width: 0 0 5.6em 15.6em !important;
    content: "! You should pick up an episode" !important;
    display: table !important;
    margin: 0 -0.45em 0 0 !important;
    padding: 0 2em 0 0 !important;
    position: absolute !important;
    visibility: visible !important;
    width: 16.3em !important;
}
div#epinfo div div p b, div#otherinfo p b {
    visibility: hidden !important;
}
#loading div .title * {
    visibility: hidden !important;
    display: none !important;
}
#loading div .content #txt {
    float: right !important;
    color: #ffffff !important;
    text-align: center !important;
    width: 100% !important;
    visibility: visible !important;
    margin-top: -47.5px !important;
}
article#loading {
    border-radius: 0 !important;
    display: inline-block !important;
    float: left !important;
    height: 0 !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
    margin-top: 0 !important;
    margin-bottom: 0 !important;
    position: relative !important;
    width: 50% !important;
    z-index: 20100 !important;
}
#loading div {
    margin: 0 0 -1em !important;
    padding-left: 0 !important;
    padding-right: 0 !important;
    padding-top: 0 !important;
}
#watch #details {
    float: left !important;
    left: 30px !important;
    position: relative !important;
    width: calc(100% - 20em);
}
article#watch {
    border: medium none !important;
    display: block !important;
    float: right !important;
    height: auto !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
    margin-top: 0 !important;
    padding: 0 !important;
    position: relative !important;
    right: 0 !important;
    width: 50% !important;
    z-index: 20000 !important;
}
article:not(:last-of-type) div {
    border: medium none !important;
}
#watch .poster {
    float: right !important;
    position: relative !important;
    right: 0 !important;
    width: 15em !important;
}
.category_box div {
    font-size: 8pt !important;
    margin-left: auto !important;
    margin-right: -0.2em !important;
    padding-right: 0.75em !important;
    text-align: center !important;
    width: auto !important;
}
.clear.newart {
    float: none !important;
    margin-top: 280px !important;
    width: 100% !important;
}
.category_value, .category {
    background-color: rgba(0, 0, 0, 0.85);
    display: block !important;
    height: auto !important;
    margin: 0 0 20px 20px !important;
    padding-left: 0 !important;
    padding-right: 0 !important;
    padding-top: 0 !important;
    width: calc(25% - 2.315em) !important;
}
.category_value img, .category img {
    display: inline-block !important;
    float: right !important;
    height: 12em !important;
    margin: 0 !important;
    padding: 0 !important;
    width: 50% !important;
}
#cats.content.poster {
    margin-left: 2.5em !important;
    margin-right: 2.5em !important;
    margin-top: 1.125em !important;
    padding-top: 1em;
}
.category_value a div, .category a div {
    background-color: transparent !important;
    border: medium none !important;
    box-shadow: none !important;
    display: block !important;
    float: left !important;
    height: 4em !important;
    margin: 0 !important;
    padding-top: 8em !important;
    position: relative !important;
    text-align: center !important;
    width: 50% !important;
    z-index: 2000 !important;
}
img[src="http://srv1.jpg.co.il/6/540070bbb81b7.jpg"] {
    display: none !important;
}
img[src="http://srv2.jpg.co.il/8/5471005b6c27e.jpg"] {
    display: none !important;
}
img[src="http://srv2.jpg.co.il/10/5471003f8cc20.jpg"] {
    display: none !important;
}
.container > div {
    height: 100% !important;
    position: absolute !important;
    width: 100% !important;
}
.container div #search {
    direction: rtl !important;
    display: inline-block !important;
    float: none !important;
    height: 44px !important;
    left: 0 !important;
    position: absolute !important;
    top: -44px !important;
    visibility: visible !important;
    width: 290px !important;
    z-index: 2000 !important;
}
footer, selectorSavingError, .side {
    display: none !important;
}
article {
    display: inherit;
    float: none !important;
    margin: 0 0 -1em !important;
    padding: 0 !important;
    position: absolute !important;
    width: 100% !important;
    z-index: 1000 !important;
}
header {
    display: block !important;
    height: 3em !important;
    position: relative !important;
    width: 100% !important;
}
.container {
    display: block !important;
    height: 100% !important;
    margin: 0 !important;
    padding: 0 !important;
    position: absolute !important;
    width: 100% !important;
}
.title {
    border-radius: 0 !important;
    text-align: center !important;
}
article#genres {
    background: #f7f7f7 none repeat scroll 0 0 !important;
    float: right !important;
    left: calc(100% - 11em) !important;
    position: fixed !important;
    width: 11em !important;
}
article#series {
    float: left !important;
    left: 0 !important;
    position: relative !important;
    width: calc(100% - 11em) !important;
}
.menu {
    display: block !important;
    left: 0 !important;
    position: absolute !important;
    top: 0 !important;
    width: 100% !important;
}
.category_box {
    height: 12em !important;
    margin: 0 1em 1em 0 !important;
    width: calc((100% - 10em) / 9 - 0.1em) !important;
}
#seriesList {
    margin-left: 0 !important;
    width: calc(100% - 0.75em) !important;
}
.category_box img {
    height: 12em !important;
    width: 100% !important;
}
article#genres .content {
    background: #f7f7f7 none repeat scroll 0 0 !important;
    border-color: #f7f7f7 !important;
    border-image: none !important;
    border-style: solid !important;
    border-width: 0.5em 0.666em !important;
    font-weight: bold !important;
    margin-top: 0 !important;
    position: fixed !important;
    right: -1.5em !important;
    width: 10em !important;
}
article#genres ul li {
    display: inline-flex !important;
    font-size: 9pt !important;
    margin: 3px !important;
    width: 5em !important;
}
.epbox {
    padding-bottom: 12px !important;
    padding-left: 35px;
    padding-right: 5px;
    width: calc(50% - 57.5px);
}
.epbox .info {
    width: calc(100% - 140px);
}
.epbox .poster {
    width: 130px;
}
.epbox .nums {
    margin-top: -65px;
}
.epbox div .info a {
    position: static;
    width: 100%;
}
img[src="/daniel_dont_touch/donate.jpg"] {
    display: none !important;
}
#player_wrapper, #player {
    left: 0px !important;
    width: 100% !important;
}
#next_cat, #prev_cat {
    display: none !important;
    visibility: hidden !important;
}
.category_value div, .category div {
    display: inline !important;
}
selectorSavingError {
    background: #222222 none repeat scroll 0 0 !important;
}
li .button.red:hover {
    background: #222222 none repeat scroll 0 0 !important;
}
.epbox .watch {
    margin-top: -20px !important;
}
.epbox .rating {
    margin-top: 3px !important;
}
.pages #next_page, .pages #prev_page {
    margin-top: -10px !important;
    position: absolute !important;
}
.newart .content {
	height: auto !important;
    opacity: 1 !important;
    padding-bottom: 1em !important;
    padding-top: 1em !important;
}
.category_value:first-of-type {
    margin-right: 0;
}
.category_value {
    border: 1px solid #474747;
    box-shadow: 0 0 8px 3px #1c1c1c;
    float: right;
    height: 317px;
    margin-right: 13px;
}
.category_value div {
    background-color: rgba(0, 0, 0, 0.85);
    bottom: 48px;
    color: #ffffff;
    display: none;
    font-size: 14px;
    font-weight: bold;
    line-height: 22px;
    position: relative;
    width: 100%;
}
div #tabs {
    display: block !important;
    height: auto !important;
    margin-left: 0 !important;
    margin-right: 0 !important;
    padding: 0 !important;
    position: relative !important;
    top: 15.75rem !important;
    width: auto !important;
    z-index: 2000 !important;
}
#alerts, #user-menu, #tabs {
    display: none !important;
}
header div.container {
    display: none !important;
    height: 0 !important;
    width: 0 !important;
}
.menu div.container {
    display: block !important;
    height: 3em !important;
    width: 100% !important;
}
.epbox .watch span {
    padding: 12px 10px 9px !important;
}
div.container article:first-of-type {
    float: right !important;
    right: 0 !important;
}
#details div {
    width: calc(100% - 5em);
}
#details p {
    width: calc(100% - 3em);
}
#watch .poster > div {
    width: 14.295em;
}
.qtip-tipsy {
    font-size: 0 !important;
    height: 30px !important;
}
.qtip-tipsy::before {
    font-family: "Lucida Grande",sans-serif;
    font-size: 11px !important;
    font-weight: 700;
    line-height: 30px;
    padding: 0 10px;
    position: relative;
    text-shadow: 0 1px #000;
}
body > div > div > a > img {
    display: none !important;
}


}
