}

.left_menu_nav {
    display: none !important;
}

.photos_period_delimiter_fixed { 
position: static;
}








body {
  font-family: tahoma, arial, verdana, sans-serif, Lucida Sans;
  font-size: 11px !important;
  font-weight: normal !important;
  direction: ltr;
 
}
  
#side_bar .left_label {
