}


.wall_module div.page_media_link_title {

    font-size: inherit;

    font-weight: bold;

    line-height: 15px;

    color: #446790;

    padding-bottom: 0;

}

.page_media_link_title:hover {text-decoration: underline;}

.wall_module div.page_media_link_desc_wrap {

    font-size: inherit;

}    
    
.audio_row .audio_info {

    font-size: 11px;

}
  
.audio_row .audio_performer {

    font-weight: bold;

}

.audio_page_player_title .audio_page_player_title_performer {

   
   font-weight: bold;
   color: #45688E;

} 

.audio_page_player  {

