}
  

.topics_module .module_body {

    padding: 10px 5px;

    font-size: 11px;

}
.replies_open {

    font-size: 11px;

}

textarea {

    font-size: inherit;

    font-family: inherit;

}

  
.ui_tab_count {

    font-size: 11px;

}

.page_list_module .group_desc,

.page_list_module .people_desc,

.page_list_module .people_extra {

    font-size: 0.9em;

