}

.wall_module .reply_author .author {

    font-weight: normal;

}

.wall_module .reply_author,

.wall_signed {

    font-size: 11px;

}

.wall_module .like_count {

    margin-left: 2px;

    font-size: 11px;

}

.wall_module.wl_post .wall_post_text {

    font-family: Georgia, "Times New Roman", Times, serif;

    font-size: 1.45em;

    line-height: 150%;

}    
    
.wl_post_body_wrap .wall_post_text {

    width: auto;

}    
h4.profile_online {

    font-size: 11px;

    font-weight: bold;
  

}
h2.page_name {

    padding-bottom: 2px;

