.wall_module .reply_to {text-decoration: none !important;}


.audio_friend { font-size: 11px;}

.page_counter .count { font-size: 16px;}

.page_block_header { font-size: 13px;}

.ui_crumb_count {font-size: 13px;}

.right_list_info {font-size: 11px;}

.photos_container_pretty_grid { padding: 0px 20px;}

.im-mess-stack .im-mess-stack--content .im-mess-stack--pname { font-size: 11px;}

