.page_media_place_label_inline { font-size: 12px;}

.wall_module .media_desc .lnk { font-size: 11px;}

.post_author .author, .wall_module .author, .wall_module .copy_author {font-weight: 700;}

#pv_narrow { font-size: 11px;}

.wall_module .reply .reply_wrap { padding: 8px 0px;}

.im-mess-stack .im-mess-stack--content .im-mess-stack--pname > a { font-weight: 500;}
.im-mess-stack .im-mess-stack--content .im-mess-stack--tools a {font-size: 11px;}
.im-mess-stack .im-mess-stack--content .im-mess-stack--tools { line-height: 0;}

.wall_module .media_desc { padding: 8px 0px 2px;}









