.docs_item_icon,.page_doc_row .page_doc_icon{background:url(http://i.imgur.com/Ofxu50X.png)}.top_profile_name{text-transform:capitalize}.group_list_row{width:48%;float:left;padding-left:1%;padding-right:1%;height:85px}.friends_photo_img,.friends_photo_wrap{width:65px;height:65px}
