css:
@-moz-document domain("enligne.cmontmorency.qc.ca") {
.selected,
.content,
.panel,
.tab,
td {
    background-color: #202020 !important;
    text-shadow: 0px 0px 2px #000 !important;
}
html,
body {
    background-image: url("data:image/gif;base64,R0lGODlhBAAEAKECABERESQkJP///////yH5BAEKAAIALAAAAAAEAAQAAAIFhB6nhlIAOw==") !important;
    background-repeat: repeat !important;
    background-color: #181818 !important;
}
li:hover {
    background: #333 !important;
    text-shadow: 0px 0px 2px #000 !important;
    -webkit-transition: background .2s, box-shadow .4s, border-color .2s !important;
    /* Safari */
    
    transition: background .2s, box-shadow .4s, border-color .2s !important;
    transition-timing-function: linear !important;
}
a {
    text-decoration: none !important;
    color: #ccc !important;
    text-shadow: 0px 0px 2px #000 !important;
    transition: color .4s;
}
body * {
    border-color: transparent !important;
    color: #888 !important;
    background-color: transparent !important;
    text-decoration: none !important;
}
a:hover {
    text-decoration: none !important;
    color: #fff !important;
    transition: color .4s;
    text-shadow: 0 0 4px #000, 0 0 2px #666 !important;
}
div {
    border-bottom: 1px solid rgba(255, 255, 255, .18) !important;
    border-radius: 3px !important;
    box-shadow: inset 0px 1px 5px 0px #000 !important;
    border-top-color: #000 !important;
    border-right-color: #111 !important;
    border-left-color: #111 !important;
    background-color: #141414 !important;
}
.BlueHeader img,
.loginleftImg img,
.loginrightImg img,
.loginlogo img,
.loginheader,
.loginbanner {
    display: none !important;
}
.BlueHeader .BlueXButton img,
.AgeHeader img,
.panel table td a img {
    display: inline !important;
}
.AgeHeader img,
.panel table td a img {
    background-color: #fff !important;
}
td {
    border: 1px solid transparent !important;
    border-radius: 3px !important;
    border-color: transparent !important;
    border-top-color: rgba(255, 255, 255, .12) !important;
    border-bottom-color: #555 !important;
    border-bottom: 1px solid #111 !important;
    border-left-color: #282828 !important;
}
.tab {
    border: none !important;
}
.BlueBlueButton {
    background-image: none !important;
}
td img {
    display: none !important;
}
.selected,
.horBorderColor td {
    background: #303030 !important;
}
.BluePanelSimple img {
    display: block !important;
}
.BluePanelSimple .Content td td {
	border-top-color: transparent !important;
}
.header td,
.Tab * {
    color: #fff !important;
}
table {
    box-shadow: 0px 0px 4px 0px #000 !important;
}
.ageCell {
    background: #303030 !important;
}
.ageCell td {
    border-left-color: transparent !important;
    box-shadow: 0px 0px 0px 0px #000 !important;
    border-bottom-color: transparent !important;
    border-bottom: none !important;
}
.loginmain div {
    background-image: none !important;
}
.readmore {
    border: none !important;
    box-shadow: none !important;
}
.BlueMenu .Content td {
    border-left: none !important;
}
.item td {
    border-left: none !important;
    display: block !important;
}
.BlueTableau td.Bleu {
	background-color: #193434 !important; 
    box-shadow: inset 0px 1px 5px 0px #000 !important;
}
.BlueTableau td.Vert {
	background-color: #435126 !important; 
    box-shadow: inset 0px 1px 5px 0px #000 !important;
}
.BlueTableau td.Rose {
	background-color: #815555 !important; 
    box-shadow: inset 0px 1px 5px 0px #000 !important;
}
.BlueTableau td.Blanc {
    background-color: #303030 !important;
    box-shadow: inset 0px 1px 5px 0px #000 !important; 
}

}
