css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://api.vircities.com/app/index.html") {
#short-info,
.o-main {
    max-width: /*[[width]]*/px;
    min-width: 0;
    margin:0 auto;
}
[id="trade-center/lots"] .c-content,
[id="trade-center/lots-company"] .c-content,
[id="maria/titles"] .c-content,
[id="maria/cups"] .c-content,
[id="maria/companies"] .c-content,
[id="maria/user-listing"] .c-content,
[id="job/vacancies"] .c-content,
[id="business"] .c-content,
[id="business/capital"] .c-content,
[id="business/corporations"] .c-content {
    max-width: 100% !important;
}
[id="trade-center/lots-company"] .c-content .c-card-wrapper__col,
[id="trade-center/lots"] .c-content .c-card-wrapper__col,
[id="maria/titles"] .c-content .c-card-wrapper__col,
[id="maria/cups"] .c-content .c-card-wrapper__col,
[id="maria/companies"] .c-content .c-card-wrapper__col,
[id="maria/user-listing"] .c-content .c-card,
[id="job/vacancies"] .c-content .c-card,
[id="business"] .c-content .c-card-wrapper__col,
[id="business/capital"] .c-content .c-card-wrapper__col,
[id="business/corporations"] .c-content .c-card-wrapper__col {
    max-width: 25%;
    float: left;
    clear: none;
}
[id="maria/user-listing"] .c-content .c-card {
    min-width: 15%;
    max-width: 15%;
}
[id="maria/user-listing"] .c-content .c-card:nth-of-type(1) {
    max-width: 100%;
    float: none;
}
[id="maria/cups"] .c-content .c-card-wrapper__col {
    height: 170px;
}
[id="maria/titles"] .c-content .c-card-wrapper__col {
    height: 270px;
}
[id="maria/companies"] .c-content .c-card .u-dtc {
    white-space: nowrap;
}
[id="job/vacancies"] .c-content .c-card {
    max-width: 23%;
    min-height: 155px !important;
}
/* Вписываем название в размер карточки */

[id="business"] .c-content .c-card .u-fz-34,
[id="business/capital"] .c-content .c-card .u-fz-34,
[id="trade-center/lots"] .c-content .c-card .u-fz-34,
[id="trade-center/lots-company"] .c-content .c-card .u-fz-34,
[id="maria/companies"] .c-content .c-card .u-fz-34,
[id="job/vacancies"] .c-content .c-card .u-fz-34 {
    max-height: 20px !important;
    overflow: hidden;
}
[id="business"] .u-bdrs--bottom-5 .c-panel__col {
    max-width: 50%;
    overflow: hidden;
}
[id="business/capital"] .c-content > .c-card > .c-separator,
[id="business/capital"] .c-panel > .dropdown,
[id="business/capital"] .c-content > .c-title {
    display: none;
}
[id="business/capital"] .c-content > .c-card > .c-panel {
    width: 30%;
    position: fixed;
    top: 80px;
    right: 420px;
}
[id="business/capital"] .c-content > .c-card > .c-panel:last-of-type {
    top: 85px;
    right: 20px;
}
[id="events/bb"] .bgc-violet-2 img {
    max-height: 200px !important;
    max-width: 200px !important;
}
[id="events/bb"] .bgc-violet-2 .u-dtc:nth-of-type(1) img {
    float: right;
}
[id="events/bb"] .bgc-violet-2 .u-dtc:nth-of-type(2) img {
    float: left;
}
[id="events/bb"] .c-content--wide {
    max-width: 60%;
    margin: 0 auto;
}
}
