css:
@-moz-document domain("furaffinity.net") {
/* Height of navbar and stickying */
#ddmenu {
    position: fixed;
    /* z-index: 1000; */
    max-height: 25px;
    background-color: #000000;
    opacity: 0.8;
}

/* Adjustment to support navbar */
#header {
    margin-top: 25px;
}

/* navbar text */
#ddmenu li {
    font-size: 10px;
    line-height: 25px;
}

#ddmenu li:hover {
    background-color: #252525;
}

#ddmenu .top-heading {
    padding-top: 1px;
}

/* Searchbox */
#searchbox input[type="search"] {
    height: 26px;
    background-color: #0d0d0d;
    font-size: 10px;
    
}
#searchbox input[type="search"]:focus {
    height: 26px;
    background-color: #131313;
}

/* FA logo resize */
#ddmenu .nav-bar-logo {
    padding-top: 2px;
    max-height: 20px;
}
/* avatar resize */

#ddmenu .menubar_icon_resize {
    max-height: 25px !important;
    padding-top: 2px;
    padding-right: 0px;
}
/* Removal of menubar icon */

#ddmenu .menubar_icon_resize {
    display: none;
}

/* Circle avatars and icons */
a.iconusername img,
img.avatar,
.contacticon {
    border-radius: 50%;
    max-width: 75px;
    max-height: 75px;
}
.contacticon {
    border-radius: 50%;
}


.submissionusericon {
    padding: inherit;
    
}

.submission-title {
    padding-left: 10px;
}


/* Darker colours */
.bg1 {
    background-color: #0d0d0d;
}

/* Lighter colours, User comments */
.bg3, .container-item-mid, .user-nav-content, .userpage-tabs a.current, .section-divider, #columnpage .content, #columnpage .submission-content, .user-comment, .admin-comment .user-comment {
      background-color: #131313;
}

/* Footer colours */
.bg5 {
    background-color: #0a0a0a;
}

/* Sidebar */
#columnpage .submission-sidebar, #columnpage .sidebar, #columnpage .sidebar-static {
        background-image: linear-gradient(to right,#191919 100%,transparent 100%);
}

/* User and admin comments */
.user-comment, .admin-comment .user-comment {
    background-color: #191919;
}

.user-comment:after, .admin-comment .user-comment:after {
    border-color: transparent #191919;
}


/* Buttons */
#submission_page .myButton {
    background-color: #242424;
    padding: 9px 0px;
    /* border-radius: 5px; */
}

/* Text box */
textarea, .textarea, .textbox, select, .uploadfield, .avataruploadfield, select, #dropdownbox {
    color: #cfcfcf;
    background-color: #131313 !important;
}



/* Orange bold font */
h2, .h2 {
    font-size: 18px;
    line-height: 24px;
    padding-bottom: 1px;
}


}
