    .feed__new-post__item__link.is-plus {
      background: #748EA5
    }

    .comment-create__cta-input {
      background: lightgrey
    }

    li.is-community {
      display: none
    }

    a:hover,
    .post-actions__item:hover {
      color: #91E4D0 !important
    }

    .post__content__participation-label {
      background: #51B6A4;
      bottom: 0;
      box-shadow: none;
      color: #13182C;
    }

    .page-home-feed .page-home-feed__feed {
      max-width: 60%
    }

    .feed-post__content__img {
      max-width: 100px !important;
      max-height: 100px !important;
      float: left;
      margin-right: 20px;
      margin-bottom: 20px;
    }

    .feed-post__content__tag,
    .post__content__tag {
      background-color: #748EA5;
      color: #13182C;
    }

    .feed-post__content__ext-link {
      bottom: auto;
      display: inline-block;
      position: relative;
      left: auto;
      font-size: small;
      padding-left: 5px;
    }

    .feed-post__content__ext-link__text:first-child {
      display: none
    }

    .feed-item.is-post .feed-item__main__comment-create-wrapper {
      clear: both
    }

    .footer__logo__img {
      padding: 7px;
      background-color: rgb(76, 77, 98);
    }

    @media (min-width: 850px) {
      .header.is-logged-in .header__title {
        background-color: rgb(76, 77, 98);
        padding: 7px;
        top: 7px;
      }
    }
  
  .comments-feed.is-post-overlay .comments-feed-wrapper {
    background: #13182C;
    padding: 10px;
}
  
  .community__side-info__profile.is-inside-side {

    background: #13182C;
}
  
  .community__side-info__community-banner__content-wrapper {
    padding: 10px;
    background: #13182C;
}
  
  .post-footer-sticky-wrapper.is-sticky .post__main__footer {
    background: #fff;
    box-shadow: 0 2px 4px 0 hsla(0,0%,80%,.5);
    height: 45px;
    padding: 10px 20px;
    margin-left: -20px;
    z-index: 1;
    position: relative !important;
}
  
  .side-nav__list-item__img {
    height: 28px;
    width: 28px;
    filter: grayscale(100%);
}
  
  .feed-item.is-post.is-compressed .feed-item__main__meta__img {
    display:none;
}
  
  .feed-post__content__tag:last-child, .post__content__tag:last-child {
    margin-right: 0;
    display: none;
}
  .feed-post__content {
    margin: 0px 0;
}
  
  feed-item.is-compressed .feed-item__main {
    padding: 0px 20px;
}
  
  .feed__filter__state__feed-modes-item__button.is-active {
    background: none;
    color: #fff;
}
  
  .feed-post-overlay__content {
    position: fixed;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    -webkit-overflow-scrolling: touch;
    overflow-y: scroll;
    background: rgb(18, 32, 53);
}
  
  .post-participation {
    padding: 10px 0 0;
    position: relative;
    display: none;
}
  
  .community__side-info__community-banner__bg {
    background-position: 0 40%;
    background-repeat: no-repeat;
    background-size: cover;
    padding: 20px 0 10px;
    text-align: center;
    background-image: none !important;
    background: #122035;
}
  
  .u-heart-up {
    background: #0D264D !important;
    position: relative;
}
  
  .side-nav__list-item__link:focus, .side-nav__list-item__link:hover {
    color: #5aa768;
    background: #3d73a5;
}
  
  .post-create {
    max-width: 720px;
    background: #141d41;
    margin: 0 auto;
    padding: 20px;
    border: 2px solid #263d6c;
}
  
  .Select-control {
    background-color: #26325f;
    border: 1px solid #b2bbc2;
    border-radius: 4px;
    color: #434343;
    cursor: default;
    display: table;
    outline: none;
    overflow: hidden;
    position: relative;
    min-height: 36px;
    width: 100%;
}
  
    .Select-menu-outer {
    background-color: #26325f;

}
  
  .page-communities__list-item {
    display: -webkit-box;
    display: -moz-box;
    display: box;
    display: -webkit-flex;
    display: -moz-flex;
    display: -ms-flexbox;
    display: flex;
    background: #13182c;
    margin-top: 10px;
    padding: 10px;
    border: 1px solid #4C4D62;
}
  
  .btn-default, .btn-icon-button, .onboard-tips__footer__button, .static-page.is-about .about-section__link {
    -webkit-transition: all .2s ease-out;
    transition: all .2s ease-out;
    background: #364263;
    border: none;
    border-radius: 4px;
    color: #fff;
    cursor: pointer;
    font: normal 1em Tenso,Helvetica,Arial,sans-serif;
    margin: 0;
    text-align: center;
}
  
  .page-notifications__list-item {
    background: #13182c;
    margin-top: 10px;
    border: 1px solid #4C4D62;
}
  
  .page-messages-chat, .page-messages-groups, .page-messages__no-results {
    background: #141d41;
    border: 2px solid #263d6c;
}
  
.messages-chat__header {
    -webkit-box-align: center;
    box-align: center;
    -webkit-align-items: center;
    -moz-align-items: center;
    -ms-align-items: center;
    -o-align-items: center;
    align-items: center;
    -ms-flex-align: center;
    display: -webkit-box;
    display: -moz-box;
    display: box;
    display: -webkit-flex;
    display: -moz-flex;
    display: -ms-flexbox;
    display: flex;
    background: #141d41;
}
  
  .messages-chat__content {
    position: absolute;
    top: 60px;
    right: 0;
    bottom: 90px;
    left: 0;
    background: #141d41;
    overflow-y: auto;
    padding: 10px;
}
  
  .messages-chat__message-create {
    -webkit-box-align: center;
    box-align: center;
    -webkit-align-items: center;
    -moz-align-items: center;
    -ms-align-items: center;
    -o-align-items: center;
    align-items: center;
    -ms-flex-align: center;
    display: -webkit-box;
    display: -moz-box;
    display: box;
    display: -webkit-flex;
    display: -moz-flex;
    display: -ms-flexbox;
    display: flex;
    padding: 10px;
    background: #141d41;
}
  
  .md-editor.is-mobile .md-editor__field {
    background: #141d41;
    color: #ccc;
    display: block;
    resize: vertical;
    width: 100%;
}
  
  .messages-chat__messages-list-item.is-owner .messages-chat__messages-list-item__content__body {
    background: #141D41;
}
  
    .messages-chat__messages-list-item.is-owner .messages-chat__messages-list-item__content__body p {
    color: #ccc;
}
  
  .comment__content {
    color: #ccc;
    font-size: 1em;
    font-weight: 300;
    line-height: 1.35em;
    word-wrap: break-word;
    font-family: Arial;
}
  
  .post-footer-sticky-wrapper.is-sticky .post__main__footer {
    background: #fff;
    box-shadow: 0 2px 4px 0 hsla(0,0%,80%,.5);
    height: 45px;
    padding: 10px 20px;
    margin-left: -20px;
    z-index: 1;
    position: relative !important;
    display: none !important;
}
  
  .community__side-info__community-banner__logo {
    height: 94px;
    width: 94px;
    border: 2px solid #fff;
    box-shadow: 0 0 5px 0 #727b80;
    display: none;
    margin: 0 auto;
}
  
  .community__side-info-wrapper is-stuck-in-parent {
    position: relative !important;
  }
  
  .community.is-compact-view .community__content-wrapper .community__side-info {
    position: relative !important;
}
  
  .feed-item__main__meta__date__tag.is-pinned {
    background: none;
    color: orange;
}
  
  .feed-item.is-post .feed-item__main__meta__link-user__tag.is-staff {
    background: none;
    color: #51b6a4;
    font-weight: bold;
}
  
  .side-nav__list-item.is-active, .side-nav__list-item:focus, .side-nav__list-item:hover {
    background: #305165;
}
  
  .account-section {
    background: #13182C;
    margin: 10px 0;
    padding: 20px;
    border: 1px solid #4C4D62;
}
  
  .nav.is-tabs .nav__item {
    background: #13182c;
    box-shadow: 0 2px 2px 0 hsla(0,0%,79%,.5);
    margin-left: 10px;
}
  
  .post__content__poll-option.is-selected {
    background: #364263;
    border: 1px solid #ccc;
}
  
    .post__content__poll-option:hover {
    background: #364263;
}
  
  .post__content__poll-result__progress-bar {
