    display: block;
}
  
  .post-action__context-menu {
    background: #364263;
    border: 1px solid #122035;
    border-top: 0;
    padding: 0 10px;
    position: absolute;
    top: 100%;
    z-index: 1;
}
  
    .menu__item:hover {
    background: #122035;

}
  
  .menu__item__button {
    display: inline-block;
    width: 100%;
}
  
  .action-modal {
