    position: relative;
    text-align: center;
}
  
  .account-profiles-list-item__is-default, .account-profiles-list-item__make-default {
    position: absolute;
    right: 0;
    bottom: 0;
    left: 0;
    background: #0c2633;
    border-top: 1px solid #e5eaed;
    font-size: .85em;
    padding: 5px;
}
  .community__banner {
    height: 0;
    width: 100%;
    padding-bottom: 23.52941%;
    filter: grayscale(50%);
    opacity: .8;
}
  
  .comment-action__context-menu {
    background: #364263;

}
  
  .comment-actions__item.is-active {
    background: #364263;

  
}
  
  .comment-edit {
