    background: #364263;
    box-shadow: 0 2px 4px 0 hsla(0,0%,80%,.5);
    display: block;
    padding: 20px;
}
  
  .static-page {
    background: #364263;
    margin: 0 auto;
    padding: 20px;
}
  
  .static-page code {
    background-color: #0c2738;
    cursor: default;
    display: block;
    line-height: 1.25em;
    margin: 10px 0;
    padding: 15px;
}
  
  .nav.is-tabs .nav__item.is-active {
    background: #202d4e;
    box-shadow: none;
}
  
  .account-message, .account-profiles-message, .appstore-item__header__info__actions__message, .comment-create.is-no-identity-reason, .comments-feed__no-identity-reason, .community-create__content__block-message, .community-create__content__similar-communities, .community-edit__content__block-message, .help-desk__report__response__footer__note, .post-create__content__section__no-identity-reason, .post-create__content__section__posting-rules, .post-edit__content__community-rules, .profile-edit__note, .profile-view__activity__anonymous-note {
