css:
@-moz-document url-prefix("http://zhidao.baidu.com/link?"), url-prefix("https://zhidao.baidu.com/link?") {
body,
.mod-shadow,.qb-section .qb-side,.mod-noshadow,.wgt-answers #show-hide-container,.wgt-user.mavin .mavin-title{
	background:#F7F7F7;
}
.qb-zan-eva{
	background:#fbfcfc url(http://iknowpc.bdimg.com/static/question/widget/sample/value-comment/img/zan-eva-line_d7cd6d2.png) no-repeat;
}
}

@-moz-document url-prefix("http://zhidao.baidu.com/question"), url-prefix("https://zhidao.baidu.com/question") {
.wgt-ask,.qb-section .qb-side,.mod-shadow,body,.wgt-user.mavin .mavin-title,.mod-noshadow{
	background-color:#fafafa;
}
.evaluate, .evaluate-mine, .evaluated{
	background:#fbfcfc url(http://iknowpc.bdimg.com/static/question/widget/value-comment/img/i-evaluate-good-new_1c15f3a.png) 11px center no-repeat;
}
.evaluate.evaluate-bad, .evaluate-mine.evaluate-bad, .evaluated.evaluate-bad{
	background:#fbfcfc url(http://iknowpc.bdimg.com/static/question/widget/value-comment/img/i-evaluate-bad-new_324e637.png) 11px center no-repeat;
}
}
