css:
/* Firefox Dark Blue script by Whitelightning */


/* AGENT_SHEET */

@namespace xul url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

body {
	-webkit-font-smoothing: antialiased;
/* -moz-osx-font-smoothing: grayscale; */
}

#bookmarks-view-children  { background-color: #001f33 !important;
                            color: White !important;
                            font-family: Segoe ui !important;
                            font-size: 15px !important;
                          }

#sidebar-title { background: transparent !important; }

#sidebar-search-container { background-color: #001f33 !important;
              color: White !important;
              font-family: Segoe ui !important;
              font-size: 14px !important; }

#sidebar-search-label { background-color: #001f33 !important;
                        color: White !important;
                        font-weight: bold !important;
                        font-family: Segoe ui !important;
                        font-size: 14px !important;
                      }

#categories { background-color: #001f33 !important;
              color: White !important;
              font-family: Segoe ui !important;
              font-size: 14px !important;
            }

#nav-bar {  background: transparent !important;
            border-color: transparent !important;
            margin-top: -12px !important;
            margin-bottom: -4px !important;
            padding: 0px 0px 0px 0px !important;
            font-weight: bold !important;
            font-family: Segoe ui !important;
            font-size: 14px !important; 
         }

#urlbar, #searchbar textbox {  color: Black !important;
                               font: 12pt Segoe ui !important;
                               margin-top: -1px !important;
                               margin-bottom: 0px !important;
                            }


#navigator-toolbox { background: transparent !important;
                     color: white !important;
                     font: 10pt Segoe ui !important;
                     margin-top: -1px !important;
                     margin-bottom: 2px !important;
                    }

#TabsToolbar { background: transparent !important;
               border-color: transparent !important;
               margin-top: 0px !important;
               margin-bottom: 4px !important;
               padding: 4px 0px 4px 0px !important;
             }

.tabbrowser-tab[selected="true"]{ 
                                  font-weight: bold !important;
                                  font-family: Segoe ui !important;
                                  font-size: 12px !important; 
                                  border-bottom: 2px solid #ff1a1a !important;
                                  border-top: 1px solid #FFA500 !important;
                                  border-left: 1px solid #FFA500 !important;
                                  border-right: 3px solid #FFA500 !important;
                                  padding: 1px 2px 2px 1px !important;
                                 }

.new-tab-button { 
                   font-weight: bold !important;
                   font-family: Segoe ui !important;
                   font-size: 12px !important; 
                   border-bottom: 3px solid #FFA500 !important;
                   border-top: 1px solid #FFA500 !important;
                   border-left: 1px solid #FFA500 !important;
                   border-right: 3px solid #FFA500 !important;
                   margin-top: 4px !important;
                   margin-bottom: 4px !important;
                   margin-left: 4px !important;
                   margin-right: 4px !important;
                   padding: 4px 4px 4px 4px !important;
                }

.tabbrowser-tab:not([selected="true"]) { font-family: Segoe ui !important;
                                         font-size: 12px !important; 
                                         padding: 4px 2px 4px 2px !important;
                                         border-bottom: 3px !important;
                                       }

.tabs-closebutton-box, .tab-close-button {display: -moz-box !important}

.tab-close-button[pinned="true"] { display: none !important; }

.scrollbutton-up, .scrollbutton-down { display: none !important; }

#status-bar, #addon-bar, #browser-bottombox {  
                                              outline:none !important;
                                              border:none !important;
                                              font-family: Segoe ui !important;
                                              font-size: 14px !important;
                                              margin-top: 0px !important;
                                              margin-bottom: 0px !important;
                                              padding: 2px 0px 2px 0px !important;
                                            }
            
#alltabs-button { visibility:visible !important }

scrollbar{ -moz-appearance: none !important;
           background: none #e0e0ef !important;
           padding: 0px !important;
           width: 11px !important
         }
scrollbar[orient="vertical"] { padding: 0px !important; }

scrollbar[orient="horizontal"] { height: 14px !important; }

scrollbar thumb { -moz-appearance: none !important;
                 background: none #00004D !important;
                 border: none !important;
                 border-radius: 11px;
                }
scrollbar thumb:hover { background: none #323270 !important; }

scrollbar thumb:active{ background: none #4c4c82 !important }



scrollbarbutton[sbattr="scrollbar-up-top"],
scrollbarbutton[sbattr="scrollbar-down-top"],
scrollbarbutton[sbattr="scrollbar-up-bottom"],
scrollbarbutton[sbattr="scrollbar-down-bottom"]{ -moz-appearance: none !important;
                                                 padding: 9px !important;
                                                 width: 12px !important
                                               }

scrollbar > scrollbarbutton[disabled="true"] {
    opacity: .5 !important}

scrollbar > scrollbarbutton:not([disabled="true"]):hover {
    border-color: #323270 !important;
    background-color: #323270 !important;
    background-clip: padding-box !important}

scrollbar > scrollbarbutton:not([disabled="true"]):hover:active,
scrollbar > scrollbarbutton:not([disabled="true"])[active="true"] { -moz-appearance: none ! important;
                                                                    border-color: #7f7fa6 !important;
                                                                    background-color: #7f7fa6 !important;
                                                                    background-clip: padding-box !important;
                                                                  }

@-moz-document url("about:newtab") { body{  background: none !important;
                                            background-color:#323270!important;  
                                         }
                                   }
