css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("1libertaire.free.fr"), domain("multitudes.net"), domain("www.cairn.info"), domain("revues.org"), domain("unmondemoderne.wordpress.com"), domain("temps-marranes.fr"), domain("slate.fr"), domain("plusloin.org"), domain("lemonde.fr"), domain("erudit.org"), domain("infokiosques.net"), domain("post-scriptum.org") {
  .normal, tbody, p {
      width: 700px;
      line-height: 1.7em !important;
      font-size: 20px !important;
     opacity: 0.97;
  }
  
  .normal:hover, tbody:hover, p:hover {
         opacity: 1;
  }
}

@-moz-document domain("vadeker.net") {
  div {
      width: 700px;
      line-height: 1.7em !important;
      font-size: 20px !important;
  }
}


@-moz-document domain("revues.org") {
#text {overflow: visible!important;}
}

@-moz-document domain("lemonde.fr") {
  .col_droite {
  margin-left: 170px !important;
  position: absolute;
  }
  
  
  .col_droite p {
      width: 320px;
      line-height: 1.7em !important;
      font-size: 13px !important;
     opacity: 0.97;
  }
  .snippet, .multimedia-embed {width: 720px!important;}
}
