@-moz-document url-prefix("http://runouw.com/forums/") {
/* Credit to Bogdan/Arturia for some code taken from https://userstyles.org/styles/122075/runowinter */
/* Credit to FF/l.m for background images */


/* === Reset backgrounds === */
span.corners-top, span.corners-bottom, span.corners-top span, span.corners-bottom span, .forabg, .forumbg, .bg1, .bg2, ul.forums,
li.row:hover, #tabs a {
	background: none !important;
}

body {
    background-image: url(http://i.imgur.com/xfyM1rZ.jpg) !important;
}


/* === Background colors === */
.forabg, .forumbg, .navbar2 {
	background: rgba(50,105,160,0.8) !important;
}

.headerbar {
	background: none !important;
}

.rules {
    background-color: #467 !important;
}

#navigation a {
	background: linear-gradient(to right, rgba(96,96,96,0.9) 0%, rgba(112,112,112,0.6) 100%) !important;
}

#navigation a:hover, #navigation #active-subsection a {
	background: linear-gradient(to right, rgba(64,64,64,0.9) 0%, rgba(80,80,80,0.6) 100%) !important;
}

html, body, select, input.search, .inputbox, .pagination span a, .pagination span a:link, .pagination span a:visited,
dl.codebox {
	background-color: #FFF !important;
}
	
.bg1, ul.forums, .panel, #tabs a {
	background-color: rgba(166,212,229,0.85) !important;
}

.bg2 {
	background-color: rgba(166,208,233,0.85) !important;
}

.bg3 {
    background-color: rgba(0,0,0,0) !important;
}

li.row:hover {
	background-color: #92CCF9 !important;
}

blockquote {
	background-color: #abc !important;
}

blockquote blockquote {
    background-color: #9ab !important;
}

blockquote blockquote blockquote {
    background-color: #89a !important;
}

.pagination span a:hover, .pagination span strong {
	background-color: #888 !important;
}

.pagination span strong {
	background-color: #a96 !important;
}

.postlink:hover {
	background-color: #ddd !important;
}

#tabs a span, #tabs a {
	background: inherit;
    background-color: rgba(0,0,0,0) !important;
}


/* === Text colors === */
html, body, .headerbar, h2, h3, .postbody, .postbody .content, #tabs .activetab a span, ul.topiclist li,
.panel li.header dd, .panel li.header dt {
	color: #805030 !important;
}

h3 a {
    color: #58a !important;
}

div.postbody a:link:not(.username-coloured) {
    color: #58a !important;
}

div.postbody a:visited:not(.username-coloured) {
    color: #69b !important;
}

.inputbox {
	color: #bbb !important;
}

a:hover {
	color: #AF0000 !important;
}

.postprofile, dl.details dd {
	color: #777 !important;
}

li.row strong, .postprofile strong, label, .panel, #tabs a span, dl.details dt {
	color: #666 !important;
}

select, input.search, .inputbox, .pagination span a, .pagination span a:link, .pagination span a:visited {
	color: #888 !important;
}

.postlink:hover, .pagination span a:hover, .pagination span strong {
	color: #333 !important;
}

dl.codebox code {
	color: #6a6 !important;
}

#navigation a:hover, #navigation #active-subsection a {
    color: #e55 !important;
}

.navbar2 a {
    color: #b2d4e4 !important;
}

.navbar {
    color: #805030 !important;
}

.navbar2 {
    color: #ccc !important;
}

.RP {
    color: #669 !important;
}


/* === Text colors for staff ranks === */
a[style*="#9933CC"] {
    /*Site Rep*/
    color: #9933CC !important;
}

[style*="#00CCFF"] {
    /*Level Mod*/
    color: #00A7D1 !important;
}

a[style*="#FF6600"] {
    /*Mod*/
    color: #D66300 !important;
}

a[style*="#00CC00"] {
    /*Global Mod*/
    color: #00B200 !important;
}

a[style*="#EE0000"] {
    /*Admin*/
    color: #D80000 !important;
}


/* === Border colors === */
hr {
	border-color: #222 #333 #333 !important;
}

blockquote {
	border-color: #333 !important;
}
	
dl.codebox {
	border-color: #363 !important;
}

select, input.search, .inputbox, .pagination span a, .pagination span a:link, .pagination span a:visited, .pagination span a:hover, .pagination span strong {
    border-color: #444 !important;
}

h2, h3 {
	border-bottom-color: #222 !important;
}

ul.navlinks, #cp-main h3 {
	border-bottom-color: #333 !important;
}
	
dl.codebox dt {
	border-bottom-color: #363 !important;
}

#tabs .activetab a {
    border-bottom-color: #444 !important;
}

.postlink {
	border-bottom-color: #a96;
}

li.row {
	border-top-color: #333 !important;
	border-bottom-color: #222 !important;
}
	
.signature, ul.cplist {
	border-top-color: #333 !important;
}

ul.topiclist dd, .postprofile {
	border-left-color: #333 !important;
}


/* === Other === */
.forabg, .forumbg, .navbar2, #navigation a, .rules {
	border-radius: 4px;
}

.post, blockquote, .panel, #message {
    border-radius: 5px;
}

#wrap {
	width: 76%;
	margin:0 auto;
	margin-top:5px;
	background-image: url(http://i.imgur.com/JXde8N0.png);
	border: 2px solid #4B7391;
    border-radius: 6px;
}

.navbar {
	background: none;	
}

div#page-header a:link, div#page-header a:visited, ul.linklist a:link, ul.linklist a:visited {
    color: #167AB0;
}

a.forumtitle:link, a.forumtitle:visited, a.topictitle:link, a.topictitle:visited {
    color: #167AB0;
}


