css:
@-moz-document domain("www.androidpolice.com") {
/* Top Nav */

.band.sub-nav, .ap-nav-wrapper {
    background-color: #37474F;
    border: none;
}

.band.sub-nav {
    box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
}

.band.header {
    background-color: #37474F;
    background-image: none;
}

ul.sub-nav>li .tip-us {
    color: #F44336;
}

ul.sub-nav li a {
    color: #ECEFF1;
}

/* Main stuff; background, front-page post colors, etc. */

.container .column, .container .columns {
    float: none;
}


.band.content.main {
    background-color: #263238;
}

.band.footer {
    display: none;
}

img.img-hero {
    display: block;
    margin: auto;
    width: 70%;
    box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
  	transition: all 0.3s cubic-bezier(.25,.1,.25,1);
    border-radius: 4px;
    max-width: 512px;
}

img.img-hero:hover {
    box-shadow: 0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22);
    max-width: 720px;
}

.post {
    border: none;
}

.post h2 {
    color: #ECEFF1;
}

.post-content img {
    max-width: 30%;
    transition: all 0.3s cubic-bezier(.25,.1,.25,1);
}

.post-content img:hover {
    max-width: 60%;
}

.post.large-post.list-view {
 	background-color: #37474F;
    box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
}

.post.med-post.list-view {
 	background-color: #37474F;
    box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
}

.post.small-post.list-view {
    background-color: #37474F;
    box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
}

.post-hero img {
    align-content:center;
}

.post-header a {
    color: #ECEFF1;
}

.post-content p {
    color: #ECEFF1;
}

.post .post-content a:not(.read-more):not(.btn):not(.banner):not(.no-style):visited {
    color: #0277BD;
}

.post .post-content a:not(.read-more):not(.btn):not(.banner):not(.no-style) {
    color: #03A9F4;
}

.post-content li {
    color: #ECEFF1;
}

blockquote p {
    color: #ECEFF1;
    text-shadow: 1px 1px 0 black
}

/* Post Time */

.post-date-time {
    color: #ECEFF1;
}

.post-date-time:hover {
    color: #ECEFF1;
}

/* Author Pic */

a.author-pic.hidden-phone img {
    border-radius: 50%;
}

/* "What's New" section */

.note.note-note li {
    color: #212121;
}

/* Hide the "read more" button and bottom of posts */

div.clear {
    display:none;
}

a.read-more {
    display:none;
}

/* "Read More" Button 

a.read-more {
    background-color: #F44336;
    box-shadow: 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23);
}

a.read-more:hover {
    background-color: #C62828;
      box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
}

*/


/* Categories */

.categories li a {
    color: #ECEFF1;
}

/* Page Nav */

.wp-pagenavi {
    background-color: #37474F;
    border: none;
    box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
}

.wp-pagenavi span.pages {
    color: #ECEFF1;
}

.wp-pagenavi span.current {
    color: #ECEFF1;
    border-bottom:4px solid #ECEFF1;
}

/* Post page */

.single .post {
    background-color: #37474F;
    box-shadow: 0 -16px 20px rgba(0,0,0,0.19), 0 0px 0px rgba(0,0,0,0.23);
}

.post strong {
    color: #ECEFF1;
}

.horizontal-boxes li a {
    color: #ECEFF1;
}

.horizontal-boxes li a:hover {
    color: #212121;
}

div.note.note-warning p {
    color: #212121;
}

div.note.note-tip p {
    color: #212121;
}

div.note.note-tip strong {
    color: #212121;
}

.post h3 {
    color: #ECEFF1;
}

blockquote div.inner strong {
    color: #212121;
}

/* dark disqus code */

div#comments .section-header {
    border: none;
    background-image: none;

}

div#comments .header-bg, .nav-bg:after {
    background-image: none;
    
}

.publisher-anchor-color a {
    color: #ECEFF1 !important;
}

.primary .post-footer {
    border: none;
}

.primary .post-footer-inner {
    background-color: #37474F;
    box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
}
    
/* Useless stuff (social, ads, etc) */

.share-box {
    display: none;
}

#sbar {
    display: none;
}

div.bo-wrapper {
    display:none;
}

.post-footer .article-nav {
    display: none;
}

.single-post-widget {
    display: none;
}
}
