css:
/******************************************************************************************

MathematicaStackexchange_DarkStyle v1.1 (27.08.2016)

- v1.1
- to get proper MathJax coloring: 
                                  right-click mouse
                                    ► Math Settings
                                      ► Math Renderer
                                        ► Common HTML


Custom Stack Exchange and Stack Overflow chat: 
  https://userstyles.org/styles/96444/custom-stack-exchange-and-stack-overflow-chat
 was used as starting point for this style

*******************************************************************************************/


/*---@namespace url(http://www.w3.org/1999/xhtml);---*/
@-moz-document domain("mathematica.stackexchange.com"),
domain("chat.stackexchange.com") {

/***=== Main ===***/
 
/*** Login-Page ***/
#logout-form,
#add-login-page #switch, #login-page #switch, #signup-page #switch,
#add-login-page #formContainer, #login-page #formContainer, #signup-page #formContainer {
    background-color: #1E1E1E;
    border-color: #1E1E1E;
}
#add-login-page .or-hr, #login-page .or-hr, #signup-page .or-hr {
    border-color: #3E3E3E;
}
#add-login-page #or, #login-page #or, #signup-page #or {
    background-color: #3E3E3E;
    color: #AAAAAA;
}

/*** Topbar ***/
.topbar {
    background-color: #060606;
}
.topbar .topbar-links .search-container input[type="text"] {
    background-color: #1E1E1E;
    color: #AAAAAA;
    box-shadow: none;
}

.topbar .topbar-links .search-container input[type=text]:focus,
.topbar .topbar-links .search-container input[type=text]:hover {
    background-color: #000;
    color: #FFF;
    border-color: #0074FF;
    box-shadow: none;
}
.topbar-dialog {
    background-color: #1E1E1E;
    border-color: #3E3E3E;
}
.topbar-dialog .header {
    background-color: #3E3E3E;
}
.topbar-dialog .header h3 {
    color: #f2f2f2;
}
.topbar-dialog .modal-content li {
    border-bottom-color: #3E3E3E;
}
.topbar-dialog .unread-item {
    background-color: #000052; /* #3E3E3E; */
}
.topbar-dialog .modal-content li:hover {
    background-color: #060606;
}
.topbar-dialog.inbox-dialog .inbox-item .item-content .item-summary, .topbar-dialog.modInbox-dialog .inbox-item .item-content .item-summary, .topbar-dialog.achievements-dialog .inbox-item .item-content .item-summary {
    color: #AAAAAA;
}
.topbar-dialog.siteSwitcher-dialog .current-site-container {
    background-color: #060606;
}
.topbar-dialog.siteSwitcher-dialog .current-site li:hover {
    background-color: #000052;
}
.topbar input[type="text"] {
    background-color: #060606;
    box-shadow: none;
    border-color: #060606;
}
.topbar-dialog .site-filter-input:focus,
.topbar-dialog .site-filter-input:hover {
    box-shadow: none;
    border-color: #0074FF;
    color: #AAAAAA;
}
.topbar .topbar-icon-on, .topbar .topbar-icon-on:hover {
    background-color: #3E3E3E !important;
}
.topbar .yes-hover:hover,
.topbar .topbar-links .profile-me:hover,
.topbar .topbar-links .topbar-menu-links a:hover {
    background-color: #1E1E1E;
}
.topbar-dialog.help-dialog .item-summary {
    color: #AAAAAA;
}
.topbar .icon-help.topbar-icon-on {
    color: inherit !important;
}

/*** Page ***/
body {
    background-image: none !important;
    background-color: #000000 !important;
    color: #f2f2f2 !important;
}
#footer {
    background-image: none !important;
    background-color: #000000 !important;
}
#footer-menu a {
    color: #2A4A7C !important;
}
.container {
    border-bottom: 0px solid transparent;
}
#content {
    background-color: #060606 !important;
}
.excerpt {
    color: inherit !important;
}
.new-post-activity,
#new-answer-activity {
    background-color: #1E1E1E !important;
    color: #ff0000 !important;
}
.new-post-activity > a {
    color: #ff0000 !important;
}

a {
    color: #0074FF !important;
}
a:hover, 
a:active {
    color: #FFF !important; /* #0058FF !important; */
}
.comment-date, .comment-date > a {
    color: #2A4A7C !important; 
}
.comment-date > a:hover {
    color: #AAAAAA !important; 
}
.nav.askquestion a, .nav.askquestion a:hover, .nav.askquestion a:visited {
    color: #ff6600 !important;
}
.nav a:hover {
    transition-duration: 0.1s;
    background-color: #060606 !important;
}
.nav a {
    color: #AAAAAA !important;
    /*background-color: #3E3E3E !important;*/
}
.nav li.youarehere a {
    background: none repeat scroll 0% 0% #060606 !important;
}
#tabs a, .tabs a, .newnav .tabs-list-container .tabs-list .intellitab a {
    color: #AAAAAA !important;
}
#tabs a:hover, .tabs a:hover, #tabs a.youarehere, .tabs a.youarehere, .newnav .tabs-list-container .tabs-list .intellitab a:hover, .newnav .tabs-list-container .tabs-list .intellitab a.youarehere {
    border-bottom: 2px solid #C00000 !important;
    color: #C00000 !important;
}
.question-summary {
    border-bottom: 1px solid #1E1E1E !important;
}
.user-info {
    background-color: #060606 !important;
}
.started-link {
    color: #AAAAAA !important;
}
.users-page .user-info .user-details > a,
.started a:not(.started-link), .started .mod-flair, 
.started a:hover:not(.started-link), .started .mod-flair:hover {
    color: #0446D9 !important;
}
.answered {
    color: DarkGreen;
}
.answered strong {
    color: inherit;
}
.answered-accepted {
    color: LightGreen;
    background-color: DarkGreen;
}
.statscontainer .votes {
    color: inherit;
}
.vote-count-post strong {
    color: inherit !important;
}
.vote span {
    color: inherit !important;
}
.statscontainer .views {
    color: #3E3E3E !important;
}

.page-numbers {
    border-color: #1E1E1E;
}
.page-numbers:hover {
    background-color: #3E3E3E;
}
a.page-numbers {
    color: #AAAAAA !important;
}
.page-numbers.current {
    background-color: #1E1E1E;
}

#user-menu {
    background-color: #1E1E1E;
    border-color: #1E1E1E;
}

/*** Sidebar ***/
.sidebar {
    color: #3E3E3E !important;
}
.module.community-bulletin {
    background-color: #1E1E1E;
    border-color: #1E1E1E;
}
.module.community-bulletin hr {
    background-color: #3E3E3E;
}
.module.community-bulletin .question-hyperlink {
    color: #2A4A7C !important;
}
#interesting-tags a {
    color: #2A4A7C !important;
}
#hot-network-questions a {
    color: #2A4A7C !important;
}

.show-votes .sidebar-linked .spacer>a:first-child .answer-votes.answered-accepted, .show-votes .sidebar-related .spacer>a:first-child .answer-votes.answered-accepted {
    color: lightgreen;
    background-color: darkgreen;
}
.show-votes .sidebar-linked .spacer>a:first-child .answer-votes, .show-votes .sidebar-related .spacer>a:first-child .answer-votes {
    background-color: #1E1E1E;
    color: DarkGreen;
}
.module h4 {
    color: inherit;
}

/*** Tags ***/
.post-tag {
    color: #AAAAAA !important;
    background-color: #1E1E1E !important;
    border: 1px solid #1E1E1E !important;
    border-radius: 2px 2px 2px 2px !important;
}

.content-inside .fl table tr {
    border-bottom-color: #1E1E1E !important;
}


/*** Search ***/
#bigsearch .col1 input, .bigsearch .col1 input, #bigsearch .col1 textarea, .bigsearch .col1 textarea {
    background-color: #1E1E1E;
    border-color: #1E1E1E;
}
.result-highlight {
    font-weight: bold;
    background: #007;
    color: #FFF;
}

/***=== Q&A ===***/

blockquote {
    padding: 5px 15px 5px 5px;
    background-color: #1E1E1E;
    border-left: 2px solid #AAAAAA;
}
.post-text pre > code:first-child, .wmd-preview pre > code:first-child {
    color: #000000 !important;
}
.tag-editor {
    border-color: #1E1E1E;
    background-color: #1E1E1E;
}

.wmd-button-bar {
    border-color: #1E1E1E;
}

#title {
    background-color: #1E1E1E;
    border: none;
    color: #fff;
    font-weight: bold !important;
}

#question-header,
.comments,
tr.comment > td,
.wmd-preview {
    border-color: #1E1E1E !important;
}
.question-page #answers .answer {
    border-color: #3E3E3E !important;
}
code {
    background-color: #1E1E1E !important;
/*  color: #ffffff !important;*/
    font-weight: bold !important;
}
.show-votes .sidebar-linked .answer-votes.answered-accepted, .show-votes .sidebar-related .answer-votes.answered-accepted {
    color: LightGreen;;
    background-color: DarkGreen;
}
.show-votes .sidebar-linked .answer-votes, .show-votes .sidebar-related .answer-votes {
    color: DarkGreen;
    background-color: #1E1E1E;
}

.question-status {
    background-color: #1E1E1E;
    border-color: #1E1E1E;
}

.owner {
    background-color: #1E1E1E !important;
}

span.js-link-separator {
    color: #1E1E1E;
}
 
pre code {
    background-color: #EEEEEE !important;
}

pre {
    color: #000 !important;
}

input[type="submit"], input[type="button"], button, .button, a.button, a.button:visited, .btn {
    background-image: none !important;
    background: none !important;
    text-shadow: none !important;
    box-shadow: none !important;
    background-color: #3E3E3E !important;
    color: #AAAAAA !important;
    border-color: #3E3E3E !important;
}
input.btn-clear[type="submit"]:hover, input.btn-clear[type="button"]:hover, button.btn-clear:hover, .btn-clear:hover, input.btn-clear[type="submit"]:focus, input.btn-clear[type="button"]:focus, button.btn-clear:focus, .btn-clear:focus {
    background-color: #3E1E1E;
}

input[type="text"] {
    color: #AAAAAA;
}

.vote-revision td,
.owner-revision td,
.revision td {
    background-color: #1E1E1E;
    color: #AAAAAA;
}
.revision-comment {
    color: #AAAAAA;
}

.post-text hr, .wmd-preview hr {
    background-color: #3E3E3E;
}

/* remove fading of show more comments comments */
.comment {
  background-color: transparent !important;
 }

.share-tip {
    background-color: #1E1E1E;
    border-color: #1E1E1E;
    box-shadow: none;
}

/***=== Rooms ===***/

.ad502-room,
.roomcard, .usercard {
    background-color: #1E1E1E !important;
    color: #AAAAAA;
    border-style: hidden;
    box-shadow: none;
    /*box-shadow: 0px 1px 3px #060606;*/
}
.roomcard h3, 
.roomcard h3 .room-name a {
    color: #0446D9 !important;
}

.room-header, .user-header {
    border-color: #3E3E3E;
}

.notification, #feed-ticker {
    background-color: #1E1E1E !important;
    background: -moz-linear-gradient(#3E3E3E, #000); /* For Firefox 3.6 to 15 */
    box-shadow: 0px 2px 15px rgb(0, 0, 195);
}

body.outside #container {
    color: inherit;
}
.nav li {
    background-color: inherit;
}
.subheader {
    border-bottom: 1px solid #3E3E3E;
}
.subheader #tabs a, .subheader #tabs .disabled {
    border-style: solid solid hidden solid;
    border-color: #3E3E3E #3E3E3E #3E3E3E; 
}
.subheader #tabs a.youarehere {
    background-color: inherit !important;
    border-color: #3E3E3E #3E3E3E #3E3E3E; 
}
.subtabs a {
    border-color: #1E1E1E;
}
.usercard-xxl, .roomcard-xxl {
    background-color: #1E1E1E;
    border-color: #1E1E1E;
    box-shadow: none;
    color: #AAAAAA;
}

/***=== User ===***/

.user-card .user-card-name {
    color: #f2f2f2 !important;
}
.top-badge a {
    color: #0074FF !important;
}
.top-badge {
    background-color: #1E1E1E !important;
}
.card {
    background-color: #1E1E1E !important;
    border-color: #1E1E1E !important;
}
.card.rep-card .rep {
    color: inherit !important;
}

.avatar-card {
    background-image: none;
    background-color: #1E1E1E !important;
    border: none;
    box-shadow: none;
}
.avatar-card .reputation {
    color: inherit;
}
.title-section {
    border-bottom-color: #1E1E1E !important;
}
.user-card .user-stats .number {
    color: inherit;
}
.top-posts .post-container {
    border-bottom-color: #1E1E1E !important;
}
.badges-content .badges-wrapper {
    border-color: #1E1E1E !important;
}
.top-tags .tag-wrapper .tag-container {
    background-color: #1E1E1E !important;
}
.top-tags .tag-wrapper .tag-container .col {
    border-color: #060606 !important;
}
.top-tags .tag-wrapper .tag-container .col-12 .post-tag, .top-tags .tag-wrapper .tag-container .col-6 .post-tag, .top-tags .tag-wrapper .tag-container .col-4 .post-tag {
    background-color: #3E3E3E !important;
  /*background-color: #060606 !important;*/
}

.rep-card .progress-bar .bar, .progress-bar.green .bar {
    border-color: #3E3E3E !important;
}
.popup-badges .all-badge-progress .bars-wrapper .progress-bar .bar .percent, .popup-badges .all-badge-progress .bars-wrapper .progress-bar-large .bar .percent,
.message-config .bars-wrapper .progress-bar .bar .percent,
.rep-card .progress-bar .percent, .progress-bar.green .percent {
    background-color: DarkGreen !important;
    border: none; 
}
.message-config .bars-wrapper .progress-bar .bar,
.popup-badges .all-badge-progress .badge-progress .bars-wrapper .bar,
.popup-badges .all-badge-progress .badge-progress:hover .bars-wrapper .bar {
    background-color: #3E3E3E !important;
}
.popup-badges .all-badge-progress .badge-progress:hover {
    background-color: #060606;
}

.progress-bar .label {
    color: #3E3E3E !important;
}
.rep-increase {
    color: LightGreen;;
    background-color: DarkGreen;
}

.message.message-config {
    background-color: #1E1E1E !important;
}
.message.message-config.tooltip .message-text {
    color: #AAAAAA;
}
.message.message-config h1, .message.message-config h2, .message.message-config h3, .message.message-config h4 {
    color: #AAAAAA;
}

.user-page .card .choose-tracker-menu, .user-show-new .card .choose-tracker-menu {
    box-shadow: none;
    border: none;
    background-color: #3E3E3E;
}
.user-page .card .choose-tracker-menu li:hover, .user-show-new .card .choose-tracker-menu li:hover {
    background-color: #1E1E1E;
}
.user-page .subheader.reloaded .mini-avatar .name {
    color: inherit;
}

.card.rep-card .graph .line {
    stroke: DarkGreen !important;
}

.card.rep-card .graph {
    background-image: none !important;
}
.badge-tag {
    color: #AAA;
    border-color: transparent;
    /* background-color: #3E3E3E; */
    background-color: rgb(47, 51, 55);
}
.popup-badges .all-badge-progress .recommended {
    background-color: DarkGreen;
}
.badge-tag:hover {
    color: #FFF;
    background-color: #060606;
    border-color: #060606;
}
.badges-content .badges-number .name {
    color: #000;
}
.badge1-alternate {
    background-color: GoldenRod;
    border-color: #DAA520;
}
.badge1-alternate .badge1 {
    background-color: Gold;
}
.badge2-alternate {
    background-color: DarkGray;
    border-color: DarkGray;
}
.badge2-alternate .badge2 {
    background-color: Silver;
}
.badge3-alternate {
    background-color: #cd7f32;
    border-color: #cd7f32;
}
.badges-card .progress-bar.badge-1 .bar, .all-badge-progress .progress-bar.badge-1 .bar {
    border-color: #DAA520;
}
.badges-card .progress-bar.badge-2 .bar, .all-badge-progress .progress-bar.badge-2 .bar {
    border-color: Silver;
}
.badges-card .progress-bar.badge-3 .bar, .all-badge-progress .progress-bar.badge-3 .bar {
    border-color: #cd7f32;
}
.popup-badges .all-badge-progress .badge-progress {
    border-color: #3E3E3E;
}
.card.impact-card hr {
    background-color: #3E3E3E;
}
.user-page .card.impact-card hr, .user-show-new .card.impact-card hr {
    background-color: #3E3E3E;
}

.js-search-text {
    background-color: #AAAAAA;
    border-color: #AAAAAA;
}

.val-info {
    background-color: #1E1E1E !important;
    border-color: #1E1E1E !important;
}
.inner-container,
.user-page .settings-page h1 {
    border-color: #1E1E1E !important;
}

.user-flag-history #flag-stat-info-table tr.group-name,
.user-flag-history .flagged-post {
    border-color: #1E1E1E !important;
}

#side-menu ul li > a:hover {
    background-color: #1E1E1E;
}

/*** Rep ***/
.user-show-new .user-rep-full .rep-recent-row {
    background-color: #000052;
}
 .highcharts-container rect[fill="#FFFFFF"], rect[fill="rgb(255, 255, 255)"] {
  fill: #1E1E1E !important;
 }
.user-show-new .user-rep-full .rep-table-row > td {
    border-color: #1E1E1E !important;
}
.user-show-new.meta .rep-card .rep-col {
    background-color: #3E3E3E;
}
.privileges-wrapper .subtitle .stroke, .next-tag-badge-col .subtitle .stroke {
    background-color: #3E3E3E;
}
.privileges-wrapper .bars-wrapper .progress-bar .bar, .next-tag-badge-col .bars-wrapper .progress-bar .bar {
     background-color: #3E3E3E;
}
.user-show-new .user-rep .rep-amount .rep-up {
    border: none !important;
    background-color: #1E1E1E !important;
    color: DarkGreen !important;
}
.user-show-new .user-panel .mini-counts, .user-show-new #user-tab-answers .answer-votes, .user-show-new .post-container .vote, .user-show-new .user-rep .rep-amount .rep-up, .user-show-new .user-rep .rep-amount .rep-down {
    color: DarkGreen !important;
    border: none !important;
    background-color: #1E1E1E !important;
}
.user-show-new .user-rep-full .rep-up {
    color: DarkGreen;
}
.user-show-new .user-rep-full #stats .accept-indicator-tab,
.user-show-new .user-panel .mini-counts.answered-accepted, .user-show-new #user-tab-answers .answer-votes.answered-accepted, .user-show-new .post-container .vote.answered-accepted, .user-show-new .user-rep .rep-amount .rep-up.answered-accepted, .user-show-new .user-rep .rep-amount .rep-down.answered-accepted, .user-show-new .user-panel .mini-counts.accepted, .user-show-new #user-tab-answers .answer-votes.accepted, .user-show-new .post-container .vote.accepted, .user-show-new .user-rep .rep-amount .rep-up.accepted, .user-show-new .user-rep .rep-amount .rep-down.accepted, .user-show-new .user-panel .mini-counts.special-rep, .user-show-new #user-tab-answers .answer-votes.special-rep, .user-show-new .post-container .vote.special-rep, .user-show-new .user-rep .rep-amount .rep-up.special-rep, .user-show-new .user-rep .rep-amount .rep-down.special-rep {
    border-color: #1E1E1E !important;
    background-color: DarkGreen !important;
    color: LightGreen !important;
}
.user-show-new .user-rep-chart-summary .user-rep-chart-summary-bar {
    background-color: DarkGreen !important;
}
.user-show-new .user-rep-chart-summary .user-rep-chart-summary-tooltip:hover {

    background-color: #1E1E1E;

}
.user-show-new .user-rep-chart-summary {
    border-bottom-color: #3E3E3E !important;
}
.user-show-new .user-panel .subheader {
    border-bottom-color: #1E1E1E;
}


/*** Badges ***/
.badges-page .general .badge-hierarchy {
    border-top-color: #1E1E1E;
}

.tag-cell {
    border-bottom-color: #1E1E1E;
}

.mod-flair {
    color: #ff6600;
}

.badge1-alternate .badgecount, .badge2-alternate .badgecount, .badge3-alternate .badgecount, .badge-how-to .badgecount {
    color: #1E1E1E;
}


/***=== Review ===***/

.dashboard-item {
    border-bottom-color: #1E1E1E;
}
.review-bar-container .review-bar .review-actions-container {
    color: #060606;
}
.review-bar-container .review-bar {
    border-color: #1E1E1E;
    background-color: #1E1E1E;
}
.reviewable-post .reviewable-post-stats .label-value {
    color: #AAAAAA;
}
div.meter {
    border: none;
    background-color: #3E3E3E;
}
div.meter div {
    background-color: DarkGreen !important;
}
#tag-menu,
#badge-progress-menu {
    background-color: #1E1E1E;
    border-color: #1E1E1E;
}
.newuser {
    background-color: #1E1E1E;
    border-color: #1E1E1E;
}
.popup {
    box-shadow: none; /*0px 2px 5px rgba(0, 0, 0, 0.3)*/
    background-color: #1E1E1E;
    border-color: #1E1E1E; /*3E3E3E*/
}
.stats-mainbar .task-stat-table {
    background-color: #1E1E1E !important;
}
.stats-mainbar .task-stat-table td {
    background-color: #3E3E3E !important;
    border-color: #1E1E1E;
}
.stats-mainbar .task-stat-table .review-stats-count {
    color: #AAAAAA;
}
.review-stats-count-current-user {
    background-color: #1E1E1E !important;
}
.user-info {
    color: #f2f2f2;
}
.review-stats-current-user {
    background-color: #1E1E1E;
}
.tools-rev h1 .lsep {
    color: #1E1E1E;
}
.review-stats-all-count {
    color: #AAAAAA;
}
div.task-stat-leaderboard {
    color: #AAAAAA;
}
.diff-choices a.selected, .diff-type-controls a.selected, .diff-choices a.selected:hover, .diff-type-controls a.selected:hover {
    border: none;
    background-color: #3E3E3E;
}
.diff-choices a, .diff-type-controls a {
    border: none;
    background-color: #1E1E1E;
}
.diff-choices a:hover, .diff-type-controls a:hover {
    border: none;
    background-color: #3E3E3E;
}

#revisions .markdown-diff .full-diff {
    color: #000;
}
  
/*span.diff-add {
    background: none;
}*/

.review-bar-container .review-bar .review-instructions-reason {
    background-color: inherit;
}
.review-bar-container .review-bar .review-more-instructions {
    border-color: #3E3E3E;
}
  
.close-as-duplicate-pane .original-display .list-originals .list .item .body-summary,
.close-as-duplicate-pane .original-display .navi-container,
.action-list .action-desc {
    color: inherit;
}
.close-as-duplicate-pane .original-display .list-originals .list .item.hover {
    background-color: #060606;
}
.action-list .action-selected {
    background-color:  #000052;
}
  
.tag-suggestions > div:hover {
    background-color: #3E3E3E;
}
.tag-suggestions {
    background-color: #1E1E1E;
}

.mainbar.reviewable-answer {
    background-color: transparent !important;
}

.welovestackoverflow {
    border-color: #1E1E1E;
    background-color: #1E1E1E;
}

/***=== Favorites ===***/

.user-show-new .highlight-row {
    background-color: #000052;
}


/***=== Answers ===***/

.user-show-new .answer-summary {
    border-color: #1E1E1E;
}

.user-show-new .history-table > tbody > tr {
    border-color: #1E1E1E;
}



/***=== Chat ===***/
#loading {
    background-color: #000052 !important;
}
#loading #loading-message {
    background-color: #060606;
}

.mspbar {
    background-color: #000052 !important;
}
#input-area {
    background-image: none;
    background-color: #060606 !important;
}
#input {
    background-color: #1E1E1E !important;
    color: #fff;
    border-color: #1E1E1E;
}
#input-table td {
    color: #1E1E1E;
}
.messages {
    background-color: #1E1E1E !important;
    color: #BBB !important;
    border-radius: 0px;
}
.message {
    background-color: #1E1E1E;
}
.mine .messages {
    background-color: #000052 !important;
}
.monologue .timestamp {
    background-color: #3E3E3E;
    color: #AAAAAA;
    border-radius: 0px 0px 0px 0px;
}
.ob-wikipedia, .ob-amazon, .ob-gist, .ob-message, .ob-lpadbug, .ob-manpage, .ob-blog, .ob-exception, .ob-anime,
.ob-post {
    background-color: #060606;
    color: #AAAAAA;
}

.username.owner {
    font-style: normal;
    background-color: #000 !important;
}
a.signature {
    color: #AAAAAA !important;
}
.username.moderator {
    color: #ff6600;
}

.reply-parent,
.reply-child {
    background-color: #3E3E3E !important;
    /*background-color: #193319 !important;*/
}
.vote-count-container.stars .img {
    background-position: 0px -110px;
}
#sidebar {
    color: #AAAAAA !important;
}
#sidebar a {
    color: #2A4A7C !important;
}
#sidebar #info #roomtitle {
    text-shadow: none;
}
.more, .usercard h3 {
    color: #2A4A7C;
}
#sidebar-menu,
#present-users, ul#my-rooms,
div#starred-posts > div > ul > li {
    border-color: #1E1E1E !important;
    color: #3E3E3E !important;
}
div#starred-posts > div > ul > li {
    color: #AAAAAA !important;
}

#chat-body #searchbox, #transcript-body #searchbox {
    background-color: #1E1E1E !important;
    border-color: #060606 !important;
}

.popup .btn-close, 
#conversation-sel .btn-close {
    background-color: #000000;
}
.popup, 
#main.message-admin-mode .message-controls, 
#conversation-sel {
    background-color: #1E1E1E !important;
    color: #AAAAAA !important;
}
.popup-close a {
   background-color: #3E3E3E;
}

a#allrooms.button,
a#siterooms.button {
    color: #AAAAAA !important;
}
div.message .meta {
    background-color: #000;
}
.monologue.mine .message:hover .action-link,
.highlight,
.mention {
    background-color: #000052 !important;
}

div.message pre, div.message code {
    color: #FFF !important;
    font-weight: bold !important;
    /*background-color: #1E1E1E;*/
}

#herobox #hero-content,
#herobox {
    background-color: #1E1E1E;
    border-color: #1E1E1E;
}
#herobox-mini,
#herobox-mini #hero-content {
    color: #AAAAAA;
    background-color: #1E1E1E;
    background-image: none;
    border-color: #1E1E1E;
}

.monologue.catchup-marker {
    border-top-width: 1px;
    border-top-style: dashed;
    border-top-color: #AAA;
}

.wmd-prompt-dialog {
    background-color: #1E1E1E;
    border: none;
}
.wmd-mini-button.selected:hover, .wmd-mini-button.selected {
    background-color: #3E3E3E;
}

/***=== Priviliges ===***/

.privileges-page .privilege-table-row:hover {
    background-color: #1E1E1E;
}


.val-error {
    background: inherit;
}


/*+++++ remove fading effect on answers +++++*/
.answer {
    background-color: transparent !important;
}


/***=== Deleted Answer ===***/

.deleted-answer {
    background-color: #2E1E1E !important;
}



