css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("vk.com"){
/*********Мини-чат - Начало********************************/
/*топик*/
.fc_tab_head {
    background-color: #4c9d4f;
    border: 1px solid #2d7e30;
}
.fc_tab_title {
    padding: 0 3px;
    font-size: 12px;
    font-weight: 400;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    color: #c5fbc6;
}

.fc_tab_notify {
    background-color: #d0edd2;
    border-bottom: 1px solid #4c9d4f;
}

/*серединка - со списком*/
#fc_contacts, .fc_content {
    background: #d0edd2;
}

.fc_contact_name {
    font-weight: 500;
    color: #006e06;
}

.fc_contact.fc_contact_over {
    background: #4c9d4f;
}

#fc_ctabs_cont {
    border: 1px solid #4c9d4f;
}

/*серединка - с конкретным человеком общение*/
.fc_tab_log_wrap {
    overflow: hidden;
    background: url(http://abali.ru/wp-content/uploads/2013/03/jeans_1280x1024-600x480.jpg);
}

.fc_tab {
    border: 1px solid #2d7e30; 
}

.fc_msgs_date {
    color: #fff;
    font-size: 12px;
    padding: 4px 0 6px;
    opacity: 1.0;
    
    text-align: center;
}

/*собеседников не найдено*/
.fc_clist_empty, .fc_log_empty {
    color: #83c785;
}

/*диалоги - от собеседника*/
.fc_msgs {
    background-color: #c1ffc3;
    border: 1px solid #4c9d4f;
}   

/*диалоги - от вас*/
.fc_msgs_out.fc_msgs {
    background-color: #f5efe7;
    border: 1px solid #4c9d4f;
}

/*новое сообщение которое непрочтено от вас*/
.fc_msgs_unread {
    background: url(http://s020.radikal.ru/i723/1608/6b/b605918dae64.png);
}

/*собеседник печатает вам сообщение*/
.fc_tab_typing {
    color: #d7fdd8;   
}

/*иконка -скрепки*/
.fc_tab_attach .media_selector .ms_item_more {
    background: url(http://vk.com/images/icons/chats.png?14) no-repeat 0 -55px;
    padding: 0 6px;
    -webkit-filter: hue-rotate(268deg) brightness(0.8) saturate(3.0)!important;
}

/*низ - список*/
div.fc_clist_filter_wrap {
    border-top: 1px solid #78ce7d;
    background: #4c9d4f;
    border-radius: 0 0 0 0;
}

div.fc_tab_txt {
    background: #83c186;
    padding: 6px 3px 0 27px;
    border-top: 1px solid #2d7e30;
}

/*низ поле ввода сообщения*/ 
div.fc_editable.dark, input#fc_clist_filter  {
    background: #baf2be;
}

/*низ кружочек онлайна*/
.fc_clist_online {

    -webkit-filter: hue-rotate(338deg)  brightness(0.5) saturate(9.0)!important;
}

/*низ смайл*/
.emoji_smile_icon {
    background: url(http://vk.com/images/icons/smile_icon.png) no-repeat 4px 4px;
    -webkit-filter: hue-rotate(268deg) brightness(0.8) saturate(3.0)!important;
}

/*иконки*/

.chats_sp.fc_clist_search_icon
 {
    background: url(http://vk.com/images/icons/chats.png?14) no-repeat;
    -webkit-filter: hue-rotate(268deg) brightness(1.5) saturate(3.0)!important;
}

.media_selector .ms_item:before {
    background: url(http://vk.com/images/post_icon.png?4) no-repeat;
    opacity: 0.7;
    filter: alpha(opacity=70);
    -webkit-filter: hue-rotate(258deg) brightness(1.0) saturate(3.0)!important;
}

.fc_contact_status{
    -webkit-filter: hue-rotate(268deg) brightness(1.5) saturate(3.0)!important;
}

.fc_msg_att_icon_doc, .fc_msg_att_icon_mail, .fc_msg_att_icon_wall {
    background: url(http://vk.com/images/icons/chats.png?14) no-repeat;
    -webkit-filter: hue-rotate(268deg) brightness(0.8) saturate(3.0)!important;
}

/*********Мини-чат - Конец********************************/


/*всплывающий диалог при нажатии на скрепку - начало*/
.ms_items_more {background: #a3ffa6; border-color: #78ce7d;}
.ms_item {color: #2d7e30;}
.ms_item:hover {background: #baf2be !important;} 
 /*треугольник*/
.ms_items_more_wrap.to_up .ms_items_more:before {border-top-color: #78ce7d;} /*вниз*/
.ms_items_more_wrap.to_up .ms_items_more:after {border-top-color: #a3ffa6;}
.ms_items_more_wrap.to_down .ms_items_more:before {border-bottom-color: #78ce7d;} /*вверх*/
.ms_items_more_wrap.to_down .ms_items_more:after {border-bottom-color: #a3ffa6;}
/*всплывающий диалог при нажатии на скрепку - конец*/

.fc_tab_pointer:before { border-left-color: #2d7e30; }
.fc_tab_pointer.fc_tab_pointer_peer:after { border-left-color: #acf7b1; }

/*скроллбар*/
.scrollbar_inner {background: #54ab57;}
.scrollbar_inner:hover {background: #2d7e30;}

.scrollbar_cont {
    width: 8px;
    background-color: hsl(124, 82%, 82%);
}

input.dark::-webkit-input-placeholder{ 
color: #4d9f5a !important; 
}

div.fc_editable~.placeholder .ph_content{ 
color: #4d9f5a !important; 
}

/*чат кто онлайн цифра внизу*/
.chat_onl {
    color: #7993ad;
    -webkit-filter: hue-rotate(268deg)  brightness(0.5) saturate(9.0)!important;
}

/*треугольник при нажатии на цифру кто онлайн*/
.fc_tab_pointer::before {border-left-color: #2d7e30;}
.fc_tab_pointer::after {border-left-color: #acf7b1;}

/*кружок онлайн возле фото*/
.nim-dialog--unread, .nim-dialog.nim-dialog_unread.nim-dialog_unread-out:not(.nim-dialog_failed) .nim-dialog--unread {
    background: rgb(98, 178, 69);
}

/*при наведении на фото крестик*/
.chat_tab_close {
    background-color: rgba(45,126,48,.65);
}

/*фон правого меню от мини чата с авиками*/
.chat_tab_wrap {
    background-color: #baf2be;
    text-decoration: none;
}

.chat_tab_wrap:hover {
    background-color: #a3ffa6;
    text-decoration: none;
}

/* убираем рекламу*/
.ads_ads_box  {display: none;}    
}
