css:
@-moz-document url-prefix("http://hyunsdojo.com/user.php?u=") {
/* Nicer Profile */
@media (min-width: 960px) {
  /* PROFILE HEADER */
  
  #user_background_wrapper { background: #333 !important; }
	#online { display: none !important; }
  #profile_top_footer { height: 46px; }
  #profile_avatar { height: 0px !important; width: 0px; border-radius: 0px; box-shadow: none; background: transparent !important; }
  .top_foot_btn:before, .top_foot_btn:last-child:after, .top_stats:before, .top_stats:last-child:after { display: none; }
  #profile_avatar div { display: none; }
  #profile_avatar img {
    border-radius: 4px !important;
    height: 150px; width: 150px; margin: -45px 0px 0px -53px;
    filter: drop-shadow(0px 1px 1px rgba(0, 0, 0, 0.2)); -webkit-filter: drop-shadow(0px 1px 1px rgba(0, 0, 0, 0.2)); background: transparent !important;
  }
  #top_foot_bottom_right { bottom: 58px; }
  #top_foot_bottom_right > .button {
    text-shadow: none !important; border: none; box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.2) !important; margin-left: 5px !important;
  }
  #profile_name {
    color: white !important;
    font-weight: 600;
    margin-top: -80px; margin-left: -50px;
    -webkit-filter: drop-shadow(0px 2px 1px rgba(0, 0, 0, 0.7)); filter: drop-shadow(0px 2px 1px rgba(0, 0, 0, 0.7));
    line-height: 1.5em !important;
    text-shadow: 0 0px 4px rgba(0, 0, 0, 0.7) !important;
  }
  #profile_name > span {
    color: white !important;
    font-weight: 500; 
    line-height: 5px !important;
    text-shadow: 0 0px 4px rgba(0, 0, 0, 0.7) !important;
  }
  .top_stats { padding: 7px 10px !important; }
  #top_foot_btns { bottom: 1px !Important; }
   #user_background:before {
    content: "";
    width: 960px !important; height: 150px;
    display: block; position: relative; bottom: -150px;
    background: rgba(0, 0, 0, 0);
    background: -webkit-linear-gradient(top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.3));
    background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.3));
  } 
}
}
