css:
@-moz-document url-prefix("https://anilist.co") {
/* some code by Neku, check him his CSS code out on userstyles */
/* latin-ext */
@font-face {
  font-family: 'Karla';
  font-style: normal;
  font-weight: 400;
  src: local('Karla'), local('Karla-Regular'), url(https://fonts.gstatic.com/s/karla/v5/31P4mP32i98D9CEnGyeX9Q.woff2) format('woff2');
  unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Karla';
  font-style: normal;
  font-weight: 400;
  src: local('Karla'), local('Karla-Regular'), url(https://fonts.gstatic.com/s/karla/v5/Zi_e6rBgGqv33BWF8WTq8g.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}


html body {
	font-family: "Karla";
}
.serieslist-nav {
	height: 60px;
	background: rgba(0,0,0,0.9);
}
.search form input {
	font-family: "Karla";
	height: 60px;
}

.base-serieslist h3 {
    color: #00a8ff;
    font-size: 4em;
    font-weight: 300;
    margin: 0;
    margin-left: 10px;
    padding: 0;
    text-transform: capitalize;
}

th, td {
   	font-size: 1.2em;
}

.serieslist-nav .user {
	padding-top: 21px;
	font-size: 16px;
	font-weight: 500;
}
.serieslist-nav .theme-editor-btn {
	padding-top: 21px;
	font-size: 16px;
	font-weight: 500;
}
.serieslist-nav input {
	font-family: "Karla", sans-serif;
	font-size: 16px;
	font-weight: 500;
}
.theme-editor input,
.theme-editor select,
.theme-editor textarea {
	font-family: "Karla";
}
.nav__logo img {
    height: 0px;
    width: 0px;
}
.nav__logo {
    background-color: #121923;
    display: block;
    height: 0px;
    margin-top: 30px;
    padding-top: 0px;
    width: 60px;
}
.nav {
	background: #fff;
	width: 60px;
	border-right: 1px solid #eee;
}
.nav__links div {
	color: rgba(0, 0, 0, 0.4);
	font-size: .70em;
}
.nav__links a {
	padding-top: 18px;
	color: #000;
}
.discord-link img:nth-of-type(2) {
    left: 17px;
    opacity: 0;
    position: absolute;
	color: #000;
}
.nav__links div {
	color: transparent;
}
.nav__links a:hover {
	padding-top: 12px;
	background: transparent;
	color: #000;
}
.nav__links a:hover div {
	color: rgba(0, 0, 0, 1);
}
.nav__notifications {
	color: #000;
	background-color: rgba(18, 25, 35, 0.03);
}
-moz-selection {
	background: #999;
	color: #777;
}
.notifications {
	background: #000;
}
.noti {
	color: rgba(255, 255, 255, 0.9);
}
.noti:nth-of-type(even) {
	background-color: #111;
}
.noti__link b {
	color: #fff;
}
.nav__sml {
    background-color: rgba(18,25,35,0.03);
    float: left;
    height: 30px;
    padding-top: 7px;
    width: 30px;
}
.nav__sml i {
    font-size: 16px;
	color: #000;
}
.home.activity__type-btns {
	background: #fff;
	margin-bottom: 20px;
	padding: 9px;
	border-bottom: 1px solid #eee;
}
.home.activity__type-btns .active {
	color: #28a1d0;
}
.home.activity__type-btns a {
	font-size: 16px;
	font-weight: 500;
}
.activity__type-btns a {
	color: 000;
}
.home.activity__type-btns>div {
	border-left: solid 1px #eee;
}
body {
	background: #fff;
}
.small-header {
	color: #28a1d0;
	font-size: 18px;
	font-weight: 500;
}
.feed__row {
	background: #eee;
}
.quicklist__thumb {
	border-radius: 2px;
	width: 104px;
	height: 160px
}
.home__new section {
	height: 350px;
}
.quicklist__title {
	font-size: 14px;
}
.quicklist_plus {}
.behind-airing {
	border-bottom: solid 4px rgba(239, 48, 81, 0.82);
}
.row--sticky-false:nth-of-type(even) {
	background-color: #eee;
}
.feed__row a {
	color: #000;
	font-size: 12px;
}
.SandboxRoot.env-bp-430 .timeline-Tweet-text {
	color: #000;
}
.SandboxRoot.env-bp-330 .TweetAuthor-name,
.SandboxRoot.env-bp-430 .TweetAuthor-name,
.SandboxRoot.env-bp-550 .TweetAuthor-name {
	color: #000;
}
.recents {
	background: #111;
}
.activity__img--list>div {
    height: 79px;
}
.activity__img>div {
    width: 58px;
    border-radius: 0px;
}
.activity__img {
    border: 1px solid #eee;
    margin-right: 0px;
}
.activity__users a div {
    outline: 1px solid #eee;
    border-radius: 0px;
    height: 50px;
    width: 50px;
}
.activity__wrap {
    margin-bottom: -22px;
}
.activity__inner-wrap {
    border-top: 1px solid #eee;
    border-right: 1px solid #eee;
    background: #fff;
    border-radius: 0px;
}
.activity__header {
    background-color: #fff;
    border-bottom: 1px solid #eee;
	font-size: 13px;
}
.activity__nav {
    background-color: #fff;
    color: #000;
    border-radius: 0px;
    outline: 1px solid #eee;
    padding: 19.5px 7px 19.5px 3px;
}
.activity__header a {
    color: #000;
}
.activity__users a {
    margin-right: 0px;
    margin-top: 0px;
}
.activity__users {
    padding: 0px;
    background-color: #fff;
}
.activity {
    color: #28a1d0;
}
.activity__header time-ago {
    color: #28a1d0;
    font-size: 11px;
}
a:hover {
	color: #65c5ff;
}
review-card .summary {
	color: #fff;
	background-color: #888;
	font-size: 14px;
	box-shadow: 0 0 12px rgba(0, 0, 0, .3);
}
review-card .rating {
	background: #555;
	color: #ddd;
	font-size: 10px;
}
review-card .avatar span,
review-card .cover span {
	box-shadow: 0 0 12px rgba(0, 0, 0, .3);
}
.search form select {
	height: 60px;
	padding: 20px;
	border-bottom: 1px solid #eee;
}
.search form input {
	color: #111;
	border-bottom: 1px solid #eee;
}
p,
input,
textarea {
	font-family: "Karla";
	color: #000;
}
.thread__header .header__wrap {}
.thread__header {
	background-color: #fff;
}
.thread__body {
	background: #fff;
	color: #000;
	font-family: "Karla";
}
.thread__body markdown,
.thread__body markdown p {
	font-family: "Karla";
}
.thread__user span {
	color: #65c5ff;
	font-family: "Karla";
	font-size: 13px;
	text-align: left;
}
.tag {
	color: #65c5ff;
	background: #fff;
	border: solid 1px #65c5ff;
	border-radius: 2px;
}
.thread__reply {
	color: #65c5ff;
}
markdown h1,
markdown h2,
markdown h3,
markdown h4,
markdown h5,
markdown h6 {
	color: #000;
	font-family: "Karla";
}
markdown a {
	color: #65c5ff;
	font-family: "Karla";
}
.forum__feed--recent .row--sticky-true {
	background: #fff;
	border: 1px solid #eee;
	border-radius: 2px;
}
.feed__row .feed__ico {
	color: #65c5ff;
}
.feed__row {
	color: #aaa;
}
.forum__nav {
	color: #000;
	height: 60px;
	padding: 5px;
	font-size: 16px;
	background: #fff;
	border-bottom: 1px solid #eee;
}
.forum__nav a.active {
	color: #28a1d0;
}
.forum__nav .new-btn {
	color: #fff;
	top: 5px;
}
.thread__title {
	height: 60px;
	padding: 20px;
	color: #fff;
	background-color: #111;
	font-size: 16px;
}
.thread__title time-ago {
	color: #fff;
	right: 20px;
	padding: 2px;
}
.comment markdown {
	font-family: "Karla";
	font-size: 14px;
}
.comment markdown p {
	font-family: "Karla";
}

.browse-options {
    background-color: rgba(255,255,255,0.97);
    border-radius: 0 0 0 2px;
    color: #000;
    font-size: .9em;
    padding: 8px;
    padding-top: 5px;
    position: fixed;
    right: 0;
    top: 0;
}

.browse__nav{
	border-right: 1px solid #eee;
}


.browse__nav h1 {
    height: 60px;
    padding: 20px;
	border-bottom: 1px solid #eee;
	background-color: #fff;
}

.browse__nav div{
	padding: 10px;
	border-bottom: 1px solid #eee;
	background-color: #fff;
}

.browse__nav div:nth-of-type(even) {
    background-color: #fff;
}

.browse__genres ul li:nth-of-type(odd) {
    background-color: #fff;
}

.browse__dropdown, .browse__genres ul {
    background-color: #fff;
    border-bottom: solid 1px #eee;
    color: #466ea9;
    list-style: none;
    margin: 0;
    padding: 0;
	font-size: 15px;
}

.browse__genres>div span:first-of-type {
    border-right: solid 1px #eee; 
}

.search--browse select, .search--browse input {
    background: #fff;
}

.user__nav {
    background: #fff;
    padding: 9px 100px;
    font-size: 1em;
	border-bottom: solid 1px #eee;
}

.user__content h2, .user__sidebar h2 {
    color: #303338;
    font-size: 1em;
    font-weight: 400;
    margin: 0;
    padding: 9px 15px;
}
}
