css:
@-moz-document domain("www.imzy.com") {
body,
    .header,
    .feed__new-post,
    .voting.is-default,
    .feed-item__main,
    .feed-post__content__body,
    .feed-item.is-post .feed-item__main__comment-create-wrapper,
    .post__main,
    .post__content__preview__body,
    .comment__content,
    .post__content__body {
      background: #13182C;
      color: lightgrey;
    }

	.account-message, .account-profiles-message, .comment-create.is-no-identity-reason, .comments-feed__no-identity-reason, .community-create__content__block-message, .community-create__content__similar-communities, .community-edit__content__block-message, .help-desk__report__response__footer__note, .post-create__content__section__no-identity-reason, .post-create__content__section__posting-rules, .post-edit__content__community-rules, .profile-edit__note, .profile-view__activity__anonymous-note, .account-section__payment__list {
    color: #777;
    }

	.feed-post-overlay__bg {
    background: rgba(76, 77, 98, 0.952941);
    }

	.page-notifications__list-item {
    background: #4c4d62;
    color: #ccc;
    box-shadow: 0 0 0 0;
	}
    
    .side-nav__list-item:hover {
    background-color: #4c4d62;
    }
    
    .post-chat__header, .post-chat__content {
    background: #13182C;
    }
    
    .post-chat__messages-list-item__content__body {
    color: #ccc;
    border: 1px solid #4c4d62;
    background: #23283c;
    }
    
    .post-chat__messages-list-item.is-owner .post-chat__messages-list-item__content__body {
    background: #4c4d62;
    border: 1px solid #23283c;
    }
    
    .input__field {
    color: #ddd;
    }
    
    .nav.is-tabs .nav__item.is-active {
    background: #5c5d72;
    color: #7c7d92;
    }
    
    .nav.is-tabs .nav__item {
    background: #4c4d62;
    color: #536cb4;
    }
    
    .community-edit__content__nav.is-tabs .nav__item__title {
    color: #738cd4;
    }

    .account-profiles-list-item__is-default, .account-profiles-list-item__make-default {
    background: #13182c;
    border-top: 1px solid #5c5d72;
    }
    
    .page-messages-chat, .page-messages__no-results, .messages-chat__header, .messages-chat__content {
    background: #5c5d72;
    }
    
    .account-requests__list__item {
    background: #6c6d82;
    }
    
    .account-invite__list__item__detail, .account-invite__email__cta__text, .community-invite__contest__info__heading, .account-invite__list__header__text, .side-nav__heading, .community-invite__list__header__text, .community-invite__list__item__detail {
    color: #536cb4;
    }
    
    .account-profiles-list-item, .page-communities__list-item, .profile-view__info, .page-messages-groups, .profile-view__activity__post-item, .profile-view__activity__post-item__main, .account-section, .account-payments-summary__item, .community-invite__email__invite__box, .community-invite__contest, .community-invite__list {
    background: #4c4d62;
    }
    
    .post-create, .post-create__content, .post-create__footer, .comments-feed__content, .post__info, .community-edit__content__block {
    background: #3c3d52;
    }
    
    .editor-toolbar {
    background: #6c6d82;
    }
    
    .messages-chat__footer{
    color: #5c5d72;
    }

	.post-participation-label {
	background: #4c4d62;
	box-shadow: 0px 0px 5px 5px #4c4d62;
	color: #ccc;
	}

	.community-create__content__block{
	color: #444;
	}

    .community__side-info__community-banner, .community__side-info__profile {
    box-shadow: 0 2px 4px 0 hsla(0,0%,80%,.5);
    background: #13182c;
	
    }

	.comments-feed.is-post-overlay .comments-feed-wrapper, .post-content {
    background: #13182c;
    padding: 10px;
	}

    .header {
        box-shadow: none;
        border-bottom: 1px solid #4C4D62;
    }

    .header,
    .typeahead__field {
      border-bottom: 1px solid #4C4D62
    }

    .feed-item__main,
    .post__main {
      box-shadow: none;
      border: 1px solid #4C4D62;
    }

    .feed-pinned-items,
    .community .community__nav.is-tabs,
    .comments-feed__controls {
      border-bottom-color: #4c4d62
    }

    .side-nav__footer,
    .footer-content {
      border-top-color: #4c4d62
    }

    .header__status__nav {
      border-right-color: #4c4d62
    }

    .comments-feed__comments.is-top-level > .comments-feed__comment.is-with-children::after,
    .comments-feed__comments .comments-feed__comments.has-depth-1 > .comments-feed__comment.is-with-children::after {
      border-left-color: #4c4d62
    }

    .typeahead__field,
    .feed-item__main__type__icon {
      color: #4C4D62
    }

    .feed-item.is-post .feed-item__main__meta .feed-item__main__meta__link-community,
    .feed-post__content__heading__link,
    a.feed__new-post__item__link,
    .side-nav__list-item__link,
    .nav.is-tabs .nav__item__button,
    .nav.is-tabs .nav__item__link,
    .post__main__heading__link,
    span.comment__meta__text a.href {
      color: #748EA5
    }

    a.fi,
    .header__status__button-username__label,
    .side-nav__heading__link,
    .feed-item__main__meta__link-user,
    .side-nav__footer__link a.href,
    .post__main__meta .post__main__meta__link-user,
    .post__content__preview__heading,
    .select-default-wrapper.is-simple .select-default,
    .btn-icon-text,
    .btn-text {
      color: #51B6A4
    }

    .feed__new-post__item__link.is-plus {
      background: #748EA5
    }

    .comment-create__cta-input {
      background: lightgrey
    }

    li.is-community {
      display: none
    }

    a:hover,
    .post-actions__item:hover {
      color: #91E4D0 !important
    }

    .post__content__participation-label {
      background: #51B6A4;
      bottom: 0;
      box-shadow: none;
      color: #13182C;
    }

    .page-home-feed .page-home-feed__feed {
      max-width: 60%
    }

    .feed-post__content__img {
      max-width: 100px !important;
      max-height: 100px !important;
      float: left;
      margin-right: 20px;
      margin-bottom: 20px;
    }

    .feed-post__content__tag,
    .post__content__tag {
      background-color: #748EA5;
      color: #13182C;
    }

    .feed-post__content__ext-link {
      bottom: auto;
      display: inline-block;
      position: relative;
      left: auto;
      font-size: small;
      padding-left: 5px;
    }

    .feed-post__content__ext-link__text:first-child {
      display: none
    }

    .feed-item.is-post .feed-item__main__comment-create-wrapper {
      clear: both
    }

    .footer__logo__img {
      padding: 7px;
      background-color: rgb(76, 77, 98);
    }

    @media (min-width: 850px) {
      .header.is-logged-in .header__title {
        background-color: rgb(76, 77, 98);
        padding: 7px;
        top: 7px;
      }
      .md-editor .editor-preview code, .md-editor__field.is-preview code, .u-html code {
      background-color: #4c4d62;
      }
      
    }
a.fi, .header__status__button-username__label, .side-nav__heading__link, .feed-item__main__meta__link-user, .side-nav__footer__link a.href, .post__main__meta .post__main__meta__link-user, .post__content__preview__heading, .btn-text {
    color: #536cb4;
}

code {
background-color: #4c4d62;
}

.post__info__share-item__button {
color: #536cb4;
}
}
