css:
@-moz-document url(chrome://browser/content/browser.xul) {
  #ublock0-button label.toolbarbutton-badge {
    background: #242424 !important;
    border-radius: 20px !important;
    font-size: 9px !important;
    font-family: segoe ui;
    padding: 0px 3px !important; } }
  /* ↑ if you don't want the badge edit, delete ALL this up here ↑ */

@-moz-document url(chrome://ublock0/content/popup.html) {
  #panes h2 {
    color: white !important;
    font-size: 10px !important;
    font-weight: bold !important;
    text-transform: uppercase !important;
    background-color: #484848 !important;
  }
  #switch span.fa {
    font-size: 26px !important;
  }
  #switch .fa {
    color: #7db67c;
  }
  #gotoPrefs {
    position: absolute !important;
    bottom: 0px !important;
    font-size: 8px !important;
    text-transform: uppercase !important;
    width: 100% !important;
    background-color: white !important;
    margin-bottom: -5px !important;
  }
  #gotoPrefs #version {
    font-size: 8px !important;
  }
  #gotoPrefs span.fa {
    display: none !important;
  }
  body {
    padding-bottom: 0px;
  }
  #refresh {
    background-color: #cf4747 !important;
    border: 0px !important;
    border-radius: 0px !important;
    color: #EAEAEA;
    display: none;
    font-size: 24px !important;
    left: 0px !important;
    right: 0px !important;
    line-height: 1.10 !important;
    padding: 8px 8px !important;
    position: absolute !important;
    top: 0px !important;
  }
  #refresh:hover {
    /*transform: rotate(30deg);*/
    transition-duration: 0.2s;
    color: #484848 !important;
  }
  #extraTools {
	padding: 2px 0 4px 0 !important;
  }
  #extraTools > span {
    font-size: 0px !important;
    margin: 0 8px !important;
    position: relative;
    width: 16px;
    height: 16px;
    opacity: 0.6;
    transition-duration: 0.2s;
  }
  #extraTools > span:hover {
    opacity: 1;
    transition-duration: 0.2s;
  }
  #no-popups {
    background: url('data:image/png;base64,');
  }
  #no-large-media {
    background: url('data:image/png;base64,');
  }
  #no-cosmetic-filtering {
    background: url('data:image/png;base64,');
  }
  #no-remote-fonts {
    background: url('data:image/png;base64,');
  }
  .statName .tool {
    font-size: 12px !important;
  }
  #tooltip {
    background-color: #fafafa;
    border: 0px !important;
    border-radius: 2px;
    box-shadow: 1px 1px 4px gray;
    color: #484848;
    font: 11px sans-serif;
  }
}
