css:
@-moz-document domain("jw.xujc.com") {
body {
    background-color: #ECEFF1;
    font-family: "PingFang SC", Roboto;
    background-image: none;
}
/* Change the white to any color ;) */

input:-webkit-autofill {
    -webkit-box-shadow: 0 0 0px 1000px white inset;
}

#header #logo {
    display: none;
}

#header #menuinf {
    background-color: #607D8B;
    background-image: none;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.14), 0 4px 8px rgba(0, 0, 0, 0.28);
    width: 100%;
    margin-left: 0;
    background-size: 44px 44px;
    border-bottom: 1px solid rgba(0, 0, 0, 0.22);
    padding: 0px 20px 0px 20px;
    height: 44px;
}

#nav,
#nav ul {
    margin-top: -24px;
}

#header #menuinf #inf {
    float: right;
    margin-right: 50px;
    margin-top: 11px;
    padding: 0;
}

#nav a {
    color: #fff;
}

#content {
    box-shadow: 0 0 6px rgba(0, 0, 0, .16), 0 6px 12px rgba(0, 0, 0, .32);
    background: #fff;
    width: 80%;
    margin: 0 auto;
    margin-top: 30px;
    margin-bottom: 30px;
}

.even,
.odd {
    background: none;
}

table.solid {
    padding: 0;
    margin: 0;
    border: 0;
}

table.data td {
    padding: 10px;
    border-bottom: 1px solid #e5e5e5;
}

table.solid td {
    border: 1px solid #e5e5e5;
}

#footer {
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.14), 0 4px 8px rgba(0, 0, 0, 0.28);
    border-top: 1px solid rgba(0, 0, 0, 0.22);
    background-color: #fff;
    text-align: center;
    color: #999;
    height: 80px;
    margin: 0;
    padding-top: 40px;
}

#main2 h3 {
    display: none;
}

.red {
    color: #ccc !important;
}

table.data th {
    background-color: #f5f5f5;
    color: #000;
    text-decoration: none;
    border: none;
    border-bottom: 1px solid #e5e5e5;
}

#main2 a {
    color: #778087;
    text-decoration: none;
    word-break: break-all;
}

#main2 a:hover {
    color: #4d5256;
    text-decoration: underline;
}

#nav ul {
    background: #fff;
    border: 0;
    box-shadow: 0 0 6px rgba(0, 0, 0, .16), 0 6px 12px rgba(0, 0, 0, .32);
    width: 145px !important;
}

#nav li li {
    padding: 0;
    width: 100%;
}

#nav li ul a {
    color: rgba(0, 0, 0, .8);
    height: 40px;
    line-height: 40px;
    padding: 0 16px;
    margin: 0;
    width: auto;
    w\idth: auto;
}

#nav li ul a:hover {
    background-color: #eee;
}

#nav li ul li:hover {
    text-decoration: none;
}

#nav li:hover,
#nav li.sfhover {
    background: none;
    text-decoration: underline;
}

#page-nav table {
    width: 80%;
    margin-top: 30px;
    margin-bottom: -30px;
    background: #fff;
    box-shadow: 0 0 6px rgba(0, 0, 0, .16), 0 6px 12px rgba(0, 0, 0, .32);
    border-collapse: collapse;
}

#page-nav table td {
    border: 0;
    padding: 6px;
}

#page-nav table tr {
    height: 30px;
}

table.form th {
    padding: 5px;
    color: #5d5d5d;
    background-color: #f6f6f6;
}

table.form td {
    border: 1px solid #cecece;
}

table.form th {
    border: 1px solid #cecece;
}

table.form td img {
    opacity: .1;
    transition: .4s;
}

table.form td img:hover {
    opacity: .8;
    transition: .8s;
}

div.message {
    width: 80%;
    margin: 0 auto;
    color: #777;
}

form #wrapper #content {
    background: rgba(255, 255, 255, 0);
    width: 100%;
    box-shadow: none;
}

div#content #left {
    width: 230px;
    background-image: none;
    padding-top: 0;
    background-color: #fff;
    box-shadow: 0 0 6px rgba(0, 0, 0, .16), 0 6px 12px rgba(0, 0, 0, .32);
    margin-right: 20px;
}

div#content #main {
    width: 600px;
    box-shadow: 0 0 6px rgba(0, 0, 0, .16), 0 6px 12px rgba(0, 0, 0, .32);
    background: #fff;
    margin-right: 20px;
}

div#content #right {
    width: 120px;
    height: 120px;
    box-shadow: 0 0 6px rgba(0, 0, 0, .16), 0 6px 12px rgba(0, 0, 0, .32);
    background-image: none;
    background: #fff;
}

div#content #left #login {
    background-image: none;
}

div#content #right #xl p {
    padding: 0;
}

div#content #left #login #dr p {
    padding-left: 15px;
}

div#content #right #xl {
    background-image: none;
    background: #fff;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.12), 0 2px 4px rgba(0, 0, 0, 0.24);
    height: 120px;
    width: 120px;
}

div#header div#top {
    margin: 0;
    background-color: #607D8B;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.14), 0 4px 8px rgba(0, 0, 0, 0.28);
    width: 100%;
    background-image: none;
}

div#header div#top a,
div#header div#top p {
    color: #fff;
}

div#logo {
    /*background: url(https://qiniu.viosey.com/2016-08-29_1-1.png) no-repeat left top; */
    
    display: none;
    box-shadow: 0 0 4px rgba(0, 0, 0, 0.14), 0 4px 8px rgba(0, 0, 0, 0.28);
    border-bottom: 1px solid rgba(0, 0, 0, 0.22);
}

#main a:link {
    color: #607D8B;
}

#main a:visited {
    color: #90A4AE;
}

div#content #main #notice {
    margin-top: 72px;
    height: 60px;
    opacity: .3;
    transition: .5s;
    overflow: hidden;
}

div#content #main #notice:hover {
    margin-top: -100px;
    height: 232px;
    opacity: .9;
    transition: .5s;
    background-color: #fff;
}

div#content #left #login #dr p input {
    border: 0;
    border-bottom: 1px solid rgba(0, 0, 0, .12);
    background: 0 0;
    transition: .3s;
}

div#content #left #login #dr p input:focus {
    outline: none;
    border-bottom: 1px solid rgba(0, 0, 0, .42);
    transition: .3s;
}

div#content #left #login #dr p input.button {
    background: 0 0;
    border: none;
    border-radius: 2px;
    color: #000;
    position: relative;
    height: 36px;
    min-width: 64px;
    padding: 0 16px;
    display: inline-block;
    font-family: "Roboto", "Helvetica", "Arial", sans-serif;
    font-size: 14px;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0;
    overflow: hidden;
    will-change: box-shadow;
    transition: box-shadow .2s cubic-bezier(.4, 0, 1, 1), background-color .2s cubic-bezier(.4, 0, .2, 1), color .2s cubic-bezier(.4, 0, .2, 1);
    outline: none;
    cursor: pointer;
    text-decoration: none;
    text-align: center;
    line-height: 36px;
    vertical-align: middle;
}

div#content #left #login #dr p input.button:hover {
    background-color: rgba(158, 158, 158, .2);
}

#main ul {
    list-style-type: circle;
}

div#content #main #new {
    overflow: hidden;
    height: 437px;
}

div#content #main #new #n2 li {
    line-height: 30px;
    height: 30px;
}

#zkb a:link,
#zkb a:visited,
a.lfmenu:link,
a.lfmenu:visited {
    color: #607D8B;
}

a.lfmenu:hover {
    color: #546E7A !important;
}
}
