css:
@-moz-document url-prefix("http://www.loups-garous-en-ligne.com"){
@font-face {
  font-family: 'Montserrat';
  font-style: normal;
  font-weight: 400;
  src: local('Montserrat-Regular'), url(https://fonts.gstatic.com/s/montserrat/v7/zhcz-_WihjSQC0oHJ9TCYPk_vArhqVIZ0nv9q090hN8.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}


@font-face {
  font-family: 'Montserrat Alternates';
  font-style: normal;
  font-weight: 400;
  src: local('MontserratAlternates-Regular'), url(https://fonts.gstatic.com/s/montserratalternates/v4/z2n1Sjxk9souK3HCtdHukpy2IC1KoJIC5P3EDKbgI_s.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}

#menu {
    background-color: #ffffff!important;
    background-image: none;
    width: 100%;
    position: fixed;
    top: 0;
    left: 0;
    margin-top: 0;
    z-index: 3;
}
#menu a {
    color: #00dc91!important;
}
#menu ul li {
    border-right: 1px solid rgba(0, 0, 0, 0.08);
}
#menu > ul > a > li {
    background-color: transparent!important;
}
li#mini_news {
    background-color: rgb(53, 187, 138)!important;
    transition-duration: 400ms;
}
li#mini_news:hover {
    background-color: #4CAF50!important;
    transition-duration: 400ms;
}
#espace_reflexion {
	padding: 7px 8px 7px 7px;
    border: 1px solid #FFF;
    background: #e2001a;
    background: -moz-linear-gradient(top, #e2001a 0%, #f73c51 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #e2001a), color-stop(100%, #f73c51));
    background: -webkit-linear-gradient(top, #e2001a 0%, #f73c51 100%);
    background: -o-linear-gradient(top, #e2001a 0%, #f73c51 100%);
    background: -ms-linear-gradient(top, #e2001a 0%, #f73c51 100%);
    background: linear-gradient(to bottom, #e2001a 0%, #f73c51 100%);
}
#espace_detente {
    border: 1px solid #ffffff;
    background: -moz-linear-gradient(top, #3C71C1 0%, #58A1D8 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #35bb8a), color-stop(100%, #00e094));
    background: -webkit-linear-gradient(top, #35bb8a 0%, #00e094 100%);
    background: -o-linear-gradient(top, #3C71C1 0%, #58A1D8 100%);
    background: -ms-linear-gradient(top, #3C71C1 0%, #58A1D8 100%);
    background: linear-gradient(to bottom, #35bb8a 0%, #00e094 100%);
}
#parent_notifs_header {
    background-color: #e2001a!important;
}
.boite_salle_de_jeu_1 {
    background-color: #00dc91;
    color: #FFF!important;
    border: 1px solid #ffffff;
    transition-duration: 500ms;
}
.boite_salle_de_jeu_1 > tbody > tr > td {
    color: #FFF!important;
}
.boite_salle_de_jeu_1:hover {
    transition-duration: 500ms;
    background-color: #00b97a;
}
.boite_salle_de_jeu_3 {
    background-color: #018457;
    color: #FFF!important;
    border: 1px solid #ffffff;
    transition-duration: 500ms;
}
.boite_salle_de_jeu_3 > tbody > tr > td {
    color: #FFF!important;
}
.boite_salle_de_jeu_3:hover {
    background-color: #002f1f;
    transition-duration: 500ms;
}
.boite_salle_de_jeu_2 {
    border: 1px solid #ffffff;
    background-color: rgba(226, 0, 26, 0.7);
    color: #FFF!important;
    transition-duration: 500ms;
}
.boite_salle_de_jeu_2:hover {
    transition-duration: 500ms;
    background-color: #9a0012;
}
.boite_salle_de_jeu_2 > tbody > tr > td {
    color: #FFF!important;
}
.boite_salle_de_jeu_0 {
    border: 1px solid #ffffff;
    background-color: #a21919;
    color: #FFF!important;
}
.boite_salle_de_jeu_0:hover {
    transition-duration: 500ms;
    background-color: #560f0f;
}
.boite_salle_de_jeu_0 > tbody > tr > td {
    color: #FFF!important;
}
#matchBox {
    border: 1px #ffffff solid;
    background-color: #00dc91;
    color: #FFF!important;
}
#matchBox:hover {
    background-color: #35bb8a;
}
#createNewGame {
    border: 1px #ffffff solid;
    background-color: #00dc91;
    color: #FFF!important;
}
#createNewGame:hover {
    background-color: #35bb8a;
}
div#enCours {
    display: none;
}
a {
    color: #000;
    text-decoration: none;
    transition-duration: 600ms;
}
a:hover {
    color: #00dc91;
    transition-duration: 400ms;
}
#membre_info .player {
    color: #00dc91;
    text-transform: uppercase;
}
#membre_info .player:hover {
    text-decoration: none;
}
#membre_info {
    border: #35bb8a 1px solid;
    color: #00dc91;
    background-color: #FFF;
}
#membre_info #encartBonus {
    border: solid 1px #35bb8a;
}
input[type="submit"] {
    color: #35bb8a!important;
    background: transparent!important;
    border: none;
    font-family: Montserrat!important;
    font-size: 13px;
    padding: 3px 5px!important;
    width: auto!important;
}
input[type="submit"]:focus{
    outline: 0;
}
#chrono > input {
    color: #FFF!important;
    font-size: 15px!important;
}
#chrono > input:focus {
	outline: 0;
}
#chrono:hover{
    color: #FFF!important;
}
div#facebook_button_wrapper {
    background: none;
    border: 0;
    color: #FFF!important;
    font-family: Montserrat;
    font-size: 15px;
}
div#titre {
    font-family: Montserrat;
    font-weight: 300;
    font-size: 18px;
    letter-spacing: 3px;
}
#colonneDroite > #joueurs > div[align="center"] > img {
    width: 120px;
}
div#facebook_button_wrapper i {
    display: none;
}
div#facebook_button_wrapper:hover {
    background: none;
}
span#lien {
    font-size: 19px;
    font-family: Montserrat;
    font-weight: 300;
    text-decoration: none;
    transition-duration: 300ms;
}
span#lien:hover {
    color: #00dc91;
    transition-duration: 300ms;
}
#joueurs > b {
    color: #00dc91!important;
}
div#CONTENTchat {
    background-color: #FFF;
    color: #000;
    border: 2px solid;
}
.canal_1 {
    color: #008457;
}
div#historique {
    background-image: none;
    background-color: #FFF;
}
::-webkit-scrollbar {
    background-color: #fff;
    width: 5px;
}
::-webkit-scrollbar-thumb {
    background-color: #36bb8a;
}
#actions > form > input{
    color: #FFF;
    font-size: 20px;
}
body {
    background-color: #00dc91;
    background-image: url('http://zupimages.net/up/16/34/bhv3.png');
    background-position-x: 30px;
    background-position-y: 50px;
    background-repeat: no-repeat;
}
#maison1, #maison2, #maison3, #maison4, #maison6, #maison7, #maison8 {
    display: none!important;
}
#menu ul {
    margin: -1px auto;
}
#salle_de_jeu > table > tbody > tr > td > div > table {
    display: none;
}
#salle_de_jeu > table > tbody > tr > td > div > i:after {
    position: absolute;
    content: 'Partie normal';
    background: #560f0f;
    height: 13px;
    top: 200px;
    color: #FFF;
    left: 0;
    padding: 2px 5px;
    width: 80px;
}
#salle_de_jeu > table > tbody > tr > td > div > i:before {
    position: absolute;
    content: 'Partie spÃ©cial';
    background: #e54759;
    height: 13px;
    top: 225px;
    width: 80px;
    color: #FFF;
    left: 0;
    padding: 2px 5px;
}
#header > img {
    display: none;
}
#header {
    height: 50px!important;
}
a.imgInscription {
    display: none;
}
input[type="text"], input[type="password"] {
    border: 0!important;
    border-bottom: 1px solid #00dc91!important;
    margin-bottom: 5px;
    box-shadow: none!important;
    background: transparent!important;
    color: #FFF;
    border-radius: 0px!important;
}
input[type="text"]:focus, input[type="password"]:focus{
    outline: 0;
}
.titre{
    font-family: Montserrat;
    bottom: 5px;
    position: relative;
}
#body {
    background-image: none!important;
    width: 1000px;
    background-color: #FFF;
}
#main{
    background-color: #FFF;
}
.googleButton {
    display: none;
}
div#footer {
    color: #FFF!important;
}
div#createNewGame:after {
    position: absolute;
    content: 'Partie Fun';
    background: #00dc91;
    height: 13px;
    top: 250px;
    font-size: 10px;
    font-style: italic;
    color: #FFF;
    left: 0;
    text-align: left;
    padding: 2px 5px;
    width: 80px;
}
div#createNewGame:before {
    position: absolute;
    content: 'Partie carnage';
    background: #018457;
    height: 13px;
    top: 275px;
    font-size: 10px;
    font-style: italic;
    color: #FFF;
    left: 0;
    text-align: left;
    padding: 2px 5px;
    width: 80px;
}
#couplePopup {
    background-color: #008457!important;
    background: -webkit-gradient(linear,left top,left bottom,from(#00dc91),to(#008457));
    outline: 0;
    color: #FFF;
}
}
