  display: inline-block;
  margin: 0;
  padding: 8px;
  padding-left: 16px;
  padding-right: 16px;
  color: #fff;
}

div#menubar ul.menu li > ul {
  position: absolute;
  background-color: #212B39;
  padding: 0;
  display: none;
  opacity: 0;
  left: 0;
  top: 30px;
  -moz-transition: display .25s;
  -webkit-transition: display .25s;
  -o-transition: display .25s;
  -moz-transition: opacity .25s;
  -webkit-transition: opacity .25s;
  -o-transition: opacity .25s;
}

