@-moz-document domain("monchbox.wikia.com") {
/* minimal torus theme, currently in open beta */
/** font customization **/
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans'), local('OpenSans'), url(https://fonts.gstatic.com/s/opensans/v13/u-WUoqrET9fUeobQW7jkRRJtnKITppOI_IvcXXDNrsc.woff2) format('woff2');
  unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans'), local('OpenSans'), url(https://fonts.gstatic.com/s/opensans/v13/cJZKeOuBrn4kERxqtaUH3VtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
.skin-oasis #torus, .skin-oasis #torus #torus-window  {
    font-family: 'Open Sans', Tahoma;
}

/** interface customization **/
.skin-oasis #torus #torus-tabs, .skin-oasis #torus #torus-input {
    background: #f0f0f0;
}
.skin-oasis #torus #torus-info, .skin-oasis #torus #torus-popup {
    background: #fbfbfb;
}
.skin-oasis #torus #torus-sidebar {
    background: #f6f6f6;
    border-width: 1px 0 0 0;
}
.skin-oasis #torus, .skin-oasis #torus #torus-info, .skin-oasis #torus #torus-sidebar, .skin-oasis #torus #torus-window, .skin-oasis #torus #torus-input, .skin-oasis #torus .torus-message-seperator, .skin-oasis #torus #torus-popup, .skin-oasis #torus #torus-popup #torus-popup-avatar {
    border-color: #ccc;
}

.skin-oasis #torus #torus-tabs span#torus-tab--1 {
    background: url("http://slot1.images.wikia.nocookie.net/__cb1471285335/common/extensions/VisualEditor./../../resources/lib/oojs-ui/themes/apex/images/icons/advanced.svg") no-repeat;
    background-size: 22px;
    padding: 2px 4px 4px 18px;
    font-weight: bold;
	color: #444;
}
.skin-oasis #torus span#torus-tab--1 img {
    display: none;
}
.skin-oasis #torus #torus-tabs .torus-tab:not(#torus-tab--1) {
    background: transparent;
    border-width: 0 0 2px 0;
    border-radius: 0;

}
.skin-oasis #torus #torus-tabs span.torus-tab-active:not(#torus-tab--1) {
    border-bottom-color: #0f0f0f;
}
.skin-oasis #torus #torus-tabs span.torus-tab-ping {
    color: #e92d21;
    border-bottom-color: #e92d21;
    font-weight: bold;    
}

/** chat customization **/
.skin-oasis #torus #torus-window {
    background: #fff;
}
.skin-oasis #torus span.torus-message-separator {
    border: none;
    border-top: 1px #ccc solid;
}
.skin-oasis #torus #torus-window::-webkit-scrollbar-thumb,
.skin-oasis #torus #torus-sidebar::-webkit-scrollbar-thumb {
    height: 6px;
    border: 4px solid rgba(0, 0, 0, 0);
    background-clip: padding-box;
    border-radius: 7px;
    background-color: rgba(0, 0, 0, 0.15);
    box-shadow: inset -1px -1px 0px rgba(0, 0, 0, 0.05), inset 1px 1px 0px rgba(0, 0, 0, 0.05);
}
.skin-oasis #torus #torus-window::-webkit-scrollbar,
.skin-oasis #torus #torus-sidebar::-webkit-scrollbar {
    width: 14px;
    height: 18px;
}
.skin-oasis #torus #torus-window .torus-message { display:table; }
.skin-oasis #torus #torus-window span.torus-message-timestamp {
	padding-right: 0.2em;
}
.skin-oasis #torus #torus-window .torus-message span.torus-message-usercolor { 
        white-space: nowrap;
}
.skin-oasis #torus #torus-window .torus-message span.torus-message-text {
    display: table-cell;
    vertical-align: top;
	padding-left:0.4em;
}

/** user popup customization **/
.skin-oasis #torus #torus-popup {
    padding:0 0.5em 0.5em 0.5em;
}
.skin-oasis #torus #torus-popup #torus-popup-actions {
    margin-top:0.5em;
}
.skin-oasis #torus #torus-popup #torus-popup-userlinks a.torus-popup-userlink, .skin-oasis #torus #torus-popup #torus-popup-actions a.torus-popup-action {
	background:transparent;
    border:1px #eee solid;
    color:#0f0f0f;

}
.skin-oasis #torus #torus-popup #torus-popup-userlinks a.torus-popup-userlink, .skin-oasis #torus #torus-popup #torus-popup-actions a.torus-popup-action, .skin-oasis #torus #torus-popup #torus-popup-actions a.torus-popup-action-disabled {
	margin:1px;
    border-radius:2px;
}
.skin-oasis #torus #torus-popup #torus-popup-actions a.torus-popup-action-disabled { 
    background:#ddd;
    border:1px #ccc solid;
}
.skin-oasis #torus #torus-popup #torus-popup-userlinks a.torus-popup-userlink:hover, .skin-oasis #torus #torus-popup #torus-popup-actions a.torus-popup-action:hover {
    background-color:#f6f6f6;
    border-color:#aaa;
}

.skin-oasis #torus #torus-popup #torus-popup-userlinks a.torus-popup-userlink {
    width:calc(50% - 4px);
}
.skin-oasis #torus #torus-popup #torus-popup-actions a.torus-popup-action, .skin-oasis #torus #torus-popup #torus-popup-actions a.torus-popup-action-disabled {
    width:calc(100% - 4px);
}

/** options customization **/
.skin-oasis #torus #torus-menu-extensions a {
	background-color: #eee;
	color: #0f0f0f;
}
.skin-oasis #torus #torus-menu-extensions a:hover {
	background-color: #f0f0f0;
}
.skin-oasis #torus #torus-sidebar li.torus-sidebar-button {
    background: transparent !important;
}
.skin-oasis #torus #torus-sidebar li.torus-sidebar-button-selected, .skin-oasis #torus #torus-sidebar li.torus-sidebar-button-selected:hover {
	font-weight: bold;    
}
}
