css:
@-moz-document regexp("http://support\\.([^\\/]*)/staff/.*") {
/* The clean version */
/* Variables area */
:root {
    \--defaultBG: #000;
    \--defaultBGhighlight: #af6000;
    \--defaultFG: #f4f4f4;
    \--defaultBGodd: #111;
    \--link: #00A2E8;
    \--linkHover: darkorange;
    \--primaryBG: darkorange;
    \--primaryBGhover: #ff9b21;
    \--primaryBorder: #ffa63a;
    \--primaryFG: black;
    \--secondaryFG: darkorange;
    \--secondaryBorder: darkorange;
    \--secondaryBG: rgba(255,140,0,0.1);
    \--secondaryBGhover: rgba(255,140,0,0.3);
    \--tertiaryBorder: #008bc7;
    \--tertiaryBG: rgb(0, 101, 144);
    \--tertiaryBGhover: #af6000;
    \--tertiaryBorderHover: darkorange;
    \--tertiaryFG: white;
    \--border: #000;
    \--textShadow: rgba(0,0,0,0.4);
}

}

@-moz-document regexp("http://support\\.([^\\/]*)/staff/.*") {
/* The clean version */

/* Major areas - Areas that contain many smaller areas */
.cpuicontainer { background-color: var(--defaultBG) !important; }
html, body { background-color: var(--defaultBG); color: var(--defaultFG); }
td, pre { color: var(--defaultFG); }


/* Login area */
.loginformcontainer { background-color: var(--defaultBG); }
.loginformsub { background-color: var(--defaultBG); border-color: var(--defaultBG); }
.loginformsub .options { background-color: var(--secondaryBG); border:solid 1px var(--secondaryBorder); padding: 10px 15px; color: var(--secondaryFG); border-radius: 3px; margin-top: 15px; display:block; }
.loginformsub .options:hover { background-color: var(--secondaryBGhover); }
.loginoptions { background-color: var(--defaultBG); }
.loginoptions .gridrow1, .loginoptions .gridrow2 { background-color: var(--defaultBG) !important; }
.loginsubmit td { border-top: 0px none; }


/* Form items - Things used in many places */
select { background-color: var(--tertiaryBG); color: var(--tertiaryFG); border-color: var(--tertiaryBorder); }
textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
    background-color: var(--tertiaryBG); color: var(--tertiaryFG); border-color: var(--tertiaryBorder);
}
a:link, a:visited { color: var(--link); }
a:hover { color: var(--linkHover); }


/*** Ticket List items ***/
.gridlayoutborder { background-color: var(--defaultBG) !Important; border-color: var(--border); }
.gridcontentborder { border-color: var(--border) !important; }
.gridtabletitlerow { background-color: var(--tertiaryBG) !important; color: var(--tertiaryFG) !Important; border-bottom-color: var(--tertiaryBorder) !important; }
.gridtabletitlerowsel, .gridtabletitlerowhover { background-color: var(--tertiaryBGhover); color: var(--tertiaryFG); border-bottom-color: var(--tertiaryBorderHover); }
.rowselect { background: var(--defaultBGhighlight); color: var(--defaultFG); }
.rowselect a, .rowselect div { color: var(--defaultFG); }
.retborder { border: solid 1px var(--border) !important; background-color: var(--defaultBG) !important; }
.rowhighlight { background: var(--tertiaryBG) !important; color: var(--tertiaryFG) !important; border-top:1px solid var(--tertiaryBorder) !important; border-bottom:1px solid var(--tertiaryBorder) !important; }
.rowhighlight a { color: var(--tertiaryFG) !important; }
.gridrow1 { background-color: var(--defaultBGodd) !important; color: var(--defaultFG) !Important; }
.gridrow2 { background-color: var(--defaultBG) !important; color: var(--defaultFG) !Important; }
.gridrowitalic { background-color: var(--defaultBG); padding:150px; color: var(--secondaryFG); font-size: 40px; } /* Found this used for empty lists */
.gridrowitalic * { display:block; background-color: var(--secondaryBG); }
/*
.paginationtoolbar { border-top: 1px solid #333 !Important; background: #000 !important; color: #eef !important; border-bottom: solid 1px rgba(0,162,232,0.7) !important; }
*/



/*** Ticket post header ***/
.customfieldstaticcontent { padding: 10px 20px; }
.customfieldstaticcontent table, .customfieldstaticcontent table tbody, .customfieldstaticcontent table tr { display:block; }
.customfieldstaticcontent table td { display:block; float:left; }
.customfieldstaticcontent:after { content:''; display:block; clear:both; }
.customfieldcol1_pink { text-align:right; display:block; width: 224px !important; float:left; padding: 10px 25px 10px 10px; box-sizing: border-box; border-bottom: 1px solid var(--border) !important; color: var(--defaultFG); }
.customfieldcol2_pink { background: var(--tertiaryBG) !important; border: 1px solid var(--tertiaryBorder) !important; width: auto !important; float:left; border-radius: 3px; margin:3px; }
.customfieldstatic_pink { background-color: var(--defaultBG) !important; }
.customfieldstatictitle, .customfieldcol1_pink, .customfieldcol2_pink { color: var(--defaultFG) !important; }
.customfieldstatictitle { border-bottom-color: var(--border); }
#View_Tickettabs.ui-tabs .ui-tabs-nav { border-bottom-color: var(--tertiaryBorder) !important; }
.ticketlockinfocontainer, .ticketbillinginfocontainer, .ticketbillinginfocontainer2 { background-color: var(--defaultBG); color: var(--default); }
.ticketgeneralcontainer, .ticketreleasecontainer, .ticketreplycontainer, .ticketforwardcontainer, .ticketfucontainer { background: var(--defaultBG) !important; color: var(--default) !Important; }
p.ui-widget-content, li.ui-widget-content, div.ui-widget-content { background: var(--defaultBG); }


/*** Ticket post items ***/
/* Colouring */
.ticketpostcontents { background: var(--defaultBG) !important; color: var(--defaultFG) !important; border-left: 4px solid var(--border) !important; }
.ticketpostbarbottom { border-top: solid 1px var(--border) !important; }
.ticketpostcontentsdetailscontainer, .ticketpostcontentsdetailscontainer * { color: var(--defaultFG) !important; }
.ticketpostcontentsbar .ticketbarcontents, .ticketpostcontentsbargreen .ticketbarcontents, .ticketpostcontentsbargray .ticketbarcontents { color: var(--defaultFG) !important; }
.ticketpostcontainer, .ticketpostpaginationtoolbar {
  background: var(--defaultBG) !important;
  border-top: 1px solid var(--border) !important;
  border-left: 1px solid var(--border) !important;
  border-right: 1px solid var(--border) !important;
  border-bottom: 1px solid var(--border) !important;
}
.ticketpostbarname { color: var(--defaultFG);  }

.ticketpostcontentsbar, .ticketpostcontentsbargreen, .ticketpostcontentsbargray {
  background: var(--secondaryBG) !important;
  border: 1px solid var(--secondaryBorder) !important;
  text-shadow: 0 1px var(--textShadow) !important;
  border-radius: 3px;
}

.gridnavpageselected, .gridnavpage { background-color: var(--defaultBG) !important; color: var(--defaultFG) !important; }
.gridnavpage a:link { border: solid 1px var(--link); border-radius: 3px; transition: all 0.25s ease-in; }
.gridnavpage a:hover { border: solid 1px var(--linkHover); }
.gridnavpage a:link, .gridnavpage a:visited { color: var(--link) !important; }
.gridnavpage a:hover { color: var(--linkHover) !important; }
.gridnavpageselected a:link { background-color: var(--primaryBG); border: solid 1px var(--primaryBorder); border-radius: 3px; transition: all 0.25s ease-in; color: var(--primaryFG); }
.ticketpostcontainer a:link, .ticketpostcontainer a:visited { color: var(--link) !important; }
.ticketpostcontainer a:hover { color: var(--linkHover) !important; }
.ticketgeneralinfocontainer { color: var(--secondaryFG) !important; }


/* Positioning and spacing */
.ticketpostbardesignation, .ticketpostbarname { text-align:right; padding-right:15px; }
.ticketpostbarbadgeblue, .ticketpostbarbadgered, .ticketpostbarbadgegreen { text-align: right; padding-right: 15px; }
.ticketpostcontentsbar, .ticketpostcontentsbargreen, .ticketpostcontentsbargray { float:right; margin-left: 15px; }
.ticketpostcontentsbar { width:300px; }

.gridnavpageselected a:link, .gridnavpage a:link { display:inline-block; padding: 4px 10px; margin:0px 7px -3px; }





/* Left Sidebar items */
.navsub { background-color: var(--defaultBG) !Important; }
.staffnavbar { background: var(--defaultBG) none !important; } 
.staffnavbarclickable { background: var(--defaultBG) none !important; transition: all 0.25s ease-in; } /* The little bar that toggles the left sidebar */
.staffnavbarclickable:hover { background: var(--defaultBGhighlight) !Important; }
.staffnavbarclickable img { width: 10px !important; } /* The little bar that toggles the left sidebar */

.treeview ul { background-color: var(--defaultBG) !important; }
.renavsection {
  border: 1px solid var(--border) !important;
  background: var(--defaultBG) none !important;
  color: var(--defaultFG) !important;
}
.renavsection a:link { color: var(--link) !important; }
.renavsection a:visited { color: var(--link) !important; }
.renavsection a:hover { color: var(--linkHover) !important; }



/* Reply area */
.tablerow1_tr .tablerow1:first-child { text-align: right; } /* The labels on the reply form */



/* Mass Action items */
#ticketgridtabs { background: var(--defaultBG) none !important; }
.massactionpanel { border-top: 1px solid var(--defaultBG) !important; }
.matopitem { background: var(--tertiaryBG); border-color: var(--tertiaryBorder); color: var(--tertiaryFG); }
.swifttextautocompletediv { background-color: var(--tertiaryBG) !important; border-color: var(--tertiaryBorder); }
.settabletitlerowmain5 { background: var(--defaultBG) none !important; border-top: 1px solid var(--defaultBorder) !important; border-bottom: solid 1px var(--secondaryBorder) !important; }
.tabtoolbarsub ul li a { border-color: var(--secondaryBorder); background: var(--secondaryBG); color: var(--secondaryFG); }
.tabtoolbarsub ul li a:hover { background: var(--secondaryBGhover); color: var(--secondaryFG); }
.tabtoolbarsub ul li:first-child a { border-color: var(--primaryBorder); background: var(--primaryBG); color: var(--primaryFG); }
.tabtoolbarsub ul li:first-child a:hover { background: var(--primaryBGhover); color: var(--primaryFG); }

#ticketgridtabs.ui-tabs .ui-tabs-nav { border-bottom: solid 1px var(--tertiaryBorder) !important; }
.ui-state-default, .ui-widget-content .ui-state-default { background-color: var(--tertiaryBG) !important; border:solid 1px var(--tertiaryBorder) !important; color: var(--tertiaryFG) !important; }
.ui-state-default:hover, .ui-widget-content .ui-state-default:hover { background-color: var(--tertiaryBGhover) !important; border-color: var(--tertiaryBorderHover) !important;  }
.ui-state-default a, .ui-widget-content .ui-state-default a { color: var(--tertiaryFG) !Important; }
/*
#ticketgridtabs .ui-tabs-panel { border: 1px solid rgba(0,162,232,0.7) !important; background: #060606 none !important; color: #eef !important; }
.ui-state-active, .ui-widget-content .ui-state-active { background-color: black !important; box-shadow: 0px 0px 2px 4px rgb(50,128,232); }
#ticketgridtabs .ui-state-default { border: solid 1px rgba(0,162,232,0.4) !important; background: #123 !important; color: #eef !important; }
#ticketgridtabs .ui-state-default:hover { border: solid 1px rgba(0,162,232,0.5) !important; background: #135 !important; color: #eef !important; }
#ticketgridtabs .ui-state-default a { color: #eef !important; }
#ticketgridtabs .ui-state-active { border: solid 1px rgba(0,162,232,0.7) !important; background: #147 !important; color: #eef !important; }
*/


/** Qtip **/
.qtip-blue { background-color: var(--defaultBG); border: solid 5px var(--primaryBorder); color: var(--defaultFG); }
.qtip-blue * { color: var(--defaultFG) !important; background-color: var(--defaultBG) !important;}



/* Ticket status bar */
.ticketgeneralproperties[style="background-color: #ff0000;"] * { color: white; } /* new */
.ticketgeneralproperties[style="background-color: #c3d95f;"] * { color: black; } /* open */
.ticketgeneralproperties[style="background-color: #c2c2c2;"] * { color: black; } /* Preclose */
.ticketgeneralproperties[style="background-color: #8398a8;"] * { color: black; } /* Client Hold */
.ticketgeneralproperties[style="background-color: #b3af68;"] * { color: black; } /* in progress */
.ticketgeneralpropertiesobject[style="background-color: #595959;"] * { color: white; }


/* Dialog boxes */
.ui-dialog { background-color: var(--defaultBG); }
.ui-dialog .ui-dialog-titlebar { border-bottom-color: var(--border); }
.ui-dialog .ui-widget-header { color: var(--defaultFG); }
.ui-tabs .ui-tabs-nav { border-color: var(--tertiaryBorder); }
}

