css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("discordapp.com") {
  .message-group .comment .markup {
    font-family: initial;
    font-size: 0.9em;
    color: #000000;
  }
  .channel-textarea-inner textarea {
    font-family: initial;
    font-size: 0.9em;
    color: #000000;
  }
  .message-group h2 strong {
    font-size: 1.1em;
    font-weight: bold;
  }
  .guild-channels .channel a {
    opacity: 0.7;
  }
  .channel-text.channel-muted:not(.selected) span{
    opacity: 0.5;
  }
}
