css:
@-moz-document domain(discordapp.com) {
/* messages bloc */
.message-group {
    padding: 5px;
    margin-left: 10px;
    margin-right: 5px;
}
.channels-wrap {
    width: 220px;
}
.message-group .comment .markup {
    font-size: 11pt;
}
.avatar-large {
    margin-right: 10px !important;
}
.channel-members-wrap {
    min-width: 200px;
}
.channel-members .member+h2 {
    margin-top: 10px;
}
.channel-members .member {
    padding: 6px 10px 3px 15px;
    font-weight: normal;
}
/*channels menu on the left */
.guilds-wrapper{
    width: 50px !important;
}
.guilds-wrapper .scroller-wrap {
    width: 64px !important;
}
.guilds-wrapper .guilds .guild .guild-inner a {

    height: 36px !important;
    width: 36px !important;
    background-size: contain;
} 
.guilds-wrapper .guilds .guild .guild-inner {
    line-height: 36px !important;
}
.guilds-wrapper .guilds .guild, .guilds-wrapper .guilds .friends-icon {
    height: 36px !important;
    width: 36px !important;
}
.guilds-wrapper .guilds-add-inner {
    top: -5px;
}
.guilds-wrapper .btn-download-apps {
    width: 50px;
    visibility: hidden;
    /*nobody use that button anyway */
}

.channels-3g2vYe {
    width: 156px !important;
}



}
