style_setting_options.value:
/* [Отключение кнопки смайлов] */

._emoji_btn {
    visibility: hidden;
}

/* Подвинуть кнопку прикрепления картинок на место кнопки смайлов */
.im-chat-input .im-chat-input--attach {
	right: 1px !important;
}

/* [Отключение кнопки смайлов] */
.emoji_smile_wrap {
    display: none !important;
}


/* Подвинуть кнопку прикрепления картинок на место кнопки смайлов */
.im-chat-input .im-chat-input--attach {
	right: 1px !important;
}

/* [Прозрачный, доступный при наведении aside в ленте] */
#wrap3 > #wrap2 > #wrap1 > #content > .wide_column_left #narrow_column {
    opacity: 0.05;
    transition: opacity 0.2s ease-out;
}
#wrap3 > #wrap2 > #wrap1 > #content > .wide_column_left #narrow_column:hover {
    opacity: 1;
}

/* [Убираем правый aside в ленте] */
#wrap3 > #wrap2 > #wrap1 > #content > .wide_column_left #narrow_column {
   display: none;
}

/* [Компактный вид групп] */
.groups_list {
    display: flex;
    flex-flow: row wrap;
    padding: 0 20px;
	justify-content: space-around; 
}
.group_row_photo {
    margin-right: 0 !important;
}
.group_list_row { 
    display: flex; 
    border-bottom: 1px solid #e7e8ec; 
    position: static !important; 
    padding: 15px 0; 
    width: 140px; 
    text-align: center; 
    justify-content: space-between;
    flex-direction: column;
    align-items: center; 
}
.group_list_row:hover .group_row_title {
	color: coral;
}
.group_row_actions {
    display: none;
}

.group_row_labeled:nth-child(2), .group_row_labeled:nth-child(3) {
    display: none;
}

/* [Переносим ссылки из aside] */
#side_bar_inner > div.left_menu_nav_wrap {
	display: none;
}

/* [Переносим ссылки из aside] */
@media (min-width: 1100px) {
    #side_bar_inner {
		position: static;
	}
	#side_bar_inner > div.left_menu_nav_wrap {
		position: fixed;
    	bottom: 5px;
    	left: 20px;
	}
}
#side_bar_inner .left_menu_nav:nth-child(3) {
    display: none;
}

/* Переносим ссылки из aside */
@media (min-width: 1280px) {
    #side_bar_inner {
		position: static;
	}
	#side_bar_inner > div.left_menu_nav_wrap {
		position: fixed;
    	bottom: 5px;
    	left: 20px;
	}
}

/* [Увеличение списка недавних контактов] */
.chat_cont_scrolling.chat_fix_height {
    height: 100% !important;
}

/* [Увеличение списка недавних контактов x2] */
.chat_cont_scrolling.chat_fix_height {
    height: 600px !important;
}

/* [Отключить истории друзей] */
#stories_feed_wrap {
    display: none !important;
}

#side_bar ol {
	position: fixed;
	top: 60px;
}

/* Фиксированная шапка */
#page_header_cont {
	position: absolute !important;
}
#narrow_column.narrow_column {
	transition: all 0.2s !important;
}
#narrow_column.narrow_column.fixed {
    top: 0 !important;
}

.post_reply._reply_wrap {
	display: none;
}
.replies .replies_wrap {
	display: block !important;
}
.post_share._share_wrap.no_shares .post_share_link._link {
	display: block;
}

.wall_module .post_like_link, .wall_module .post_reply_link, .wall_module .post_share_link,
.wall_module .post_like_count, .wall_module .post_reply_count, .wall_module .post_share_count{
	font-weight: 400;
}

.wall_module .post_like_link, .wall_module .post_reply_link, .wall_module .post_share_link {
	font-weight: 400;
}

/* [Отключение обложек аудиозаписей] */
.audio_row .audio_row_cover_wrap {
	border-radius: 50%;
    transform: scale(0.8888);
}
.audio_row .audio_row_cover {
	display: none;
}
.audio_row .audio_row_cover_wrap {
	background-image: none;
    background-color: rgba(87, 124, 161, 0.9);
}
.audio_row:not(.audio_row_playing) .audio_row_cover_play_icon {
	opacity: 1 !important;
}
.audio_row.audio_row_current:not(.audio_row_playing) .audio_sound_bars {
	display: none;
}

/* [Отключение обложек аудиозаписей] */
.audio_row .audio_row_cover_wrap {
	border-radius: 50%;
    transform: scale(0.8888);
}
.audio_page_player .audio_page_player__cover {
        display: none;
}

/* [Отключение рекламы] */
#ads_left {
	display: none !important;
}
._ads_promoted_post_data_w {
        display: none !important;
}

._sticker_hints {
	display: none !important;
}
