css:
@-moz-document domain("protonmail.com") {
/* GMAIL-STYLE SCROLLBARS  */
/* FROM https://gist.github.com/jambu/2004633 */

::-webkit-scrollbar {
    width: 12px
}
::-webkit-scrollbar-thumb {
    border-width: 1px 1px 1px 2px
}
::-webkit-scrollbar-track {
    border-width: 0
}
::-webkit-scrollbar {
    height: 16px;
    overflow: visible;
    width: 16px;
}
::-webkit-scrollbar-button {
    height: 0;
    width: 0;
}
::-webkit-scrollbar-track {
    background-clip: padding-box;
    border: solid transparent;
    border-width: 0 0 0 4px;
}
::-webkit-scrollbar-track:horizontal {
    border-width: 4px 0 0
}
::-webkit-scrollbar-track:hover {
    background-color: rgba(255, 255, 255, .05);
    box-shadow: inset 1px 0 0 rgba(255, 255, 255, .1);
}
::-webkit-scrollbar-track:horizontal:hover {
    box-shadow: inset 0 1px 0 rgba(255, 255, 255, .1)
}
::-webkit-scrollbar-track:active {
    background-color: rgba(220, 172, 0, .05);
    box-shadow: inset 1px 0 0 rgba(255, 255, 255, .14), inset -1px 0 0 rgba(220, 172, 0, .07);
}
::-webkit-scrollbar-track:horizontal:active {
    box-shadow: inset 0 1px 0 rgba(255, 255, 255, .14), inset 0 -1px 0 rgba(220, 172, 0, .07)
}
::-webkit-scrollbar-thumb {
    background-color: rgba(255, 255, 255, .2);
    background-clip: padding-box;
    border: solid transparent;
    border-width: 1px 1px 1px 6px;
    min-height: 28px;
    padding: 100px 0 0;
    box-shadow: inset 1px 1px 0 rgba(255, 255, 255, .1), inset 0 -1px 0 rgba(220, 172, 0, .07);
}
::-webkit-scrollbar-thumb:horizontal {
    border-width: 6px 1px 1px;
    padding: 0 0 0 100px;
    box-shadow: inset 1px 1px 0 rgba(255, 255, 255, .1), inset -1px 0 0 rgba(255, 255, 255, .07);
}
::-webkit-scrollbar-thumb:hover {
    background-color: rgba(255, 255, 255, .4);
    box-shadow: inset 1px 1px 1px rgba(255, 255, 255, .25);
}
::-webkit-scrollbar-thumb:active {
    background-color: rgba(255, 255, 255, 0.5);
    box-shadow: inset 1px 1px 3px rgba(255, 255, 255, 0.35);
}
::-webkit-scrollbar-track {
    border-width: 0 1px 0 6px
}
::-webkit-scrollbar-track:horizontal {
    border-width: 6px 0 1px
}
::-webkit-scrollbar-track:hover {
    background-color: rgba(255, 255, 255, .035);
    box-shadow: inset 1px 1px 0 rgba(255, 255, 255, .14), inset -1px -1px 0 rgba(220, 172, 0, .07);
}
::-webkit-scrollbar-thumb {
    border-width: 0 1px 0 6px
}
::-webkit-scrollbar-thumb:horizontal {
    border-width: 6px 0 1px
}
::-webkit-scrollbar-corner {
    background: transparent
}
body::-webkit-scrollbar-track-piece {
    background-clip: padding-box;
    background-color: #f5f5f5;
    border: solid #fff;
    border-width: 0 0 0 3px;
    box-shadow: inset 1px 0 0 rgba(255, 255, 255, .14), inset -1px 0 0 rgba(220, 172, 0, .07);
}
body::-webkit-scrollbar-track-piece:horizontal {
    border-width: 3px 0 02;
    box-shadow: inset 0 1px 0 rgba(255, 255, 255, .14), inset 0 -1px 0 rgba(220, 172, 0, .07);
}
body::-webkit-scrollbar-thumb {
    border-width: 1px 1px 1px 5px
}
body::-webkit-scrollbar-thumb:horizontal {
    border-width: 5px 1px 1px
}
body::-webkit-scrollbar-corner {
    background-clip: padding-box;
    background-color: #f5f5f5;
    border: solid #fff;
    border-width: 3px 0 0 3px;
    box-shadow: inset 1px 1px 0 rgba(255, 255, 255, .14);
}
::-webkit-scrollbar-thumb:hover {
    background-color: #eee;
}


/* HEADER */

header#pm_header-desktop,
header#pm_header-desktop a.logo {
    background: transparent;
}
header#pm_header-desktop a.logo img {
    margin: 8px 0 0 0;
}
form.searchForm fieldset,
form.searchForm fieldset button[type=button],
form.searchForm fieldset input.query,
form.searchForm fieldset #closeAdvSearch,
form.searchForm fieldset button[type=submit],
form.searchForm.adv fieldset,
form.searchForm.adv fieldset button[type=button] {
    border-radius: 0;
}
form.searchForm fieldset {
    background: rgba(255, 255, 255, 1);
    border-color: rgba(255, 255, 255, 1);
    -webkit-box-shadow: 0 1px 2px rgba(0, 0, 0, .2);
    box-shadow: 0 1px 2px rgba(0, 0, 0, .2);
    color: #000;
}
form.searchForm fieldset input.query {
    background: rgba(255, 255, 255, 1);
}
form.searchForm fieldset,
form.searchForm fieldset,
form.searchForm fieldset button[type=button],
form.searchForm fieldset input.query {
    color: #000;
}
form.searchForm fieldset #closeAdvSearch,
form.searchForm fieldset button[type=submit] {
    background-color: #4d90f0;
}
form.searchForm {
    max-width: 34%;
}
header#pm_header-desktop ul.navigation>li.active,
header#pm_header-desktop ul.navigation>li:hover {
    box-shadow:0 5px 0 0 #4d90f0 inset;
}

header#pm_header-desktop ul.navigation>li>a {
    color: #eee;
    text-shadow: 0px 1px 2px rgba(0, 0, 0, .75);
}
header#pm_header-desktop ul.navigation>li>a:hover {
    color: #fff;
}
header#pm_header-desktop ul.navigation>li.active>a>span .fa {
    color: #A2C5F9;
}
header#pm_header-desktop ul.navigation>li.active>a>strong {
    font-weight: bold;
}

/* General */
p a {
    color: #15c;
}

/* SIDEBAR */

body section.sidebar {
    background: transparent;
}
body section.sidebar a.compose {
    font-weight: bold;
}
body section.sidebar a.compose .fa {
    display: none;
}
body section.sidebar ul.menu li.active a,
body section.sidebar ul.menu li a,
body section.sidebar div.footer div.link a,
body section.sidebar div.footer div.storage strong,
body section.sidebar a.version,
body section.sidebar ul.menu li a em,
body section.sidebar div.labels ul li a,
body section.sidebar div.labels ul li a em,
body section.sidebar ul.menu li a i.fa {
    color: #eee;
    text-shadow: 0px 1px 2px rgba(0, 0, 0, .75);
}
body section.sidebar ul.menu li.active a i.fa {
    color: #A2C5F9;
}
body section.sidebar ul.menu li a em,
body section.sidebar div.labels ul li a em{
    font-weight: bold;
}
.pm_button.primary {
    background-color: #f5f5f5;
    background-image: -webkit-linear-gradient(top, #f5f5f5, #f1f1f1);
    background-image: linear-gradient(top, #f5f5f5, #f1f1f1);
    color: #444;
    border: 1px solid #dcdcdc;
    border: 1px solid rgba(0, 0, 0, 0.1);
}
body section.sidebar div.footer .bar {
    background-color: rgba(255,255,255,0.35);
}


/* MESSAGE LIST - Buttons */

.pm_buttons {
    border: 1px solid transparent;
}
.pm_buttons a,
.pm_buttons a.active, 
.pm_buttons a:active {
    background-color: transparent;
    background-image: -webkit-linear-gradient(top, rgba(68, 68, 68, .75), rgba(60, 60, 60, .75));
    background-image: linear-gradient(top, rgba(68, 68, 68, .75), rgba(60, 60, 60, .75));
    background-image: -webkit-gradient(linear, left top, left bottom, from(rgba(68, 68, 68, .75)), to(rgba(60, 60, 60, .75)));
    color: #ccc;
}
.pm_buttons a.active img,
.pm_buttons a:active img {
    background-color: #ccc;
}
.pm_buttons a:hover {
    background-color: transparent;
    background-image: -webkit-linear-gradient(top, rgba(68, 68, 68, .75), rgba(60, 60, 60, .75));
    background-image: linear-gradient(top, rgba(68, 68, 68, .75), rgba(60, 60, 60, .75));
    background-image: -webkit-gradient(linear, left top, left bottom, from(rgba(68, 68, 68, .75)), to(rgba(60, 60, 60, .75)));
    color: #fff;
}
.pm_buttons .disabled, 
.pm_buttons [disabled],
.pm_buttons.disabled,
.pm_button.disabled,
.pm_button[disabled] {
    opacity: 0.25;
    border-color: transparent;
    box-shadow: inset 0 0 5rem 0 rgba(0,0,0,.06);
}
.pm_buttons>* {
    border-left: 1px solid transparent;
}
.pm_toolbar {
    box-shadow: none;
}

.pm_toolbar .pm_buttons .pm_dropdown a,
.pm_toolbar .pm_buttons .pm_dropdown a.active, 
.pm_toolbar .pm_buttons .pm_dropdown a:active,
#pm_header-desktop .pm_buttons .pm_dropdown a,
#pm_header-desktop .pm_buttons .pm_dropdown a.active, 
#pm_header-desktop .pm_buttons .pm_dropdown a:active {
    background-color: #fff;
    background-image: none;
    color: #333;
}

/* MESSAGE LIST */

body #conversation-list-rows .conversation,
body #conversation-list-columns .conversation {
    box-shadow: inset 0 -1px 0 #2a2a2a;
}
#nothingToSeeHere .fa,
#nothingToSeeHere span,
#pm_placeholder{
    color: #eee;
    text-shadow:0px 1px 2px rgba(0, 0, 0, .75)
}


/* MESSAGE LIST LABELS */

.conversation.read .pm_labels label,
.conversation .row .pm_labels label,
body #conversation-list-rows .conversation .subject .pm_labels,
body #conversation-list-rows .conversation .subject .pm_labels label,
body #conversation-list-rows .conversation.read, 
body #conversation-list-rows .conversation.read .subject .pm_labels, 
body #conversation-list-rows .conversation.read .subject .pm_labels label{
    box-shadow: none;
    background: transparent;
}
.conversation.marked::before {
    background-color: #4d90f0;
}


/* MESSAGE LIST - Unread */

body #conversation-list-rows .conversation,
body #conversation-list-columns .conversation {
    background: rgba(35, 35, 35, .9);
    color: #eee;
}
body #conversation-list-rows .conversation .subject h4,
body #conversation-list-columns .conversation .row h4 {
    color: #eee;
}
body #conversation-list-rows .conversation .senders,
body #conversation-list-columns .conversation .senders {
    font-weight: bold;
}
body #conversation-list-rows .conversation.read.selected .subject h4,
body #conversation-list-columns .conversation.read.selected h4.subject {
    color: #eee;
}


/* MESSAGE LIST - Read */

body #conversation-list-rows .conversation.read,
body #conversation-list-columns .conversation.read,
body #conversation-list-rows .conversation.read .subject .pm_labels,
body #conversation-list-columns .conversation.read .subject .pm_labels,
body #conversation-list-rows .conversation.read .subject .pm_labels label body #conversation-list-columns .conversation.read .subject .pm_labels label {
    background: rgba(5, 5, 5, .70);
    color: #aaa
}
body #conversation-list-rows .conversation.read .subject h4,
body #conversation-list-columns .conversation.read h4.subject {
    color: #aaa;
}
body #conversation-list-rows .conversation.read .senders,
body #conversation-list-columns .conversation.read .senders {
    font-weight: 400;
}


/* MESSAGE LIST - Unread & Read Selected */

body #conversation-list-rows .conversation.selected,
body #conversation-list-rows .conversation.selected .subject .pm_labels,
body #conversation-list-rows .conversation.selected .subject .pm_labels label,
body #conversation-list-rows .conversation.read.selected,
body #conversation-list-rows .conversation.read.selected .subject .pm_labels,
body #conversation-list-rows .conversation.read.selected .subject .pm_labels label,
body #conversation-list-columns .conversation.selected,
body #conversation-list-columns .conversation.selected .subject .pm_labels,
body #conversation-list-columns .conversation.selected .subject .pm_labels label,
body #conversation-list-columns .conversation.read.selected,
body #conversation-list-columns .conversation.read.selected .subject .pm_labels,
body #conversation-list-columns .conversation.read.selected .subject .pm_labels label {
    background: #555;
    color: #eee
}
body #conversation-list-rows .conversation.read.selected .subject h4,
body #conversation-list-columns .conversation.read.selected h4.subject {
    color: #eee;
}


/* CONVERSATION VIEW - MESSAGE */

body #conversation-view,
body #pm_view {
    background-color: transparent;
}
#conversation-view .message .frame {
    background-color: #fff;
}
#conversation-view .message {
    border: 0px solid #cfcfcf;
    border-radius: 0px;
    border-width: 1px 0px 1px 0px;
}
#conversation-view .message>div.summary,
.alert {
    border-radius: 0px;
}
.alert a {
    color: #777;
}


/* MESSAGE VIEW - HEADER */

#conversation-view .message>div.details {
    padding: 0px 10px 0px;
}
#conversation-view .message .toggleDetails,
#conversation-view .message .contact a.compose {
    color: #15c;
}
#conversation-view .message>div.summary,
#conversation-view .message>div.details,
#conversation-view .message>div.summary .labels, 
#conversation-view .message>div.summary .status, 
#conversation-view .message>div.summary .time,
#conversation-view .message>div.summary .labels .pm_labels label,
#conversation-view .message.details.open .labelz,
#conversation-view .message .frame .email .pm_button.more {
    background: #f5f5f5;
}
#conversation-view .message>div.summary .labels .pm_labels label {
    box-shadow: .25rem 0 0 .25rem #f5f5f5;
}
#conversation-view .message>div.summary .time::after {
    background: -moz-linear-gradient(left,rgba(255,255,255,0) 0,#f5f5f5 100%);
    background: -webkit-linear-gradient(left,rgba(255,255,255,0) 0,#f5f5f5 100%);
    background: linear-gradient(to right,rgba(255,255,255,0) 0,#f5f5f5 100%);
}

#conversation-view header {
    padding: 0px 0px 9px 0px;
    border-bottom: 1px solid rgba(255,255,255,0.35);
}
#conversation-view header h1 {
    color: #eee;
    text-shadow:0px 1px 2px rgba(0, 0, 0, .75)
}
#conversation-view .message>div.details .pm_buttons a {
    color: #666;
}


/* COMPOSE WINDOW */

#pm_composer .composer header {
    background: #404040;
}
#pm_composer .composer .row {
    border-bottom: 1px solid #cfcfcf;
}
.squire-toolbar {
    border-color: #cfcfcf;
    border-top: 0px;
    background-color: #f5f5f5;
}
.squire-toolbar li.separator {
    background: #cfcfcf;
}
#pm_composer .composer footer {
    background: #f5f5f5;
    border-color: #cfcfcf;
}
#pm_composer .composer footer .pm_button {
    border-color: transparent;
    background-color: transparent;
    box-shadow: none;
}
#pm_composer .composer footer .pm_button:hover {
    border-color: #cfcfcf;
    background-color: transparent;
    box-shadow: 0 1px 2px 0 rgba(0,0,0,.1);
}
#pm_composer .composer footer .pm_button.primary {
	-webkit-box-shadow: none;
    box-shadow: none;
    background-color: #4d90fe;
    background-image: -webkit-linear-gradient(top,#4d90fe,#4787ed);
    background-image: linear-gradient(top,#4d90fe,#4787ed);
    border: 1px solid #3079ed;
    color: #fff;
}
#pm_composer .composer footer .pm_button.primary:hover {
	-webkit-box-shadow: none;
    box-shadow: none;
    background-color: #357ae8;
    background-image: -webkit-linear-gradient(top,#4d90fe,#357ae8);
    background-image: linear-gradient(top,#4d90fe,#357ae8);
    border: 1px solid #2f5bb7;
    border-bottom-color: #2f5bb7;
}

/* SETTINGS PAGE */

#pm_settings .pm_tabs li.active a.pm_button,
.pm_button.link {
    color: #4d90f0;
}
.pm_toggle.off .off, .pm_toggle.on .on {
    background: #4d90f0;
}
.pm_button.primary.disabled, .pm_button.primary[disabled] {
    background: #92c5f9;
}
#pm_settings .settings,
#pm_settings .pm_tabs li.active a.pm_button {
    background-color: #f5f5f5;
}
#pm_settings .pm_tabs li.active a.pm_button {
    background-color: #f5f5f5 !important;
    border-bottom-color: #f5f5f5;
}
#pm_settings .pm_tabs {
    background-color: transparent;
}
.pm_modal .modal-dialog {
    border: 5px solid #4d90f0;
}
.pm_modal .modal-dialog .close {
    background: #4d90f0;
}
.alert.alert-info {
    background: #cfe0fb;
}
.alert.alert-warning {
    background: #ffedce;
}

/* CONTACTS PAGE */
.pm_table table thead {
    border-top-color: transparent;
}
.pm_table table th {
    background: rgb(60,60,60);
    border-bottom-color: #000;
}
.pm_table table th,
.pm_table table th a,
.pm_table table th .fa{
    color: #eee;
}
.pm_table table tr {
    background: rgba(5, 5, 5, .70);
}
.pm_table table td {
    color: #aaa;
    line-height: 35px;
    border-color: transparent;
}
.pm_table table td .pm_button, 
.pm_table table th .pm_button {
    height: 18px;
    line-height: 18px;
}
.pm_table table td.actions .pm_button,
#pm_contacts .pm_toolbar button,
#pm_contacts .pm_toolbar .pm_button {
    border-color: transparent;
    background-color: transparent;
    background-image: -webkit-linear-gradient(top, rgba(68, 68, 68, .75), rgba(60, 60, 60, .75));
    background-image: linear-gradient(top, rgba(68, 68, 68, .75), rgba(60, 60, 60, .75));
    background-image: -webkit-gradient(linear, left top, left bottom, from(rgba(68, 68, 68, .75)), to(rgba(60, 60, 60, .75)));
    color: #ccc;
}
.pm_table table td.actions .pm_button:hover,
#pm_contacts .pm_toolbar button:hover,
#pm_contacts .pm_toolbar .pm_button:hover {
    color: #fff;
}
#pm_contacts .pm_table td,
#pm_contacts .pm_table td span {
    line-height: 18px;
}

/* MODAL DIALOGS */

.pm_modal .modal-dialog,
.pm_modal .modal-dialog .modal-footer,
.pm_modal .modal-dialog .modal-content {
    background-color: #f5f5f5;
}

}

@-moz-document domain("protonmail.com") {
html.protonmail body.blank,
html.protonmail body.secure {
    background-size: cover;
    background-image: url("https://i.imgur.com/fVKOKaf.jpg");
}
}
