css:
@-moz-document domain("www.chatwork.com") {
::-webkit-scrollbar {
    width: 2px;
}

::-webkit-scrollbar-thumb {
    background-color: rgba(0, 0, 0, 0.50);
}

.wrapper {
    background: none;
}

.button,
.button:hover,
.button:active {
    box-shadow: 0 2px 5px rgba(0,0,0,.16),0 2px 10px rgba(0,0,0,.12);
    border: none;
    background: #FFF;
    text-shadow: none;
}

.btnPrimary,
.btnPrimary:hover {
    background: #2196F3;
}

.globalHeader {
    background: #37474F;
    box-shadow: 0 1px 4px rgba(0,0,0,.3);
