}

.sideContentHl {
    color: #FFF;
    font-weight: normal;
    font-size: 12px;
    border-bottom: none;
    background: none;
}

.sideTitleBtn,
.sideTitleBtn:hover {
    box-shadow: none;
    background: rgba(0,0,0,.37);
    color: #FFF;
    border: none;
    border-radius: 0;
}

.chatFilterMenu {
    border-top: 0;
    border-bottom: 0;
    box-shadow: none;
    height: 40px;
}

.chatFilterMenu .button {
    box-shadow: none;
    background: rgba(0,0,0,.37);
    color: #FFF;
    border: none;
    border-radius: 0 !important;
}

.chatRoomListArea {
    background: #FFF;
}

.menuListTitle > li {
    border-top: 0;
    border-bottom: 0;
}

.chatRoomListArea .menuListTitleSelected, .chatRoomListArea .menuListTitleSelected:hover {
    background: #F4F4F4;
    color: #000;
    text-shadow: none;
}

.chatContent {
    border: none;
    padding: 0 10px;
    background: #edecec;
}

.mainContentHl {
    border: none;
    background: #FFF;
    font-size: 14px;
    box-shadow: 0 1px 4px rgba(0,0,0,.3);
    font-weight: normal;
}

.conciergeMenu {
    border-right: 0;
}

.chatRoomMemberInfo {
    border-right: 0;
}

.chatTimeLineMessage:not(.chatTimeLineMessageMention) {
    background: #FFF;
}

.timeStamp {
    right: 9px;
}

.chatSendArea {
    box-shadow: 0 8px 17px rgba(0,0,0,.2),0 6px 20px rgba(0,0,0,.19);
}

.chatSendAreaContentShadow {
    border-radius: 0;
    box-shadow: none;
    border: 0;
}

.chatSendToolbar {
    border: none;
    background: #FFF;
    height: 40px;
}

.chatSendAreaContentInner {
    padding: 0;
}

.windowBtnR, .windowBtnL {
    background: #FFF;
}

.windowBtnR:hover, .windowBtnL:hover {
    border: none;
}

.subContentLabel {
    background: #edecec;
    border: none;
    padding: 10px 0 5px;
}

.chatSendTextarea textarea {
    background: #f6f6f6;
}

.dateHead {
    margin: 15px 0;
}

.dateContent {
    background: #37474F;
    color: #FFF;
}

.dateContent span.icoFontClock {
    color: #FFF;
}

.chatInfo {
    border-radius: 0;
    border: 1px solid #ddd;
}

.chatInfo > .title {
    background: #f7f7f7;
    border: none;
}

.showMoreComplete {
    border-radius: 0;
    border: 0;
    background: #FFF;
}

.taskComplete .chatInfoTaskMetaArea {
    border: 0;
    background: #FFF;
}

.toolTip {
    border-radius: 0;
    border: 0;
}

.dialogContent {
    border-radius: 0;
    border: none;
    background: #F7F7F7;
}

.dialogContent h1 {
    border-radius: 0;
    background: #FFF;
}

}
