css:
@namespace url(http://www.w3.org/1999/xhtml);
/* 4chan Dark Blue (Bloatfree) 09.09.2016 @t0ppis */
@-moz-document domain("4chan.org") {
/* Tausta ja otsikoiden värit*/ 
body {
    background: #2A3344 !important;
    color: #FFFFFF !important;
}
/* Kuvan poistaminen */
#logo-fp img {
   display: none !important;
}
/* Etusivun otsikon väri ja tausta*/
.boxbar {
    color: #F2F2F !important; 
    background: #205086 !important;
}
/* Etusivun boksin taustaväri */
.boxcontent {
    background: #313841 !important;
}
/* Etusivun boksin tekstien värit */
#boards a {
    color: #156B90 !important;
}
/* Laudan nimikko otsikkokuva */
div.boardBanner {
    display: none !important;
}
/* Turhan tekstin piilotus */
.globalMessage{
    display: none !important;
} 
/* Turhan tekstin nappulan piilotus */
#toggleMsgBtn {
    display: none !important;
} 
/* Langan aiheen tekstin väri */
span.subject {
     color: #B0BCD5 !important;
}
/* Postaajan nimi */
span.name {
     color: #156B90 !important;
}
/* Viittaukset */
.yotsuba_b_new .backlink a{
     color: #194F8E !important;
}
/* Click here to view */
a.replylink:not(:hover) {
     color: #194F8E !important;
}
/* Vastauslinkki */
.quotelink {
    color: #2893C1 !important;    
}
/* Uuden langan aloitusteksti ja OP:n teksti*/
a {
    color: #FFFFFF !important;
}
/* Lankojen vastauksien tausta */ 
div.reply {
    background-color: #212A3A !important; 
    border: 1px solid #192337 !important;
}
/* Lautojen ylälinkkien tekstiväri */
div#boardNavDesktop a {
    color: #FFFFFF !important;
}
/* Sivunvaihto tausta */
div.pagelist {
    color: #156B90 !important;
    background: #2A3344 !important;
    border-right: 0px !important;
    border-bottom: 0px !important;
}
/* Adblock muistutuksen piilotus */
.center {
    display: none !important;  
}
/* Uutisten piilotus */
#blotter {
    display: none !important;
}
/* Viivojen piilotusta */ 
hr.abovePostForm {
    display: none !important;
}
.belowLeaderboard {
    display: none !important;
}
.aboveMidAd {
    display: none !important;
}
}
