css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wiki.greasespot.net") {

  #content{
    background-color: #111;
    color: lightgray;
  }
  
  h1, h2, h3, h4, h5, h6 {
    color: lightgray;
  }
  
  .mw-body {
    border: 1px solid black;
  }
  
  .infobox {
    background-color: #222;
    border: 1px solid black;
    color: lightgray;
  }
  
  .infobox th {
    background-color: darkblue;
  }
  
  .portlet h3 {
    background: #111;
  }
  
  .pBody {
    background-color: #222;
    color: lightgray;
    border: 1px solid black;
  }
  
  code {
    color: green;
    background-color: black;
    border: 1px solid #333;
  }
  
  dt code, li code:first-child {
    color: purple;
  }
  
  code b {
    color: blue;
  }
  
  code i {
    color: lightgreen;
  }
  
  div#footer {
    background-color: #261e00;
    border-top: 1px solid #6C4C01;
    border-bottom: 1px solid #6C4C01;
    color: lightgray;
  }
  
  .catlinks {
    border: 1px solid black;
    background-color: #080808;
    color: lightgray;
  }
  
  #p-cactions li a {
    background-color: black;
    color: #A00000;
  }
  
  #p-cactions li a:hover {
    background-color: #3b0000;
    color: red;
}
  
  #p-cactions li {
    border: 1px solid #360000;
    background: white;
  }
  
  #p-cactions li:hover {
    border: 1px solid darkred;
  }
  
  #p-cactions li.selected a {
    background-color: darkred;
    color: lightgray;
  }
  
  #p-cactions li.selected {
    border-color: black;
  }
  
  #toc, .toc, .mw-warning, .toccolours {
    border: 1px solid black;
    background-color: #080808;
  }
  
  pre.sample {
    border-color: black;
  }
  
  pre, .mw-code {
    color: green;
    background-color: #080808;
    border: 1px solid black;
  }
  
}
